--
-- Cấu trúc bảng cho bảng `baiviet`
--

CREATE TABLE `baiviet` (
  `IDBV` int(11) NOT NULL,
  `IDKhuVuc` varchar(6) NOT NULL,
  `IDLoaiTin` varchar(6) NOT NULL,
  `TieuDe` text NOT NULL,
  `NoiDung` longtext NOT NULL,
  `TomTat` text NOT NULL,
  `NoiBat` binary(1) DEFAULT NULL,
  `NgayViet` date DEFAULT NULL,
  `NgayCapNhat` date NOT NULL,
  `IDNhanVien` varchar(6) NOT NULL,
  `updated_at` date NOT NULL,
  `created_at` date NOT NULL,
  `anhdaidien` varchar(300) DEFAULT NULL,
  `luotxem` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `baiviet`
--

INSERT INTO `baiviet` (`IDBV`, `IDKhuVuc`, `IDLoaiTin`, `TieuDe`, `NoiDung`, `TomTat`, `NoiBat`, `NgayViet`, `NgayCapNhat`, `IDNhanVien`, `updated_at`, `created_at`, `anhdaidien`, `luotxem`) VALUES
(1, '8', '2', 'The Factory Contemporaty Arts Centre – khu nghệ thuật đương đại đúng nghĩa đầu tiên ở Sài Gòn 1', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Mong muốn đưa nghệ thuật đương đại đến gần hơn với giới trẻ Việt Nam, thế nên The Factory Contemporaty Arts Centre được đầu tư rất kĩ lưỡng về không gian, tạo nên một khu nghệ thuật vô cùng hiện đại, ấn tượng và thu hút ngay từ cái nhìn đầu tiên.</em></strong></p> <p style="text-align: justify;"><span id="more-202318"></span></p> <p style="text-align: justify;"></p><div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/tp-hcm/" target="_blank">Du lịch TP. Hồ Chí Minh</a></strong></div><p></p> <h2 style="text-align: justify;">The Factory Contemporaty Arts Centre – khu nghệ thuật đương đại đúng nghĩa đầu tiên ở Sài Gòn</h2> <p style="text-align: justify;">Giới trẻ Sài Gòn không chỉ có gu thưởng thức, thích tìm hiểu, trải nghiệm những cái mới mà còn là những người có tâm hồn nghệ thuật không kém cạnh bạn bè thế giới. Bằng chứng là trong thời gian qua, rất nhiều những sự kiện nghệ thuật đã được diễn ra tại TP.HCM và sự kiện nào cũng thu hút đông đảo sự quan tâm của các bạn.</p> <p style="text-align: justify;">Đáng tiếc là số lượng không gian thật sự đáp ứng được nhu cầu này của giới trẻ lại rất ít. Đa phần những buổi triển lãm tranh, những buổi workshop về nghệ thuật, những phòng trưng bày đều phải đi mượn không gian ở những nơi khác, sau đó biến tấu lại để phù hợp với mục đích ban đầu. Tuy nhiên đừng vội lo, một không gian mới toanh chuyên phục vụ cho các hoạt động nghệ thuật vừa được ra đời ở Sài Gòn và hứa hẹn sẽ làm mưa làm gió trong thời gian sắp tới đây!</p> <div id="attachment_202356" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-202356 size-full" title="Vẻ ngoài nổi bật của The Factory." src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-1.jpg" alt="Vẻ ngoài nổi bật của The Factory." width="650" height="434"><p class="wp-caption-text">Vẻ ngoài nổi bật của The Factory.</p></div> <p style="text-align: justify;">Công trình đó chính là The Factory Contemporary Arts Centre nằm ở địa chỉ 15 Nguyễn Ư Dĩ, Thảo Điền, quận 2. Đây có thể xem là trung tâm nghệ thuật đương đại đầu tiên và duy nhất tại Sài Gòn hiện nay. Bên cạnh việc trưng bày các tác phẩm nghệ thuật dưới nhiều hình thức khác nhau như tranh vẽ, tượng điêu khắc, công trình nghệ thuật sắp đặt… thì The Factory còn là nơi tổ chức các buổi gặp gỡ giữa những nghệ sĩ lớn trong nghề và là nơi diễn ra các buổi workshop để mọi người cùng được học tập, trau dồi thêm về tư duy nghệ thuật.</p> <p style="text-align: justify;">Nằm tại khu vực Thảo Điền, quận 2 cách xa trung tâm thành phố, The Factory tựa như một ốc đảo tách biệt với thế giới xô bồ, vồn vã ngoài kia. Đập ngay vào mắt bạn khi đến The Factory đó chính là vẻ ngoài với cấu trúc ấn tượng khi sử dụng một loạt những container để làm nên không gian phía ngoài cực chất và bắt mắt.</p> <p><img class="aligncenter wp-image-202357 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-2.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-2" width="650" height="434"> <img class="aligncenter wp-image-202358 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-3.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-3" width="650" height="434"> <img class="aligncenter wp-image-202359 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-4.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-4" width="650" height="434"></p> <p style="text-align: justify;">The Factory có tổng diện tích vừa tròn 1000m² và được chia thành nhiều khu vực. Khu vực container bắt mắt mà bạn nhìn thấy đầu tiên khi đặt chân đến đây là những căn phòng nho nhỏ và sẽ sớm được đưa vào hoạt động với nhiều mục đích khác nhau như làm văn phòng, cho thuê để làm pop-up sale, trưng bày các tác phẩm nghệ thuật…</p> <p><img class="aligncenter wp-image-202360 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-5.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-5" width="650" height="434"> <img class="aligncenter wp-image-202361 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-6.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-6" width="650" height="434"> <img class="aligncenter wp-image-202362 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-7.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-7" width="650" height="434"> <img class="aligncenter wp-image-202363 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-8.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-8" width="650" height="434"></p> <p style="text-align: justify;">Bước chân vào đây, bạn sẽ cảm nhận được ngay luồng không khí nghệ thuật ngập tràn với những bức tranh đẹp mê hồn, những bức tượng tinh xảo hay cả những công trình nghệ thuật sắp đặt cực kì trừu tượng và gợi mở.</p> <p><img class="aligncenter wp-image-202364 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-9.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-9" width="650" height="434"> <img class="aligncenter wp-image-202365 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-10.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-10" width="650" height="434"></p> <p style="text-align: justify;">Phía bên trong thì có những không gian chính như: quán cà phê phục vụ những vị khách dừng chân; không gian mở dùng để tổ chức những buổi trò chuyện, workshop, seminar; thư viện với hàng loạt những đầu sách hiếm về nhiều lĩnh vực nghệ thuật khác nhau; phòng trưng bày; quán bar. Tất cả các khu vực đều được trang trí, chăm chút rất tỉ mẩn và trông cực “nghệ”, tha hồ để bạn có được 1001 kiểu ảnh “so deep”.</p> <p><img class="aligncenter wp-image-202366 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-11.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-11" width="650" height="975"> <img class="aligncenter wp-image-202367 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-12.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-12" width="650" height="434"> <img class="aligncenter wp-image-202368 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-13.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-13" width="650" height="434"> <img class="aligncenter wp-image-202369 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-14.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-14" width="650" height="975"></p> <p style="text-align: justify;">Khi được hỏi về sứ mệnh của The Factory, chị Bảo Lê, trợ lí giám tuyển chia sẻ: “The Factory được mở ra với mục đích phát triển hoạt động nghệ thuật, phục vụ cộng đồng, kết nối các xu hướng và phong cách nghệ thuật đa dạng. Đồng thời góp phần hình thành mạng lưới nghệ thuật tại Việt Nam, truyền cảm hứng đến cộng đồng, tạo dựng sức sống mới trong hoạt động nghệ thuật.”</p> <p><img class="aligncenter wp-image-202370 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-15.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-15" width="650" height="434"> <img class="aligncenter wp-image-202371 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-16.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-16" width="650" height="434"> <img class="aligncenter wp-image-202372 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-17.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-17" width="650" height="434"></p> <p style="text-align: justify;">Dù chỉ mới chính thức đi vào hoạt động được một thời gian ngắn nhưng The Factory đã nhận được sự ủng hộ của rất nhiều người, đặc biệt là những bạn trẻ yêu thích nghệ thuật. Chị Bảo Lê chia sẻ rằng vì vị trí nằm khá xa trung tâm nên thời gian đầu số lượng người đến chưa nhiều. Tuy nhiên đến hiện tại thì các bạn đã dần hình thành thói quen đến The Factory đều đặn để ngắm tranh, xem tượng, để đọc sách và gặp gỡ với những người có cùng chung đam mê, sở thích với mình. Đây thực sự là một dấu hiệu rất đáng mừng!</p> <p><img class="aligncenter wp-image-202373 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-18.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-18" width="650" height="975"> <img class="aligncenter wp-image-202374 size-full" src="https://cdn3.ivivu.com/2017/04/roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-19.jpg" alt="roi-cung-toi-ngay-gioi-tre-sai-gon-co-the-factory-khu-nghe-thuat-duong-dai-dung-nghia-dau-tien-moi-la-va-an-tuong-ivivu-19" width="650" height="975"></p> <p style="text-align: justify;">Nào, chờ gì nữa mà không ghi ngay The Factory vào danh sách những nơi phải đi ngay và luôn! Còn gì tuyệt vời hơn khi có một buổi chiều được sống trong không khí nghệ thuật, thưởng thức tách cà phê thơm lừng, được trò chuyện, gặp gỡ với rất nhiều người thú vị và còn có ảnh đẹp lung linh để khoe với mọi người nữa cơ chứ!</p> <p style="text-align: justify;"></p><div class="ltt-contentbox white"><span class="icon star"></span><p></p> <h4 style="text-align: justify;"><br></h4> <p style="text-align: justify;"></p></div></div>', 'Mong muốn đưa nghệ thuật đương đại đến gần hơn với giới trẻ Việt Nam, thế nên The Factory Contemporaty Arts Centre được đầu tư rất kĩ lưỡng về không gian, tạo nên một khu nghệ thuật vô cùng hiện đại, ấn tượng và thu hút ngay từ cái nhìn đầu tiên.', 0x31, '2017-04-08', '2017-04-08', '3', '2018-04-06', '2017-04-08', 'victoria-chau-doc-ivivu-10.jpg', NULL),
(2, '9', '3', 'Bún thang Hà Nội – món ăn bình dân trong lớp vỏ quý tộc', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/bun-thang-ha-noi-mon-an-binh-dan-trong-lop-vo-quy-toc/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Bún thang từ lâu đã được coi là thức bún có đẳng cấp hơn hẳn các loại bún khác ở <a href="https://www.ivivu.com/blog/category/viet-nam/ha-noi/" target="_blank">Hà Nội</a> về mặt tinh tế, cầu kỳ, kiểu cách… kể từ nguyên liệu, cách chế biến đến thưởng thức.</em></strong> <span id="more-203183"></span></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/ha-noi/" target="_blank">Du lịch Hà Nội</a></strong></div></p> <h2 style="text-align: justify;">Bún thang Hà Nội – món ăn bình dân trong lớp vỏ quý tộc</h2> <p style="text-align: justify;">Nhiều người, khi đến hoặc trở về Hà Nội, ngoài việc ăn bún chả, bún ốc, bún riêu… kiểu gì cũng muốn được thưởng thức một tô bún thang kiểu “bất đáo bún thang phi sành điệu”. Bún thang Hà Nội trong mắt các thần khẩu này là cái gì đó “rất Hà Nội”, đồng nghĩa với sự tinh tế, thanh cao, mỹ vị.</p> <p style="text-align: justify;">Cùng với những tín đồ ăn uống, nhiều chuyên gia ẩm thực, học giả đều nhất trí nâng bún thang lên tầm “Thăng Long đệ nhất bún”, là tinh hoa ẩm thực của giới quý tộc đất Hà Thành, không thể đem so sánh với mấy thứ bún “đầu hè, xó chợ” như bún ốc, bún riêu, bún bung… Ví dụ như ở khâu chế biến. Bún thang có rất nhiều nguyên liệu, căn bản là bún, thịt gà, giò lụa, trứng, tôm, củ cải, mắm tôm, rau thơm…</p> <div id="attachment_203055" style="width: 690px" class="wp-caption aligncenter"><img class="wp-image-203055 size-full" title="Các nguyên liệu cơ bản của bún thang." src="https://cdn3.ivivu.com/2017/04/bun-thang-ha-noi-mon-an-binh-dan-trong-lop-vo-quy-toc-ivivu-1.jpg" alt="Các nguyên liệu cơ bản của bún thang." width="680" height="420" /><p class="wp-caption-text">Các nguyên liệu cơ bản của bún thang.</p></div> <p style="text-align: justify;">Yêu cầu từng nguyên liệu cũng nhiêu khê lắm. Bún phải là loại bún nhỏ, dai chứ bún sợi to bị coi là thô kệch. Thịt gà dẫu chẳng “kinh thiên động địa” gì nhưng phải xé bằng tay thật nhỏ và xốp chứ thái bằng dao là thịt bết như ở quán phở gà tầm thường. Riêng phần da thái bằng dao, tuy nhiên, phải nhỏ như sợi bún.</p> <p style="text-align: justify;">Trứng gà dùng cho bún thang phải tươi, sau đó đem tráng thật mỏng, rồi dùng dao thái nhẹ nhàng thành những sợi “tóc mây bay ngang trời”. Giò lụa cũng thế, phải là loại giò “thửa”, không dùng loại giò pha bột hay giò “công nghiệp”. Có thế thì khi thái nhỏ sợi da gà thì giò mới không gãy, bở.</p> <p style="text-align: justify;">Củ cải dầm phải thơm, giòn không bị ủng do phơi thiếu nắng, cũng thái chỉ giống kiểu ăn sashimi của người Nhật. Mắm tôm, chanh cốm, ớt tươi, rau răm, rau mùi… cũng phải tươi, ngon thì mới xứng dự phần vào bát bún thang.</p> <p style="text-align: justify;">Nước dùng cho bún thang nhất quyết phải là nước dùng gà chứ không chơi xương lợn hay hay bò vì mùi ôi. Cứ ninh xương gà với hành củ nướng, gừng nướng, thêm một vốc sá sùng hoặc tôm he cho ngọt lừ, đem lọc lấy nước trong, đun sôi sùng sục rồi mới đem chan được.</p> <p style="text-align: justify;">Chưa kể, việc trình bày bát bún thang cũng cầu kỳ như nghệ thuật cắm hoa Ikebana. Bún chần qua nước nóng già rồi cho vào 2/3 lòng bát tô, thịt gà cả da lẫn thịt xếp ở giữa, một nhúm trứng xếp đối xứng với một nhúm giò, tôm nõn bóc chỉ xếp cạnh củ cải dầm và rau thơm, mắm tôm chanh ớt đánh sủi bọt rưới một thìa lên rau để tí chan nước thẳng vào chỗ đó, sang trọng nữa thì thêm một giọt lệ cà cuống.</p> <p style="text-align: justify;">Cách đây chưa lâu, có nghệ nhân nấu ăn khẳng định, xếp bún thang phải có khuôn, bên trong chia làm 5 ô. Đặt cái khuôn đó lên mặt bát bún rồi lần lượt xếp từng món vào từng ô cho thật cân đối, riêng biệt như đất nền phân lô rồi nhấc khuôn ra, sau đó mới chan. Thế mới đúng là bún thang, bởi thang có nghĩa là khuôn.</p> <p style="text-align: justify;">Làm bún thang đã thế, ăn bún cũng không thể xuề xòa được. Người ta thường nấu bún thang vào dịp Tết để đãi nhau cho tỏ tình trân trọng. Nơi ăn bún cũng phải lịch sự, thanh nhã, có bàn ghế ngồi chĩnh chện chứ không chồm hỗm xì xụp trên vỉa hè trắng xóa giấy ăn và bụi đường. Có thế bún thang mới xứng danh là tuyệt đỉnh ẩm thực Hà Nội. Chỉ kém nghi lễ trà đạo ở khoản người nhận bát bún không phải cúi lạy người chan bún, rồi xoay bát 3 lần theo chiều kim đồng hồ rồi mới ăn, và sau khi ăn xong lại xoay 3 lần ngược lại trước khi trả bát mà thôi.</p> <p style="text-align: justify;">Nhưng bún thang thực chất không phải “kiêu kỳ, kiểu cách, quy củ” đến thế mà chỉ bình thường như muôn loại bún khác mà thôi. Thậm chí, bún thang thể hiện tinh thần “tiết kiệm” đặc sắc của dân tộc, bên cạnh món rau tập tàng hay món tả pí lù của người Hoa.</p> <div id="attachment_203056" style="width: 510px" class="wp-caption aligncenter"><img class="wp-image-203056 size-full" title="Nhiều nhà hàng tại Hà Nội dùng củ cải loại to trong bát bún thang. Ảnh: Linh Hương" src="https://cdn3.ivivu.com/2017/04/bun-thang-ha-noi-mon-an-binh-dan-trong-lop-vo-quy-toc-ivivu-2.jpg" alt="Nhiều nhà hàng tại Hà Nội dùng củ cải loại to trong bát bún thang. Ảnh: Linh Hương" width="500" height="627" /><p class="wp-caption-text">Nhiều nhà hàng tại Hà Nội dùng củ cải loại to trong bát bún thang. Ảnh: Linh Hương</p></div> <p style="text-align: justify;">Đúng là bún thang trước đây thường được nấu vào dịp Tết là bởi nó có tính chất “tổng kết Tết”. Sau những ngày ăn Tết ê hề mâm cao cỗ đầy, chạn nhà nào (nay là tủ lạnh) cũng còn thừa tí gà, tí giò… Nếu để ăn riêng rẽ thì ngán, chi bằng đem hết ra nấu bún. Và một loại bún thập cẩm giống như nồi tả pí lù (gồm thịt bò, thịt ngựa, thịt cừu, thịt lợn, thủy hải sản, xí quách tức xương động vật… thừa ra sau khi ăn Tết hay yến tiệc, được đem nấu chung một nồi) hình thành, vừa để đổi khẩu vị, vừa không để phí thực phẩm.</p> <p style="text-align: justify;">Nước luộc gà để nấu măng miến thừa đem hết ra đun với hành nướng và củ su hào. Nhà giàu có thì dùng sá sùng, tôm khô hay mực khô cho ngọt nước. Thịt gà thừa dồn đĩa đem ra xé nhỏ cho đỡ phải nhai. Đầu giò loăn xoăn khó tạo hình thì thái sợi. Trứng gà tráng thái mỏng thêm màu sắc cho bát bún vui mắt. Củ cải dầm vốn dùng để ăn ghém ngày Tết cũng trưng dụng nốt để nhai cho vui tai.</p> <p style="text-align: justify;">Điều lạ kỳ nhất là rau răm và mắm tôm. Hai thứ này đi với bún có vẻ không hợp, nhất là thứ bún đó lại cho là thuộc giống cao sang. Thế nhưng, với bún thang lại hữu dụng và đúng điệu. Bởi vì các nguyên liệu để nấu bún thang đều là đồ thừa, để lâu ngày. Với cái thời tiết ẩm nồm ở Hà Nội và miền Bắc mùa xuân, thức ăn rất dễ biến chất, nặng thì thiu chảy nước, nhẹ thì có mùi. Với tinh thần “ăn hết không bỏ phí” không có lẽ lại vứt đồ, thế nên để át chế cái mùi không mong muốn đó, người ta dùng mắm tôm chanh ớt, hợp lực cùng rau răm ngái, chưa đủ thì cả tinh dầu cà cuống để khử mùi. Đấy mới là cốt yếu của bún thang, chứ không phải là “sự hòa quyện của gà béo, trứng ngậy, giò bùi”.</p> <p style="text-align: justify;">Chưa hết, để an tâm, yêu cầu nước dùng phải thật nóng, sôi sùng sục, nóng rẫy tay của bát bún thang, cộng mùi vị của mắm tôm, rau răm sẽ chia tâm trí của người ăn theo nhiều hướng, dẫn tới hiệu quả: khi ăn không còn thấy mùi khó chịu nữa, thậm chí lại ngon miệng vì khẩu vị được thay đổi. Có lẽ, chính vì yêu cầu nước dùng phải nóng như thang canh của người Hoa mà thứ bún này được gọi là bún thang chăng? Còn luận điểm dùng thang tức khuôn để chia “phân lô” nhân bánh là không có cơ sở. Bởi ở diện đại chúng, chẳng thấy ai dùng cái thang ấy cả, có chăng đó là sáng chế cá nhân.</p> <p style="text-align: justify;">Bún thang thực chất là thế. Một món sáng tạo nhằm tận dụng đồ ăn thừa, rất dân dã và chẳng quý tộc chút nào. Bây giờ, khi điều kiện kinh tế khá giả hơn, người ta không còn dùng đồ thừa để nấu nữa mà là nguyên liệu tươi ngon. Tuy nhiên, vẫn phải dùng mắm tôm, rau răm bởi thiếu nó thì thành bún gà rồi.</p> <p style="text-align: justify;">Bún thang ăn ở đâu ngon? Chỉ có bún thang nấu ở nhà là ngon nhất vì nó thường được ăn vào dịp Tết, gia đình sum họp. Chứ bún thang bán nhan nhản ở Hà Nội, thậm chí có quán nổi danh, nườm nượp người vào ăn thì đa phần thực khách đều chỉ là… người ghé qua Hà Nội cả. Còn đây là chỉ dẫn cho ai thích ăn bún thang đúng kiểu: Nước dùng gà ra ngõ Thanh Hà gần chợ Đồng Xuân mua vào buổi chiều, bởi khi đó nồi nước đã luộc hàng chục hoặc cả trăm con gà rồi, chất lượng khỏi băn khoăn. Thịt gà hai bên lườn bán sẵn, về chỉ việc xé phay. Giò ngon mua về tự thái. Trứng tự tráng, tự thái, dày mỏng tùy ý, miễn là miệng nhai phải cảm nhận được đây là trứng. Tôm có hay không tùy. Bún Phú Đô sợi nhỏ bán ê hề, mua về chần nước sôi cho nóng. Củ cải tự dầm không thì mua ngoài chợ. Đun nước dùng gà sôi già, nêm nếm vừa ăn, xếp bún đã chần nóng vào bát rồi bày đầy đủ nhân, chan nước dùng và xơi ngay cho nóng.</p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon star"></span></p> <h4 style="text-align: justify;"><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN HÀ NỘI GIÁ TỐT</strong></h4> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/pullman-hanoi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Pullman Hanoi</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/khach-san-novotel-suites-ha-noi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Novotel Suites Hà Nội</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/khach-san-muong-thanh-grand-ha-noi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Mường Thanh Grand Hà Nội</a></strong></p> <p style="text-align: justify;"></div>', 'Bún thang từ lâu đã được coi là thức bún có đẳng cấp hơn hẳn các loại bún khác ở Hà Nội về mặt tinh tế, cầu kỳ, kiểu cách… kể từ nguyên liệu, cách chế biến đến thưởng thức.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-4.jpg', NULL),
(3, '9', '3', 'Tổ hợp những quán ăn Hà Nội lúc nào cũng tấp nập khách ở khu Trường Chinh, Đại La', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Khu Trường Chinh, Đại La không phải là phố ẩm thực nhưng cũng có kha khá những quán ăn nổi tiếng đến độ luôn tấp nập khách.</em></strong><span id="more-202641"></span></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/ha-noi/" target="_blank">Du lịch Hà Nội</a></strong></div></p> <h2 style="text-align: left;">Tổ hợp những quán ăn Hà Nội lúc nào cũng tấp nập khách ở khu Trường Chinh, Đại La</h2> <p style="text-align: justify;"><strong>1. Bún bung Bà Minh</strong></p> <p style="text-align: justify;">Quán nằm gần đoạn ngã tư Trường Chinh giao với Ngã Tư Sở, khá nhỏ khi chỉ có vài bàn vỉa hè và vài bàn kê bên trong nhà một cửa hàng bán đồ điện. Quán chuyên bún bung với các lựa chọn đi kèm khá đa dạng như mọc, sườn, thịt chân giò hay lưỡi.</p> <p><img class="aligncenter wp-image-202594 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-1.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-1" width="650" height="867" /> <img class="aligncenter wp-image-202595 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-2.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-2" width="650" height="682" /></p> <p style="text-align: justify;">Điểm cộng của bún ở đây là thứ nước dùng thơm, chua chua vị dễ chịu. Các nguyên liệu ăn kèm cũng khá ổn, mọc mềm, thơm vì có cả nấm hương và mộc nhĩ, vừa thơm lại giòn giòn sừn sựt. Miếng sườn được nấu kĩ, khá mềm nhưng vẫn còn độ chắc của thịt. Dọc mùng ăn cũng không bị ngứa.</p> <p><img class="aligncenter wp-image-202596 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-3.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-3" width="650" height="975" /> <img class="aligncenter wp-image-202597 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-4.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-4" width="650" height="975" /> <img class="aligncenter wp-image-202598 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-5.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-5" width="650" height="867" /></p> <p style="text-align: justify;">Một tô bún ở đây trung bình là 25.000 đồng, trừ khi gọi nhiều loại nhân ăn kèm, khá hợp lý để ăn sáng, kể cả trong những ngày kinh tế chẳng mấy rủng rỉnh. Các loại nhân ăn kèm trong bát bún không quá hào phóng nhưng tính ra vẫn đủ ăn và phù hợp với giá bán. Đặc biệt quán có kèm trà đá miễn phí. Quán chỉ bán sáng, đến khoảng 9 giờ là nghỉ.</p> <p style="text-align: justify;"><strong>2. Bún ốc cô Nga</strong></p> <p style="text-align: justify;">Quán nằm trong một con ngõ nhỏ, khá gần với hàng bún bung Bà Minh. Quán bún ốc cô Nga chỉ bán buổi sáng với đủ các loại ốc to, nhỏ và nấu theo kiểu ốc chuối đậu. Theo nhận xét của nhiều người thì nước dùng bún ở đây có vị chua cay, đậm đà, đúng hương vị cổ truyền. Đặc biệt ốc trong tô bún làm rất sạch, con ốc mập mạp, chắc thịt, ăn giòn.</p> <p><img class="aligncenter wp-image-202599 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-6.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-6" width="650" height="711" /> <img class="aligncenter wp-image-202601 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-8.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-8" width="650" height="650" /> <img class="aligncenter wp-image-202600 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-7.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-7" width="650" height="657" /></p> <p style="text-align: justify;">Quán bún này nằm khá sâu trong ngõ nhưng cứ sáng đến là tấp nập khách. Một bát bún trung bình chỉ 25.000 đồng. Ngoài bún ốc, ở đây còn có riêu bò hoặc có thể ăn thập cẩm tùy ý. Về không gian, quán có cả chỗ ngồi trong nhà và chỗ ngồi ngoài ngõ.</p> <p><img class="aligncenter wp-image-202602 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-9.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-9" width="650" height="485" /> <img class="aligncenter wp-image-202603 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-10.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-10" width="650" height="397" /></p> <p><strong>3. Xôi sườn cay, Trường Chinh</strong></p> <p style="text-align: justify;">Đây là một quán xôi vỉa hè nằm trên phố Trường Chinh, đoạn gần với Ngã Tư Sở. Dù là quán vỉa hè nhưng lượng khách ghé đến đây rất đông khách, lý do bởi món xôi sườn cay với thứ nước sốt cay xè của quán vô cùng ngon miệng và độc đáo.</p> <p><img class="aligncenter wp-image-202604 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-11.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-11" width="650" height="650" /> <img class="aligncenter wp-image-202605 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-12.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-12" width="650" height="650" /></p> <p style="text-align: justify;">Bát xôi ở đây có phần hơi bé, một bát xôi chỉ tầm lưng lưng nhưng không quá ít mà vừa để đủ no. Ngoài món sườn rang vừa miệng, thơm lừng, ở đây còn có có đủ các loại chả, thịt, xúc xích như nhiều hàng xôi khác.</p> <p><img class="aligncenter wp-image-202606 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-13.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-13" width="640" height="640" /> <img class="aligncenter wp-image-202608 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-15.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-15" width="600" height="421" /></p> <p style="text-align: justify;">Xôi Trường Chinh chuyên bán tối, giờ bán từ 5 giờ chiều đến nửa đêm, giá xôi ở đây khoảng 25-35.000 đồng/suất tuỳ loại. Thế nên nếu bạn muốn tìm một món chắc dạ để ăn thì đây là địa chỉ rất nên ghé. Đặc biệt vào những ngày mát trời, một bát xôi với đầy ắp chả thịt lại có thêm nước sốt cay thì quả là ngon không gì bằng.</p> <p style="text-align: justify;"><strong>4. Bún đậu Cây Bàng, Đại La</strong></p> <p style="text-align: justify;">Quán bún đậu này đông khách đến mức cứ đến buổi trưa là khách lại ngồi tràn ở vỉa hè đển ăn. Điểm đáng chú ý ở quán này là các đồ gia giảm cho suất bún được bày biện cực đẹp mắt và hợp lý để phục vụ công tác chế biến nhanh nhất. Đậu được chiên sơ, chả xếp lớp, mắm tôm xếp thành chồng, có người cắt bún riêng. Cảnh tượng náo nhiệt ấy khiến thực khách đặc biệt ấn tượng.</p> <p><img class="aligncenter wp-image-202609 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-16.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-16" width="600" height="900" /> <img class="aligncenter wp-image-202611 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-18.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-18" width="600" height="600" /> <img class="aligncenter wp-image-202610 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-17.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-17" width="600" height="400" /></p> <p style="text-align: justify;">Nói bún đậu Đại La ngon chẳng hề quá lời, đậu là thứ đậu đặt ở chính làng Mơ béo, mềm, chiên vàng giòn 4 mặt. Khi ăn, vẫn cảm nhận được độ giòn của vỏ, lớp ruột mềm thơm, không khô. Đặc biệt phần nước chấm ở đây vô cùng đặc biệt, dù là mắm tôm, nước mắm hay bột canh đều được cho thêm chút sa tế tự làm giúp cho phần nước chấm có một vị riêng rất đặc trưng.</p> <p><img class="aligncenter wp-image-202612 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-19.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-19" width="600" height="414" /></p> <p style="text-align: justify;">Điểm hạn chế duy nhất của quán ăn này là khi tới vào giờ cao điểm bạn sẽ gặp khó khăn đôi chút trong việc đỗ xe, bạn có thể gửi xe ở chợ gần đó hoặc kiên nhẫn đợi ít phút để chú nhân viên giúp bạn cất xe nhé! Quán bán từ khoảng 11h trưa tới 2h chiều.</p> <p style="text-align: justify;"><strong>5. Mỳ vằn thắn Tuấn Tàu</strong></p> <p style="text-align: justify;">Mỳ vằn thắn ở đây khá nổi tiếng, một bát đầy đủ gồm thịt xá xíu, sủi cảo hấp lẫn sủi cảo rán, trứng, gan, rau cải, hẹ, khá đầy đặn. Nước dùng ở đây đậm đà, lượng mỳ cho cực nhiều nên đủ để ăn no căng bụng.</p> <p><img class="aligncenter wp-image-202613 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-20.jpeg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-20" width="650" height="826" /> <img class="aligncenter wp-image-202614 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-21.jpeg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-21" width="650" height="650" /> <strong><img class="aligncenter wp-image-202615 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-22.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-22" width="650" height="650" /></strong></p> <p><strong>6. Quẩy Nóng – Đại La</strong></p> <p style="text-align: justify;">Quẩy ở đây được làm rất khéo. Bột bánh mỏng manh, hai cánh quẩy nở đều, lại được rán rất vừa để có màu vàng nâu hơi ngả sang màu cánh gián. Quẩy nóng giòn rụm, nhưng vẫn giữ được độ xốp, độ dẻo của bột. Hai cánh quẩy cũng “buông thả” tự do, uốn lượn, thỉnh thoảng có chiếc cong cong như hình cánh bướm chứ không cứng cáp, thẳng đuột như quẩy giòn.</p> <p><img class="aligncenter wp-image-202616 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-23.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-23" width="600" height="400" /> <img class="aligncenter wp-image-202617 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-24.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-24" width="600" height="400" /></p> <p style="text-align: justify;">Nước mắm chấm quẩy ngọt hơn và nhạt hơn nước mắm chấm nem, nhưng được gia giảm thêm bởi chút dưa góp, khi thì dưa chuột, lúc là đu đủ, cà rốt và không thể thiếu tương ớt. Thêm nữa, quán ở đây dùng tương ớt xay kiểu “cổ” (tương ớt ăn phở) để thêm vào nước mắm chứ không dùng tương ớt mịn hay tương ớt cà chua như ở một số quán khác.</p> <p><img class="aligncenter wp-image-202618 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-25.jpg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-25" width="600" height="400" /></p> <p><strong>7. Lẩu Gật Gù</strong></p> <p style="text-align: justify;">Quán nằm trong top những quán lẩu ngon, rẻ ở Hà Nội với các món lẩu đa dạng. Ở quán, bạn dễ dàng tìm thấy đủ món lẩu như lẩu gà, lẩu ếch và lẩu riêu cua bắp bò với mức giá trung bình 150.000 đồng đến 200.000 đồng/ nồi đủ cho 3 đến 4 người ăn. Thậm chí lượng đồ ăn được đánh giá là hào phóng và đầy đặn so với mức giá.</p> <p><img class="aligncenter wp-image-202621 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-28.jpeg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-28" width="650" height="1168" /></p> <p style="text-align: justify;">Bên cạnh yếu tố đồ ăn giá bình dân, quán còn có ưu điểm là không gian mới, thoáng đãng, rộng có phòng riêng cho các nhóm hội họp trên 30 người, có khu vực để xe trong nhà an toàn và rộng,… Giá rẻ, lượng khách đến đông nên quán ăn này thường hay hết bàn, đặc biệt vào dịp lễ Tết, cuối tuần.</p> <p style="text-align: right;"><img class="aligncenter wp-image-202622 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-29.jpeg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-29" width="650" height="657" /> <img class="aligncenter wp-image-202623 size-full" src="https://cdn3.ivivu.com/2017/04/to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-30.jpeg" alt="to-hop-nhung-quan-an-luc-nao-cung-tap-nap-khach-o-khu-truong-chinh-dai-la-ivivu-30" width="650" height="563" /></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon star"></span></p> <h4 style="text-align: justify;"><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN HÀ NỘI GIÁ TỐT</strong></h4> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/pullman-hanoi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Pullman Hanoi</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/khach-san-novotel-suites-ha-noi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Novotel Suites Hà Nội</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ha-noi/khach-san-muong-thanh-grand-ha-noi?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Mường Thanh Grand Hà Nội</a></strong></p> <p style="text-align: justify;"></div>', 'Khu Trường Chinh, Đại La không phải là phố ẩm thực nhưng cũng có kha khá những quán ăn nổi tiếng đến độ luôn tấp nập khách.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-7.jpg', NULL);
INSERT INTO `baiviet` (`IDBV`, `IDKhuVuc`, `IDLoaiTin`, `TieuDe`, `NoiDung`, `TomTat`, `NoiBat`, `NgayViet`, `NgayCapNhat`, `IDNhanVien`, `updated_at`, `created_at`, `anhdaidien`, `luotxem`) VALUES
(4, '9', '3', '10 quán cà phê ở Sài Gòn với tông màu trắng chủ đạo, cứ đến là có ảnh đẹp', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Ngoài cà phê thơm, bánh ngọt thì những hàng quán trong danh sách này còn có không gian bên trong cực kì bắt mắt với tông màu trắng chủ đạo. Ghi nhanh những cái tên này vào sổ để đi dần ngay nhé!</em></strong><span id="more-203190"></span></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/tp-hcm/" target="_blank">Du lịch TP. Hồ Chí Minh</a></strong></div></p> <h2 style="text-align: justify;">10 quán cà phê ở Sài Gòn với tông màu trắng chủ đạo, cứ đến là có ảnh đẹp</h2> <p style="text-align: justify;">Giới trẻ luôn là những người có chuẩn mực rất cao về việc thưởng thức. Đối với họ, đi uống cà phê, đi uống nước không chỉ đơn giản là bỏ tiền ra để mua một thứ gì đó giải khát, mà đó còn là tiền để mua cảm giác thoải mái, mua sự dễ chịu, mua thái độ phục vụ tận tình và tất nhiên là mua cả một không gian thật xứng đáng với số tiền mà họ đã bỏ ra. Không cần biết quán này ngon dở thế nào nhưng chỉ cần “ngoại hình” nhếch nhác, kém chăm chút hay không hợp thời là bảo đảm đã mất điểm ngay và khó lòng níu chân được những vị khách thông minh này.</p> <p style="text-align: justify;">Hiểu được điều đó nên trong thời gian qua, ở Sài Gòn liên tục mọc lên những hàng quán với vẻ ngoài không chê vào đâu được. Bên cạnh việc chăm chút cho từng li nước, từng món ăn thì rất nhiều hàng quán đã có sự đầu tư hợp lí về không gian của mình nhằm đem đến những trải nghiệm tốt nhất cho các bạn trẻ. Đừng bất ngờ nếu đến một ngày mở Facebook/ Instagram lên và thấy giới trẻ Sài Gòn check-in ở những quán nước đẹp lung linh không thua gì những studio hay những hàng quán xinh xắn mà ta vẫn thường trầm trồ mỗi khi xem ảnh trên Pinterest.</p> <p style="text-align: justify;">Không tin ư, đây là danh sách 10 quán cà phê với tông màu trắng hợp thời và bắt mắt đang làm mưa làm gió ở Sài Gòn. Ngoài cà phê thơm, bánh ngọt thì khi đến với những cái tên này, thứ mà bạn nhận được còn là một không gian cực kì chuẩn nữa đấy!</p> <p><strong>1. Thinker&Dreamer Coffee</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203018 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-1.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-1" width="650" height="829" />Quán nằm ở vị trí rất đẹp là chung cư 42 Nguyễn Huệ. Thích nhất cách đặt tên quán, không hiểu sao cảm thấy cái tên quán rất hay ho và gợi mở. Toàn bộ đồ uống của Thinker&Dreamer được để trên thớt gỗ nên nhìn khá đẹp mắt. Ghé quán bạn nên gọi thử món đào macchiato. Không gian nơi đây tuy hơi nhỏ nhưng bù lại sở hữu ánh sáng đẹp, nhiều món đồ trang trí bắt mắt và tất nhiên là không thể không kể đến tông màu trắng chủ đạo đẹp mắt.</p> <p><strong>2. Heritage Concept Chill Hub</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203019 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-2.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-2" width="650" height="829" />Heritage chính là nơi đầu tiên mang mô hình chill hub (không gian thư giãn, gặp gỡ) về Sài Gòn. Quán có nhiều khu vực khác nhau, đặc biệt là có vô số góc để chụp hình sống ảo. Tầng trệt thì mang hơi hướng vintage hoài cổ, góc vườn sau quán cũng hay ho đáng để thử. Không gian trên lầu có tông màu trắng với bàn marble, ánh sáng tốt cực thích hợp cho bạn nào thích chụp flatlay. Menu khá đa dạng và đầy đủ.</p> <p><strong>3. Slow&Chill Coffee</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203020 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-3.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-3" width="650" height="829" />Quán nằm trên lầu 2 chung cư 90 Nguyễn Huệ, bàn ghế nhỏ nhỏ xinh xinh. Điểm nhấn của quán chính là phần cửa kính lớn với view nhìn ra phố đi bộ Nguyễn Huệ. Những hôm nào trời mưa mà ngồi đây thì đúng là lãng mạn không gì bằng. Slow&Chill nổi tiếng với món choco-volcano lạ mắt. Ngoài ra trong những ngày oi bức thế này bạn nên chọn sorbet soda uống cho mát nha!</p> <p><strong>4. Vietcetera Cafe</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203021 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-4.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-4" width="650" height="829" />Một cái tên còn khá mới mẻ với giới trẻ Sài Gòn nhưng lại sở hữu không gian lẫn chất lượng nước uống “không phải dạng vừa đâu”. Vietcetera được mở ra với mục đích tạo nên một không gian cộng đồng để mọi người có thể đến đây để gặp gỡ, lắng nghe, giao lưu và trò chuyện. Với slogan “Local stories for a global audience”, bảo đảm bạn sẽ có được những phút giây đáng nhớ khi đến với nơi đây. Cửa hàng phục vụ nhiều loại nước uống và đồ ăn đa dạng, tha hồ để lê la cả ngày mà không biết chán!</p> <p><strong>5. JUS – Juice Up Saigon</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203022 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-5.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-5" width="650" height="829" />Đúng như tên gọi của mình, JUS – Juice Up Saigon là nơi chuyên phục vụ những món nước ép trái cây. Quán nằm ngay trung tâm quận 1 nên khá dễ kiếm. Các thức uống ở đây ngoài chuyện đẹp mắt thì còn rất thơm ngon và có lợi cho sức khỏe. Không gian quán tuy không quá rộng nhưng vẫn đủ để bạn có thể ngồi lại và trò chuyện với bạn bè mình.</p> <p><strong>6. Be & John Coffee</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203023 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-6.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-6" width="650" height="829" />Be & John ban đầu được nhiều người biết đến nhờ vào không gian với tông màu trắng chủ đạo cực bắt mắt. Tuy nhiên khi đến rồi thì mới biết đó vẫn chưa phải là điểm tuyệt nhất của nơi này. Đầu tiên phải kể đến thái độ phục vụ của các bạn nhân viên cực kì thân thiện, biết cách chiều lòng khách hàng và hoàn toàn vui vẻ khi mọi người chụp ảnh trong quán. Kế đến chính là những món nước được pha rất vừa miệng, điển hình như caramel đá xay, chocolate đá xay. Các loại bánh như passion cheesecake hay plan gato đều được đánh giá là ngon đúng chuẩn. Nói chung là rất đáng để thử!</p> <p><strong>7. Bosgaurus Coffee</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203024 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-7.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-7" width="650" height="829" />Quán có không gian rất rất rất rộng rãi và thoải mái. Ban ngày trời nóng thì có thể ngồi bên trong vừa hưởng máy lạnh vừa nhâm nhi cà phê cùng bạn bè hoặc chụp hình “sống ảo”. Vì không gian to oành như thế nên chả sợ ai đụng ai hay vô tình có vài nhân vật không mời mà xuất hiện trong khung hình. Còn hôm nào ông trời vui tính mát mẻ thì có thể ra ngoài sân nằm thư giãn. Đã vậy khắp quán là vô số những mảng xanh mát mắt, nắng lúc nào cũng ngập tràn càng góp phần thành công cho công cuộc sống ảo của mọi người.</p> <p><strong>8. Loft Cafe</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203025 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-8.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-8" width="650" height="829" />Loft Cafe nằm trên chung cư 26 Lý Tự Trọng. Quán nổi tiếng với cái khung cửa sổ đồng hồ nhìn rất art và bắt mắt. Nơi đây có kiểu tường màu trắng và bàn marble thích hợp cho những bạn nào muốn chụp flatlay. Nếu như đang muốn tìm một không gian nào đó thật riêng tư nhưng lại nằm ngay giữa trung tâm thành phố thì đây chính là một gợi ý phù hợp dành cho bạn!</p> <p><strong>9. The Open Space Bakery & Coffee</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203026 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-9.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-9" width="650" height="829" />The Open Space sở hữu không gian khá rộng, được chia tách ra thành nhiều khu vực riêng và tổng thể vẫn là sự kết hợp giữa màu trắng và màu nâu gỗ ấm áp. Bên cạnh đó cửa hàng này còn có thêm khu vực sân vườn cực mát mắt và rộng rãi. Tất cả các loại bánh tại đây đều được nướng trong ngày, ngay tại căn bếp của quán nên bạn không cần phải lăn tăn về chất lượng hay độ tươi ngon. Điều thú vị nhất là không chỉ ăn, bạn còn có thể đăng kí tham gia những lớp học làm bánh tại quán để có thể tự tay làm ra những mẻ bánh của riêng mình nữa đó!</p> <p><strong>10. The Vintage Emporium Cafe</strong></p> <p style="text-align: justify;"><img class="aligncenter wp-image-203027 size-full" src="https://cdn3.ivivu.com/2017/04/10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-10.jpg" alt="10-quan-ca-phe-tong-trang-o-sai-gon-cu-den-la-co-anh-dep-ivivu-10" width="650" height="829" />Không gian của quán cực kì hoài cổ đúng với tên gọi. Khắp mọi ngóc ngách ở đây là những món đồ sẽ khiến bạn ố á bất ngờ và phải thốt lên “cái này hồi xưa nhà mình cũng có nè!” Nếu so với mặt bằng chung thì giá cả ở The Vintage Emporium khá cao. Tuy nhiên bù lại bạn sẽ có được một không gian cực kì riêng tư và những món ăn, những món nước được thực hiện bài bản đúng chuẩn “thượng hạng”. Playlist nhạc của quán cũng là một điểm cộng lớn không thể bỏ qua!</p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon star"></span></p> <h4 style="text-align: justify;"><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN SÀI GÒN GIÁ TỐT</strong></h4> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/sheraton-saigon-hotel-towers?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Sheraton Saigon Hotel & Towers</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/khach-san-ibis-saigon-south?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách Sạn Ibis Saigon South</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/khach-san-the-reverie-saigon?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn The Reverie Sài Gòn</a></strong></p> <p style="text-align: justify;"></div>', 'Ngoài cà phê thơm, bánh ngọt thì những hàng quán trong danh sách này còn có không gian bên trong cực kì bắt mắt với tông màu trắng chủ đạo. Ghi nhanh những cái tên này vào sổ để đi dần ngay nhé!', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'victoria-chau-doc-ivivu-7.jpg', NULL),
(5, '9', '3', '5 món ngon Sài Gòn cho buổi chiều ngon lành và dân dã', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Nói đến quà chiều <a href="https://www.ivivu.com/blog/category/viet-nam/tp-hcm/" target="_blank">Sài Gòn</a>, sẽ vô cùng thiếu sót nếu không nhắc tên súp cua, bánh tráng trộn hay dĩa flan mát lạnh.</em></strong><span id="more-202376"></span></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/tp-hcm/" target="_blank">Du lịch TP. Hồ Chí Minh</a></strong></div></p> <h2 style="text-align: justify;">5 món ngon Sài Gòn cho buổi chiều ngon lành và dân dã</h2> <p style="text-align: justify;"><strong>Súp cua</strong></p> <p style="text-align: justify;">Súp nấu dễ, nhưng nấu ngon lại cần sự đầu tư của nhiều xương thịt, gà xé, thịt cua ghẹ… súp nấu đặc nhưng không đặc bột mà đặc thịt thà, ăn thế mới “đã miệng”. Một chén súp dầy đặn thịt cua, gà xé, trứng cút quả là<em><strong> món ngon Sài Gòn</strong></em> cho buổi chiều khi nó vừa bình dân, vừa dễ ăn lại dễ kiếm.</p> <div id="attachment_202323" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-202323 size-full" title="@maryderoux" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-1.jpg" alt="@maryderoux" width="650" height="488" /><p class="wp-caption-text">@maryderoux</p></div> <div id="attachment_202324" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-202324 size-full" title="@ivykute" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-2.jpg" alt="@ivykute" width="650" height="650" /><p class="wp-caption-text">@ivykute</p></div> <p style="text-align: justify;">Thêm vào đó, món này cũng không gây béo như các món ăn có nhiều tinh bột khác. Trời Sài Gòn buổi chiều thường không quá nóng bức, thế nên một tô súp với quả trứng bắc thảo bùi bùi giá chỉ khoảng 15.000 đồng, ai mà từ chối.</p> <p style="text-align: justify;"><strong>Bánh tráng trộn</strong></p> <p style="text-align: justify;">Bánh tráng trộn từ lâu đã là một món ăn vặt gần gũi với các bạn trẻ, khắp mọi nơi ở Sài Gòn điều dễ dàng tìm được những xe hoặc thậm chí là những chiếc bàn nhỏ bày đủ thứ linh tinh trên đó, vì vậy có thể nói bánh tráng trộn gần như đã trở thành một món đặc sản của riêng người Sài Gòn.</p> <div id="attachment_202325" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-202325 size-full" title="@buiphuongyen1711" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-3.jpg" alt="@buiphuongyen1711" width="650" height="650" /><p class="wp-caption-text">@buiphuongyen1711</p></div> <div id="attachment_202326" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-202326 size-full" title="@he.shui.xian" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-4.jpg" alt="@he.shui.xian" width="650" height="650" /><p class="wp-caption-text">@he.shui.xian</p></div> <div id="attachment_202327" style="width: 650px" class="wp-caption aligncenter"><img class="wp-image-202327 size-full" title="@suriuyenvo" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-5.jpg" alt="@suriuyenvo" width="640" height="640" /><p class="wp-caption-text">@suriuyenvo</p></div> <p style="text-align: justify;">Bánh tráng trộn đặc biệt được ưa thích để làm quà chiều bởi dễ ăn, không ngán, không mấy ảnh hưởng đến các bữa tối. Một bịch bánh tráng trộn với nhiều loại topping như khô bò, khô mực, ruốc giá cũng chỉ 15.000 đồng là dư sức ấm bụng.</p> <p style="text-align: justify;"><strong>Bánh flan</strong></p> <p style="text-align: justify;">Và dĩ nhiên không thể không nhắc đến món bánh flan trứ danh của giới trẻ Sài Gòn. Bởi từ bao năm, đây chính là món ăn vặt được nhớ tới nhiều nhất trời về chiều bụng đói râm ran, lại thèm cái gì đó man mát.</p> <p><img class="aligncenter wp-image-202328 size-full" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-6.jpeg" alt="5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-6" width="650" height="869" /></p> <p style="text-align: justify;">Những chiếc bánh flan mềm mịn thơm trứng gà, có thể chan thêm cốt dừa béo ngậy, ăn cùng cafe đen đặc và đá đập nhuyễn quả là ngon miễn bàn. Một dĩa bánh flan trung bình chỉ 15, 20.000 đồng là cũng đủ để bạn vừa ăn vừa nói chuyện cười đùa cùng hội bạn, không no căng nhưng đủ “dằn bụng” để về kịp cơm tối.</p> <p><img class="aligncenter wp-image-202329 size-full" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-7.jpeg" alt="5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-7" width="650" height="915" /><strong>Bột chiên</strong></p> <p style="text-align: justify;">Bột chiên thật ra rất đơn giản, chỉ là những khối bột gạo được xắt vuông vừa ăn, xóc qua hắc xì dầu, nước tương, chiên trên chảo cho vàng giòn mặt ngoài, nóng bên trong, cùng với trứng, hành lá. Bột vừa chiên xong nóng hổi, giòn giòn với vị béo của trứng, bùi của đậu phộng cùng mùi hành phi thơm và vị chua cay mặn ngọt hài hòa của nước tương khiến ai cũng muốn ăn thật nhiều!</p> <p><img class="aligncenter wp-image-202330 size-full" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-8.jpg" alt="5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-8" width="1024" height="683" /></p> <p style="text-align: justify;">Đĩa bột chiên thơm nức mũi, bên ngoài giòn rộm, bên trong mềm lại có trứng bùi béo được ăn kèm với đồ chua và chấm với nước tương dấm có vị chua ngọt khiến ai nếm cũng phải mê. Và du khách khi đã nếm một lần rồi khi rời đi chắc chắn sẽ đôi lần thèm, nhớ.</p> <p style="text-align: justify;"><strong>Bò bía</strong></p> <p style="text-align: justify;">Món ăn vặt này nổi tiếng ở Sài Thành. Bò bía gồm lớp bánh tráng mỏng, cuốn bên trong là củ cải trắng hay củ sắn hấp mềm, con ruốc được rang mặn ngọt, một ít rau xà lách, rau thơm và vài lát lạp xưởng. Bò bía được dọn ăn kèm với tương ngọt, đậu phộng rang và đồ chua. Cuốn bò bía nhỏ, nhờ thế mà ăn vui miệng, lại chẳng nhiều thịt thà nên ăn vặt thì cực hợp.</p> <p><img class="aligncenter wp-image-202331 size-full" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-9.jpeg" alt="5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-9" width="650" height="433" /> <img class="aligncenter wp-image-202332 size-full" src="https://cdn3.ivivu.com/2017/04/5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-10.jpeg" alt="5-mon-qua-chieu-ngon-lanh-dan-da-va-dam-chat-sai-gon-ivivu-10" width="650" height="975" /></p> <p style="text-align: justify;">Hiện nay, bò bía vẫn bán nhiều trên các xe đẩy ở Sài Gòn, nhưng nếu muốn tìm ăn, bạn có thể đến khu ăn vặt đối diện trường đại học Sài Gòn quận 5.</p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon star"></span></p> <h4 style="text-align: justify;"><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN SÀI GÒN GIÁ TỐT</strong></h4> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/sheraton-saigon-hotel-towers?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Sheraton Saigon Hotel & Towers</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/khach-san-ibis-saigon-south?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách Sạn Ibis Saigon South</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/khach-san-ho-chi-minh/khach-san-the-reverie-saigon?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn The Reverie Sài Gòn</a></strong></p> <p style="text-align: justify;"></div>', 'Nói đến quà chiều Sài Gòn, sẽ vô cùng thiếu sót nếu không nhắc tên súp cua, bánh tráng trộn hay dĩa flan mát lạnh.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-6.jpg', NULL),
(6, '2', '1', 'Nghỉ dưỡng khách sạn A La Carte Đà Nẵng Beach ở phòng 2 Bedroom +đưa đón sân bay + ăn tối cho 4 khách chỉ 4.599.000 đồng', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/05/nghi-duong-khach-san-a-la-carte-da-nang-beach-o-phong-2-bedroom-dua-don-san-bay-an-toi-cho-4-khach-chi-4-599-000-dong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Là thành phố du lịch nổi tiếng, vì thế tại Đà Nẵng không thiếu những <a href="https://www.ivivu.com/blog/2016/10/top-7-resort-dat-xat-ra-mieng-ban-nhat-dinh-phai-check-in-khi-den-du-lich-da-nang/" target="_blank">khách sạn, resort cao cấp</a> dành cho du khách. Thế nhưng khách sạn <a href="https://www.ivivu.com/khach-san-da-nang/a-la-carte-da-nang-beach?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">A La Carte </a></strong><strong><a href="https://www.ivivu.com/khach-san-da-nang/a-la-carte-da-nang-beach?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Đà Nẵng Beach</a> </strong><strong>lại chưa bao giờ “hạ nhiệt” và đã trở thành một trong những khách sạn “hot” nhất Đà Nẵng mà bạn phải check-in.</strong></em></p> <p style="text-align: justify;"><span id="more-205635"></span></p> <p style="text-align: justify;"><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/da-nang/" target="_blank">Du lịch Đà Nẵng</a></div> </strong></p> <h2 style="text-align: left;">Nghỉ dưỡng khách sạn A La Carte Đà Nẵng Beach ở phòng 2 Bedroom +đưa đón sân bay + ăn tối cho 4 khách chỉ 4.599.000 đồng</h2> <p style="text-align: justify;">Điều gì tạo nên sự khác biệt của A La Carte so với các khách sạn khác ở Đà Nẵng? Theo ý kiến của nhiều du khách đánh giá thì có vài lý do chính. Đầu tiên, khách sạn này thực sự gây ấn tượng cho du khách với thiết kế mang đậm nét kiến trúc Pháp hiện đại, hào hoa, nội thất tinh tế kiểu Tây với gam màu trắng chủ đạo. Thứ hai là A La Carte được thiết kế theo dạng căn hộ nhỏ rất thích hợp cho những nhóm bạn hay những gia đình muốn ở quây quần cùng nhau trong những kì nghỉ.</p> <div id="attachment_205660" style="width: 485px" class="wp-caption aligncenter"><img class="wp-image-205660 size-full" title="Ảnh:@mynlucifer" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-4.png" alt="Ảnh:@mynlucifer" width="475" height="567" /><p class="wp-caption-text">Ảnh:@mynlucifer</p></div> <div id="attachment_205651" style="width: 485px" class="wp-caption aligncenter"><img class="wp-image-205651 size-full" title="Ảnh:@trang.pizaa" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-1.png" alt="Ảnh:@trang.pizaa" width="475" height="587" /><p class="wp-caption-text">Ảnh:@trang.pizaa</p></div> <div id="attachment_205658" style="width: 608px" class="wp-caption aligncenter"><img class="wp-image-205658 size-full" title="Ảnh:@2__seulgi" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-2.png" alt="Ảnh:@2__seulgi" width="598" height="584" /><p class="wp-caption-text">Ảnh:@2__seulgi</p></div> <p style="text-align: justify;">Nhưng điều đặc biệt nhất tạo nên “thương hiệu” của A La Carte đó chính là là hồ bơi tràn biển với góc nhìn trực tiếp ra bãi biển cát trắng đẹp đến ngỡ ngàng.</p> <div id="attachment_205678" style="width: 483px" class="wp-caption aligncenter"><img class="wp-image-205678 size-full" title="Ảnh:@__ihn" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-10.png" alt="Ảnh:@__ihn" width="473" height="510" /><p class="wp-caption-text">Ảnh:@__ihn</p></div> <div id="attachment_205680" style="width: 598px" class="wp-caption aligncenter"><img class="wp-image-205680 size-full" title="Ảnh:@chaeeun.park" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-15.png" alt="Ảnh:@chaeeun.park" width="588" height="576" /><p class="wp-caption-text">Ảnh:@chaeeun.park</p></div> <p style="text-align: justify;">Nếu bạn nghĩ rằng bể bơi ở các khách sạn, khu resort cao cấp chỉ để thư giãn, tận hưởng kỳ nghỉ khi ngâm mình hay vẫy vùng trong làn nước mát trong xanh thì đó là một suy nghĩ sai “không hề nhẹ”. Bởi vì đây còn là những tuyệt tác kiến trúc của các ý tưởng thiết kế độc đáo và hồ bơi tràn biển ở A La Carte có thể là một ví dụ minh chứng điển hình.</p> <div id="attachment_205666" style="width: 605px" class="wp-caption aligncenter"><img class="wp-image-205666 size-full" title="Ảnh:@vudaminhchau" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-8.png" alt="Ảnh:@vudaminhchau" width="595" height="452" /><p class="wp-caption-text">Ảnh:@vudaminhchau</p></div> <div id="attachment_205667" style="width: 608px" class="wp-caption aligncenter"><img class="wp-image-205667 size-full" title="Ảnh:@giseletran" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-9.png" alt="Ảnh:@giseletran" width="598" height="577" /><p class="wp-caption-text">Ảnh:@giseletran</p></div> <div id="attachment_205664" style="width: 602px" class="wp-caption aligncenter"><img class="size-full wp-image-205664" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-7.png" alt="Ảnh:@en.pham18" width="592" height="556" /><p class="wp-caption-text">Ảnh:@en.pham18</p></div> <p style="text-align: justify;">Là hồ bơi vô tận trên tầng thượng hiếm thấy tại Việt Nam, hồ bơi tại A La Carte mang đến cho du khách những trải nghiệm ngoạn mục khi trầm mình vào làn nước mát lạnh, nhìn ngắm quang cảnh tuyệt đẹp của bãi biển và thành phố Đà Nẵng trong khi nhâm nhi những ly cocktail hảo hạng.</p> <div id="attachment_205659" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-205659 size-full" title="Ảnh:@iam_quynhtien" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-3.png" alt="Ảnh:@iam_quynhtien" width="596" height="450" /><p class="wp-caption-text">Ảnh:@iam_quynhtien</p></div> <p style="text-align: justify;">Hồ bơi được lát gạch mosaic với các tông màu xanh dương huyền ảo, dễ chịu. Giữa hồ bơi là một sàn gỗ nổi đa chức năng như một khu vực lounge thư giãn, nơi du khách tắm nắng, thưởng thức spa cùng nhạc nhẹ hoặc dễ dàng biến thành một sàn nhảy sôi động trong những buổi tiệc thú vị được tổ chức thường xuyên.</p> <div id="attachment_205661" style="width: 599px" class="wp-caption aligncenter"><img class="wp-image-205661 size-full" title="Ảnh:@essjay.j" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-5.png" alt="Ảnh:@essjay.j" width="589" height="588" /><p class="wp-caption-text">Ảnh:@essjay.j</p></div> <p style="text-align: justify;">Nếu như hồ bơi tại những khách sạn khác bị giới hạn tầm nhìn thì điểm lợi thế của hồ bơi ở A La Carte là bạn có thể thỏa sức phóng tầm mắt của mình chiêm ngưỡng những bãi biển trong xanh của Đà Nẵng. Đồng thời, đây cũng là địa điểm lý tưởng cho bạn cảm nhận vẻ đẹp thơ mộng của Đà Nẵng lúc bình minh hay hoàng hôn.</p> <div id="attachment_205682" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-205682 size-full" title="Ảnh:@_vuonghanh_" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-16.png" alt="alacarte-da-nang-ivivu-16" width="594" height="446" /><p class="wp-caption-text">Ảnh:@_vuonghanh_</p></div> <div id="attachment_205662" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-205662 size-full" title="Ảnh:@lichae_" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-6.png" alt="Ảnh:@lichae_" width="594" height="576" /><p class="wp-caption-text">Ảnh:@lichae_</p></div> <p style="text-align: justify;">Ở A La Carte, bạn sẽ không phải lo lắng về dịch vụ nghỉ ngơi, ăn uống, thư giãn. Mỗi phòng ngủ ở đây đều mang đến không gian sống riêng biệt, có tầm nhìn hướng ra biển tuyệt đẹp. Một số phòng có cả ban công. Với không gian sống đảm bảo cho mọi du khách có thể làm những gì mình thích, tất cả các phòng đều được trang bị máy lạnh, tủ lạnh, lò vi sóng, bàn ăn, TV màn hình phẳng, ấm đun nước điện và rất nhiều tiện nghi hiện đại khác.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-205670" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-10.jpg" alt="alacarte-da-nang-ivivu-10" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-205673" src="https://cdn3.ivivu.com/2017/05/alacarte-da-nang-ivivu-14.jpg" alt="alacarte-da-nang-ivivu-14" width="1024" height="683" /></p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span></p> <p style="text-align: justify;"><strong>Thông tin thêm:</strong></p> <p>Với gói khuyến mãi <strong>4.599.000 đồng/4 khách</strong>, bạn sẽ được tận hưởng các ưu đãi gồm:</p> <p>– 1 đêm nghỉ dưỡng phòng Delight Plus (2-Bedroom) tại <a href="https://www.ivivu.com/khach-san-da-nang/a-la-carte-da-nang-beach?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><em>khách sạn A La Carte Đà Nẵng Beach</em></a></p> <p style="text-align: justify;">- Ăn sáng buffet dành cho gia đình</p> <p style="text-align: justify;">- Đưa đón sân bay miễn phí</p> <p style="text-align: justify;">- 1 bữa tối set menu Việt Nam tại nhà hàng Fishca</p> <p style="text-align: justify;">- 4 đơn vị nước uống tại Top Bar</p> <p style="text-align: justify;">- 1 lần sử dụng shuttle bus đi Bà Nà/Hội An</p> <p style="text-align: justify;">- Sử dụng các tiện ích: wifi, hồ bơi, phòng gym,…</p> <p style="text-align: justify;"><strong>Điều kiện áp dụng:</strong></p> <p style="text-align: justify;"> – Áp dụng cho đặt phòng tối thiểu 2 đêm trở lên</p> <p style="text-align: justify;">- Thời gian lưu trú từ nay đến hết 30/09 (không áp dụng giai đoạn lễ 01/09 – 03/09)</p> <p><strong>Hướng dẫn cách đặt khuyến mãi này:</strong></p> <p>– Gọi <strong>1900 1870</strong> để được tư vấn</p> <p>– Đặt online <a href="https://www.ivivu.com/khach-san-da-nang/a-la-carte-da-nang-beach?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><strong>TẠI ĐÂY</strong></a></p> <p style="text-align: justify;"></div>', 'Là thành phố du lịch nổi tiếng, vì thế tại Đà Nẵng không thiếu những khách sạn, resort cao cấp dành cho du khách. Thế nhưng khách sạn A La Carte Đà Nẵng Beach lại chưa bao giờ “hạ nhiệt” và đã trở thành một trong những khách sạn “hot” nhất Đà Nẵng mà bạn phải check-in.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-15', '2017-04-08', 'alacarte-da-nang-ivivu-4.png', 0),
(7, '9', '1', 'Cuối tuần đổi gió du lịch Cần Thơ với gói nghỉ dưỡng 3N2Đ tại resort Victoria Cần Thơ bao gồm xe đưa đón từ Sài Gòn và tour tham quan chợ nổi chỉ 2,5 triệu đồng/khách. ', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/3n2d-nghi-duong-resort-phong-cach-phap-4-sao-o-can-tho-xe-dua-don-tu-sai-gon-tour-tham-quan-cho-noi-chi-25-trieu-dong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Nếu bạn đã chán đi biển cũng chán lên núi thì còn chần chừ gì nữa mà không tranh thủ cuối tuần đổi gió du lịch Cần Thơ với gói nghỉ dưỡng 3N2Đ tại <a href="https://www.ivivu.com/khach-san-can-tho/khu-nghi-duong-victoria-can-tho?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">resort Victoria Cần Thơ</a> bao gồm xe đưa đón từ Sài Gòn và tour tham quan chợ nổi chỉ 2,5 triệu đồng/khách. </strong></em></p> <h2><span id="more-202998"></span></h2> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/du-lich-mien-tay/du-lich-can-tho/" target="_blank">Du lịch Cần Thơ</a></div> </strong></p> <h2>3N2Đ nghỉ dưỡng resort phong cách Pháp 4 sao ở Cần Thơ + xe đưa đón từ Sài Gòn + tour tham quan chợ nổi chỉ 2,5 triệu đồng</h2> <p style="text-align: justify;">Ngoài ra, với gói khuyến mãi này du khách cũng được miễn phí ăn sáng buffet, miễn phí 01 phần bánh pizza trên nhà hàng nổi Lady Hậu, miễn phí sử dụng shuttle boat đến Bến Ninh Kiều.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203012" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-10.jpg" alt="Victoria-can-tho-ivivu-10" width="1024" height="683" /></p> <p style="text-align: justify;">Là khu nghỉ dưỡng 4 sao đầu tiên theo tiêu chuẩn quốc tế tại khu vực Đồng bằng sông Cửu Long, <strong>du lịch Cần Thơ</strong> đến đây, du khách cứ ngỡ mình đang lạc bước vào một tòa lâu đài nằm giữa khu vườn ngập tràn cỏ cây hoa lá, trong những trang cổ tích ngày xưa.</p> <p><img class="aligncenter size-full wp-image-203004" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-2.jpg" alt="Victoria-can-tho-ivivu-2" width="1024" height="683" /></p> <p><img class="aligncenter size-full wp-image-203003" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-1.jpg" alt="Victoria-can-tho-ivivu-1" width="888" height="592" /></p> <p style="text-align: justify;">Với 92 phòng nghỉ gồm 28 phòng Superior, 56 phòng Deluxe và 8 phòng Suite. Mỗi phòng đều có phong cách thiết kế tinh tế giao hòa giữa những nét đẹp truyền thống địa phương và tiện nghi đẳng cấp quốc tế, mang nét đẹp riêng của kiến trúc Việt kết hợp với tư duy thẩm mỹ tinh tế của người Pháp.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203005" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-3.jpg" alt="Victoria-can-tho-ivivu-3" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203009" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-7.jpg" alt="Victoria-can-tho-ivivu-7" width="678" height="354" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203008" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-6.jpg" alt="Victoria-can-tho-ivivu-6" width="840" height="460" /></p> <p style="text-align: justify;">Toàn bộ nội thất phòng nghỉ ở đây đều được làm bằng gỗ, mang tông màu ấm và có view hướng vườn yên tĩnh đem đến cảm giác thanh bình nơi thôn quê miền Tây sông nước. Không gian thoáng mát cùng với những làn gió thổi lên từ phía bờ sông thơ mộng sẽ mang đến cho bạn cảm giác dễ chịu, và thoải mái khi lưu trú tại Victoria Cần Thơ.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203006" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-4.jpg" alt="Victoria-can-tho-ivivu-4" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203007" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-5.jpg" alt="Victoria-can-tho-ivivu-5" width="1024" height="683" /></p> <p style="text-align: justify;">Nhà hàng Spices với chỗ ngồi trong nhà và ngoài trời hướng nhìn ra vườn cây xanh mát và sông Hậu thơ mộng. Nhà hàng chuyên phục vụ ẩm thực châu Âu và các món ăn thuần Việt. Với kiến trúc nội thất trang nhã theo phong cách thượng lưu thời Pháp cổ, nhà hàng Spices là một địa điểm lý tưởng cho những bữa tiệc đầm ấm, cổ điển. Ngoài ra, khu nghỉ dưỡng cũng vừa mới khai trương nhà hàng Pizz trên tàu Lady Hậu, giúp du khách có thể vừa thưởng thức ẩm thực, vừa đi dạo trên sông ngắm cảnh.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203010" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-8.jpg" alt="Victoria-can-tho-ivivu-8" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203013" src="https://cdn3.ivivu.com/2017/04/Victoria-can-tho-ivivu-11.jpg" alt="Victoria-can-tho-ivivu-11" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203311" src="https://cdn3.ivivu.com/2017/04/VictCanThoJ-B0722.jpg" alt="VictCanThoJ B0722" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-203309" src="https://cdn3.ivivu.com/2017/04/Victoria-Can-Tho-Resort_Facilities_Spa2.jpg" alt="Victoria Can Tho Resort_Facilities_Spa(2)" width="1024" height="683" /></p> <p style="text-align: justify;">Sau một ngày thấm mệt vì các hoạt động vui chơi, hãy để những chuyên viên Spa dày dặn kinh nghiệm của khu nghỉ dưỡng chăm sóc cho bạn với những phương pháp trị liệu cao cấp, từ các loại nguyên liệu thiên nhiên thuần khiết.</p> <div class="ltt-contentbox white"><span class="icon add"></span></p> <p><strong>Thông tin thêm:</strong></p> <p>Với <strong><a href="https://www.ivivu.com/khach-san-can-tho/khu-nghi-duong-victoria-can-tho?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">gói khuyến mãi 2,5 triệu đồng/khách</a></strong>, bạn sẽ được tận hưởng các ưu đãi gồm:</p> <p>– 3 ngày 2 đêm nghỉ dưỡng tại phòng Superior tại resort Victoria Cần Thơ</p> <p>– Xe đưa đón HCM – Cần Thơ.</p> <p>– Ăn sáng Buffet.</p> <p>– 1 chuyến tham quan chợ nổi Cái Răng bằng Victoria Cruise (theo đoàn)</p> <p>– Miễn phí 01 phần bánh pizza trên nhà hàng nổi Lady Hậu cho mỗi phòng/mỗi giai đoạn ở</p> <p>– Miễn phí sử dụng shuttle boat đến Bến Ninh Kiều.</p> <p>– Miễn phí sử dụng các tiện ích: hồ bơi, phòng tập thể dục</p> <p><em>Lưu ý:</em></p> <p>– Đặt tối thiểu 2 khách/phòng</p> <p>– Giai đoạn lưu trú: Từ nay đến 30/09/2017 (giai đoạn Lễ Tết có phụ thu thêm)</p> <p>– Giá trên áp dụng cho khách khởi hành từ TP.Hồ Chí Minh</p> <p>– Chính sách không hoàn, hủy, thay đổi</p> <p><strong>Hướng dẫn cách đặt khuyến mãi này:</strong></p> <p>– Gọi <strong>1900 1870</strong> để được tư vấn</p> <p>– Đặt online <a href="https://www.ivivu.com/khach-san-can-tho/khu-nghi-duong-victoria-can-tho?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><strong>TẠI ĐÂY</strong></a></p> <p></div> <p style="text-align: right;"><strong>Theo iVIVU.com</strong></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p><strong>Xem thêm các bài viết:</strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/02/du-lich-can-tho-chi-can-nho-9-diem-den-nay-la-du-vui-het-nac/" target="_blank">Du lịch Cần Thơ chỉ cần nhớ 9 điểm đến này là đủ vui hết nấc</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2015/10/du-lich-can-tho-kham-pha-the-gioi-do-an-vat-ben-ben-ninh-kieu/" target="_blank">Du lịch Cần Thơ khám phá thế giới đồ ăn vặt bên bến Ninh Kiều</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2015/06/du-lich-can-tho-cam-nang-tu-a-den-z/" target="_blank">Du lịch Cần Thơ: Cẩm nang từ A đến Z</a></strong></p> </div>', 'Nếu bạn đã chán đi biển cũng chán lên núi thì còn chần chừ gì nữa mà không tranh thủ cuối tuần đổi gió du lịch Cần Thơ với gói nghỉ dưỡng 3N2Đ tại resort Victoria Cần Thơ bao gồm xe đưa đón từ Sài Gòn và tour tham quan chợ nổi chỉ 2,5 triệu đồng/khách. ', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-24', '2017-04-08', 'resort-vinpearl-du-lich-gia-dinh-ivivu-111.jpg', NULL),
(8, '14', '1', 'Combo 3N2Đ ở khách sạn M Social Singapore + vé máy bay chỉ 5.499.000 đồng', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/05/combo-3n2d-o-khach-san-m-social-singapore-ve-may-bay-chi-5-499-000-dong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Chỉ với 5.499.000 đồng/khách bạn sẽ được vi vu Singapore 3N2Đ ở <a href="https://www.ivivu.com/khach-san-singapore-singapore/khach-san-m-social-singapore?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">khách Sạn M Social Singapore</a>, đặc biệt giá này đã bao gồm luôn vé bay khứ hồi và có cơ hội giảm đến 500.000 đồng/khách nếu lưu trú trong tháng 5 này.</strong></em></p> <p><span id="more-205159"></span></p> <div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/dong-nam-a-2/du-lich-singapore/" target="_blank">Du lịch Singapore</a></strong></div> <h2>Combo 3N2Đ ở khách sạn M Social Singapore + vé máy bay chỉ 5.499.000 đồng</h2> <p style="text-align: justify;">Khách sạn M Social Singapore có vị trí nằm ở trung tâm bến cảng Robertson Quay sôi động dọc sông Singapore, lưu trú tại đây bạn sẽ dễ dàng khám phá những khu vực lân cận như khu ăn uống Tiong Bahru Market, trung tâm mua sắm ION Orchard, Universal Studios Singapore hoặc các địa điểm nổi tiếng mà không mất quá nhiều thời gian di chuyển.</p> <p style="text-align: justify;"><img class="size-full wp-image-205190 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-1.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-1" width="800" height="533" /></p> <p style="text-align: justify;"><img class="size-full wp-image-205191 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-2.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-2" width="1024" height="683" /></p> <p style="text-align: justify;">Khách sạn M Social Singapore cung cấp 293 phòng với nét kiến trúc nội thất pha trộn văn hóa cổ điển độc đáo, được thiết kế bởi nhà thiết kế người Pháp lừng danh – Philippe Starck sẽ mang đến cho du khách những góc nhìn đẹp ngỡ ngàng qua từng ngóc ngách của khách sạn.</p> <div id="attachment_205192" style="width: 1090px" class="wp-caption aligncenter"><img class="Ảnh: @msocialsingapore wp-image-205192 size-full" title="Ảnh: @msocialsingapore" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-3.jpg" alt="Ảnh: @msocialsingapore" width="1080" height="1080" /><p class="wp-caption-text">Ảnh: @msocialsingapore</p></div> <p style="text-align: justify;">Mỗi phòng nghỉ tại đây đều được trang bị bằng thảm dày và có TV màn hình phẳng với các kênh truyền hình vệ tinh. Trong phòng cũng có điện thoại thông minh với hướng dẫn du lịch thành phố được tải sẵn, dữ liệu di động không giới hạn và cho phép du khách thực hiện các cuộc gọi điện thoại miễn phí, cả trong nước lẫn quốc tế. Một số phòng có khu vực tiếp khách nhằm mang lại thuận tiện cho du khách. Một số phòng cũng có sân hiên để thư giãn sau một ngày dài.</p> <p style="text-align: justify;"><img class="size-full wp-image-205193 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-4.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-4" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="size-full wp-image-205194 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-5.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-5" width="960" height="726" /></p> <p style="text-align: justify;"><img class="alignnone size-full wp-image-205195" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-6.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-6" width="818" height="534" /></p> <p style="text-align: justify;">Khách sạn còn có hồ bơi ngoài trời và trung tâm thể dục để du khách có những giây phút thư giãn thoải mái nhất. Một trong những tiện ích ở khách sạn M Social Singapore đó là ki-ốt nhận phòng tự phục vụ giúp du khách check-in nhận phòng mà không mất quá nhiều thời gian.</p> <p style="text-align: justify;"><img class="size-full wp-image-205196 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-7.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-7" width="1024" height="683" /></p> <p style="text-align: justify;"><img class="size-full wp-image-205197 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-8.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-8" width="1024" height="560" /></p> <p style="text-align: justify;"><img class="size-full wp-image-205198 aligncenter" src="https://cdn3.ivivu.com/2017/05/combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-9.jpg" alt="combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-9" width="1024" height="683" /></p> <p style="text-align: justify;">Khách sạn M Social Singapore sẽ thỏa mãn các tín đồ mua sắm khi nơi đây chỉ cách trung tâm mua sắm Robertson Walk 800 m và trung tâm Mua sắm UE Square 900 m. Hơn hết, khách sạn còn có shuffte bus sẵn sàng đưa bạn đến các điểm nổi tiếng và trạm MRT hoàn toàn miễn phí luôn nhé. Giờ đây bạn còn chần chờ gì nữa mà không đặt ngay khách sạn M Social Singapore để thỏa sức cho chuyến <strong>du lịch Singapore</strong> sắp tới thôi nào.</p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon add"></span></p> <p style="text-align: justify;"><strong>Thông tin thêm:</strong></p> <p style="text-align: justify;">Với <strong><a href="https://www.ivivu.com/khach-san-singapore-singapore/khach-san-m-social-singapore?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">gói khuyến mãi 5.499.000 đồng/khách này</a></strong>, bạn sẽ tận hưởng các ưu đãi gồm:</p> <p>– Vé máy bay khứ hồi TP. HCM – Singapore (bao gồm 7kg hành lý xách tay).</p> <p>– 02 đêm nghỉ dưỡng ở <a href="https://www.ivivu.com/khach-san-singapore-singapore/khach-san-m-social-singapore?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><strong>khách sạn M Social Singapore</strong></a></p> <p>– Đặt phòng giai đoạn tháng 5, 6 chỉ từ 5.499.000 đồng/khách</p> <p>– Đặt phòng giai đoạn tháng 7, 8 chỉ từ 5.799.000 đồng/khách</p> <p>– Cơ hội giảm đến 500.000 đồng cho khách lưu trú trong tháng 5<strong> </strong>(Số lượng có hạn, chương trình kết thúc khi hết chỗ)</p> <p><strong>– </strong>Ăn sáng</p> <p><strong>–</strong> Miễn phí shuttle bus đi đến các điểm trung tâm.</p> <p><strong>–</strong> Miễn phí các tiện ích: wifi, hồ bơi ngoài trời, phòng tập gym…</p> <p><strong>Lưu ý:</strong></p> <p>– Giá này áp dụng cho khách khởi hành từ TPHCM.</p> <p>– Giá áp dụng khi đặt 2 người/phòng.</p> <p>– Thời gian lưu trú: đến hết 30/08/2017</p> <p><strong>Hướng dẫn cách đặt gói khuyến mãi này:</strong></p> <p>– Gọi <strong>1900 1870</strong> để được tư vấn</p> <p>– Đặt online <strong><a href="https://www.ivivu.com/khach-san-singapore-singapore/khach-san-m-social-singapore?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tại đây</a></strong></p> <p style="text-align: justify;"></div>', 'Chỉ với 5.499.000 đồng/khách bạn sẽ được vi vu Singapore 3N2Đ ở khách Sạn M Social Singapore, đặc biệt giá này đã bao gồm luôn vé bay khứ hồi và có cơ hội giảm đến 500.000 đồng/khách nếu lưu trú trong tháng 5 này.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-11', '2017-04-08', 'combo-3n2d-o-khach-san-M-Social-Singapore-ve-may-bay-chi-5-499-000-dong-ivivu-1.jpg', 0);
INSERT INTO `baiviet` (`IDBV`, `IDKhuVuc`, `IDLoaiTin`, `TieuDe`, `NoiDung`, `TomTat`, `NoiBat`, `NgayViet`, `NgayCapNhat`, `IDNhanVien`, `updated_at`, `created_at`, `anhdaidien`, `luotxem`) VALUES
(9, '2', '2', 'Trải nghiệm ẩm thực Phan Thiết qua bộ ảnh đặc biệt', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Khi nhắc đến <a href="https://www.ivivu.com/blog/category/viet-nam/bai-viet-phan-thiet/" target="_blank">thành phố Phan Thiết</a>, người ta không chỉ nhớ đến cảnh đẹp của vùng biển xanh cát trắng, mà còn nhớ đến một vùng miền với sự đặc sắc và đa dạng về ẩm thực.</em></strong><span id="more-205535"></span></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/bai-viet-phan-thiet/" target="_blank">Du lịch Phan Thiết</a></div></strong></p> <h2>Trải nghiệm ẩm thực Phan Thiết qua bộ ảnh đặc biệt</h2> <p style="text-align: justify;">Anh Hảo Nguyễn là một đầu bếp chuyên nghiệp và có sở thích chụp ảnh những món ăn ngon. Với mục đích nhằm muốn chia sẻ hình ảnh<strong><em> du lịch Phan Thiết</em></strong>, và đặc biệt là ẩm thực của quê hương. Anh đã dùng chiếc điện thoại của mình, cùng con mắt nghệ thuật và sự am hiểu về ẩm thực, để tạo nên những bức ảnh đặc sắc về món ăn Phan Thiết.</p> <div id="attachment_205536" style="width: 1086px" class="wp-caption aligncenter"><img class="wp-image-205536 size-full" title="Món mỳ quảng Phan Thiết nổi tiếng được chụp từ điện thoại." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-1.jpg" alt="Món mỳ quảng Phan Thiết nổi tiếng được chụp từ điện thoại." width="1076" height="720" /><p class="wp-caption-text">Món mì quảng Phan Thiết nổi tiếng được chụp từ điện thoại.</p></div> <p>Những bức ảnh của anh, tuy chỉ được chụp từ chiếc điện thoại, nhưng với sự sắp đặt và chọn bố cục thích hợp, đã khiến người xem cảm thấy thèm thuồng muốn thưởng thức món ăn ngay.</p> <div id="attachment_205537" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205537 size-full" title=" Món phá lấu đặc sản Mũi Né." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-2.jpg" alt=" Món phá lấu đặc sản Mũi Né." width="720" height="960" /><p class="wp-caption-text">Món phá lấu đặc sản Mũi Né.</p></div> <div id="attachment_205538" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205538 size-full" title="Chả cuốn cá trích, đặc sản miền biển." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-3.jpg" alt="Chả cuốn cá trích, đặc sản miền biển." width="720" height="960" /><p class="wp-caption-text">Chả cuốn cá trích, đặc sản miền biển.</p></div> <div id="attachment_205539" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205539 size-full" title="Ẩm thực Phan Thiết không thể không nhắc đến món bún mắm nêm." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-4.jpg" alt="Ẩm thực Phan Thiết không thể không nhắc đến món bún mắm nêm." width="720" height="900" /><p class="wp-caption-text">Ẩm thực Phan Thiết không thể không nhắc đến món bún mắm nêm.</p></div> <div id="attachment_205540" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205540 size-full" title="Bánh hỏi Phú Long, món ngon nhất định phải ăn khi đến Phan Thiết." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-5.jpg" alt="Bánh hỏi Phú Long, món ngon nhất định phải ăn khi đến Phan Thiết." width="720" height="899" /><p class="wp-caption-text">Bánh hỏi Phú Long, món ngon nhất định phải ăn khi đến Phan Thiết.</p></div> <div id="attachment_205541" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205541 size-full" title="Phan Thiết nổi tiếng với món bánh canh giò heo." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-6.jpg" alt="Phan Thiết nổi tiếng với món bánh canh giò heo." width="720" height="899" /><p class="wp-caption-text">Phan Thiết nổi tiếng với món bánh canh giò heo.</p></div> <div id="attachment_205542" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205542 size-full" title="Cá lồi sối mỡ, món đặc biệt của vùng biển Phan Thiết." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-7.jpg" alt="Cá lồi sối mỡ, món đặc biệt của vùng biển Phan Thiết." width="720" height="899" /><p class="wp-caption-text">Cá lồi xối mỡ, món đặc biệt của vùng biển Phan Thiết.</p></div> <div id="attachment_205543" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-205543 size-full" title="Bánh bò Phan Thiết dưới góc nhìn của một nhiếp ảnh đầu bếp." src="https://cdn3.ivivu.com/2017/05/trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-8.jpg" alt="Bánh bò Phan Thiết dưới góc nhìn của một nhiếp ảnh đầu bếp." width="720" height="900" /><p class="wp-caption-text">Bánh bò Phan Thiết.</p></div> <p><strong><em>Những địa chỉ các quán ăn ngon ở Phan Thiết cho bạn tham khảo:</em></strong></p> <p>Bánh hỏi lòng heo: 40 Trần Phú, Lạc Đạo, TP Phan Thiết.</p> <p>Mì quảng: 129 Trần Phú, TP Phan Thiết.</p> <p>Bánh căn: 13 Ngư Ông, TP Phan Thiết.</p> <p>Phá lấu Mũi Né: phá lấu Dì Hồng, ngay vòng xoay Mũi Né.</p> <p>Răng mực: 52 Võ Thị Sáu, TP Phan Thiết.</p> <p>Bánh canh: 13 Tuyên Quang, TP Phan Thiết.</p> <p>Hải sản: bờ kè Phạm Văn Đồng. quán Gia Đình (đường Phạm Ngọc Thạch), biển Đồi Dương.</p> <div class="ltt-contentbox white"><span class="icon star"></span></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN PHAN THIẾT GIÁ TỐT</strong></h4> <p><strong>1. <a href="https://www.ivivu.com/khach-san-phan-thiet/victoria-phan-thiet-beach-resort-spa-12668?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Victoria Phan Thiết Beach Resort & Spa </a></strong></p> <p><strong>2. <a href="https://www.ivivu.com/khach-san-phan-thiet/the-sailing-bay-beach-resort?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">The Sailing Bay Beach Phan Thiết </a><br /> </strong></p> <p><strong>3. <a href="https://www.ivivu.com/khach-san-phan-thiet/anantara-resort-spa-mui-ne?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Anantara Resort & Spa Mũi Né</a> </strong></p> <p></div> <div class="ltt-contentbox white"><span class="icon star"></span></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ TOUR DU LỊCH PHAN THIẾT </strong><strong>GIÁ TỐT</strong></h4> <p><strong>1. <a href="https://www.ivivu.com/du-lich/tour-phan-thiet-2n1d-hai-dang-ke-ga-lau-dai-ruou-vang/310?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tour Phan Thiết 2N1D: Hải đăng Kê Gà – Lâu đài rượu vang</a></strong></p> <p><strong>2. <a href="https://www.ivivu.com/du-lich/tour-phan-thiet-2n1d-phan-thiet-ta-cu/311?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tour Phan Thiết 2N1D: Phan Thiết – Tà Cú</a></strong></p> <p><strong>3. <a href="https://www.ivivu.com/du-lich/tour-phan-thiet-mui-ne-2n1d/364?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tour Phan Thiết – Mũi Né 2N1D</a></strong></p> <p></div> <p style="text-align: right;"><strong>Theo Nguyễn Trường Sơn</strong></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p><strong>Xem thêm bài viết:</strong></p> <p><strong><a href="https://www.ivivu.com/blog/2016/09/phan-thiet-tuoi-tre-di-va-kham-pha/" target="_blank">Phan Thiết – tuổi trẻ đi và khám phá</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2016/08/9-diem-chup-hinh-bao-dep-khong-the-bo-qua-khi-du-lich-phan-thiet/" target="_blank">9 điểm chụp hình ‘bao đẹp’ không thể bỏ qua khi du lịch Phan Thiết</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2016/07/kham-pha-ke-ga-ngon-hai-dang-co-nhat-viet-nam/" target="_blank">Khám phá Kê Gà – Ngọn hải đăng cổ nhất Việt Nam</a></strong></p> <p><strong></div>', 'Khi nhắc đến thành phố Phan Thiết, người ta không chỉ nhớ đến cảnh đẹp của vùng biển xanh cát trắng, mà còn nhớ đến một vùng miền với sự đặc sắc và đa dạng về ẩm thực.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-11', '2017-04-08', 'trai-nghiem-am-thuc-phan-thiet-qua-bo-anh-dac-biet-ivivu-1.jpg', 0),
(10, '7', '3', ' Du lịch An Giang lên Ngũ Hồ Sơn thưởng thức mùi hồng quân', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>“Hàng cây xanh thắm đợi chờ/ Bồ quân chín đỏ giấc mơ cuộc đời”… Ở vùng Bảy Núi, <a href="https://www.ivivu.com/blog/category/viet-nam/du-lich-mien-tay/an-giang/" target="_blank">An Giang</a>, đến mùa hồng quân chín tím cả một khu vườn cũng là thời điểm trẻ con xôn xao cả một góc trời.</em></strong> <span id="more-205610"></span></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/an-giang/" target="_blank">Du lịch An Giang</a></div></strong></p> <h2> Du lịch An Giang lên Ngũ Hồ Sơn thưởng thức mùi hồng quân</h2> <div id="attachment_205570" style="width: 675px" class="wp-caption aligncenter"><img class="wp-image-205570 size-full" title="Trái hồng quân mộng nước, mùi vị thanh tao - Ảnh: Hoài Vũ" src="https://cdn3.ivivu.com/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-1.jpg" alt="Trái hồng quân mộng nước, mùi vị thanh tao - Ảnh: Hoài Vũ" width="665" height="397" /><p class="wp-caption-text">Trái hồng quân mộng nước, mùi vị thanh tao – Ảnh: Hoài Vũ</p></div> <p style="text-align: justify;">Ngũ Hồ Sơn còn có tên là núi Dài Năm Giếng hay núi Dài Nhỏ. Gọi là núi Dài Năm Giếng vì gần trên đỉnh núi có năm giếng nước nhỏ mang nhiều huyền thoại cổ tích từ thuở xa xưa. Ngoài yếu tố nhân văn, Ngũ Hồ Sơn còn có nhiều loại cây trái đặc sản, trong đó nổi tiếng nhất là trái hồng quân.</p> <p style="text-align: justify;">Núi Dài Năm Giếng hiện nay thuộc ấp Phú Hòa, xã An Phú, huyện Tịnh Biên, An Giang. Núi cao khoảng 265m, nằm đối diện núi Ông Két (Anh Vũ Sơn). Dọc theo triền núi có một con đường bêtông dẫn tới Điện Bà, còn gọi là Vồ Đầu, nơi có năm giếng nước.</p> <p style="text-align: justify;">Tại đây, khách<strong> du lịch An Giang</strong> và hành hương thường lui tới để tham quan ngoạn cảnh và thưởng thức trái cây rừng. Dọc hai bên đường lên núi là rừng cây bát ngát xanh um, xen lẫn những khóm hoa rừng rực rỡ quanh năm, ấn tượng nhất là hoa mua và bằng lăng tím.</p> <p style="text-align: justify;">Trong số những loài cây trái sum sê như vú sữa, xoài, mít còn có một loài cây mang cái tên rất đẹp và ăn cũng rất ngon, mùi vị hấp dẫn, đó là trái hồng quân.</p> <p style="text-align: justify;">Đặc điểm của trái hồng quân là trước khi ăn phải vò cho mềm, càng mềm ăn càng ngọt. Nếu trái chưa chín hẳn thịt có vị chát và hơi chua, không ngon bằng trái chín muồi.</p> <div id="attachment_205571" style="width: 675px" class="wp-caption aligncenter"><img class="wp-image-205571 size-full" title="Cây hồng quân ra trái nghịch mùa - Ảnh: Hoài Vũ" src="https://cdn3.ivivu.com/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-2.jpg" alt="Cây hồng quân ra trái nghịch mùa - Ảnh: Hoài Vũ" width="665" height="365" /><p class="wp-caption-text">Cây hồng quân ra trái nghịch mùa – Ảnh: Hoài Vũ</p></div> <div id="attachment_205572" style="width: 647px" class="wp-caption aligncenter"><img class="wp-image-205572 size-full" title=" Hồng quân vào mùa thu hoạch - Ảnh: Hoài Vũ" src="https://cdn3.ivivu.com/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-3.jpg" alt=" Hồng quân vào mùa thu hoạch - Ảnh: Hoài Vũ" width="637" height="1042" /><p class="wp-caption-text">Hồng quân vào mùa thu hoạch – Ảnh: Hoài Vũ</p></div> <p style="text-align: justify;">Ông Nguyễn Văn Bồi, một cư dân sống lâu đời trên rừng núi, cho biết hồng quân Ngũ Hồ Sơn có vị ngọt thanh hơn hồng quân ở núi Cấm nhờ đất cát và thổ nhưỡng ở đây thích hợp với loại cây này.</p> <p style="text-align: justify;">Gia đình ông có 200 cây đang cho trái, hằng năm cây ra hoa từ đầu mùa mưa và trái bắt đầu chín từ tháng 8 kéo dài đến qua tết. Tuy nhiên cũng có vài cây ra bông muộn cho trái nghịch mùa vào tháng 3, tháng 4. Vào thời điểm này trái rất ngon, ngọt và thanh tao.</p> <p style="text-align: justify;">Hồng quân là trái để ăn tươi, làm mứt hoặc làm rượu. Trẻ con thường hái trái xanh hoặc chín hườm chấm muối ớt vừa chát chát, chua chua thật thi vị.</p> <p style="text-align: justify;">Ở vùng Bảy Núi, An Giang, hồng quân là món quà vặt của tuổi thơ. Đến mùa hồng quân chín tím cả một khu vườn cũng là thời điểm trẻ con xôn xao cả một góc trời. Bởi thế, một nhà thơ ở Hội Thi đàn đã viết: “Hàng cây xanh thắm đợi chờ/ Bồ quân chín đỏ giấc mơ cuộc đời”…</p> <div id="attachment_205573" style="width: 675px" class="wp-caption aligncenter"><img class="wp-image-205573 size-full" title="Hồng quân chuẩn bị ngâm ủ rượu - Ảnh: Hoài Vũ" src="https://cdn3.ivivu.com/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-4.jpg" alt="Hồng quân chuẩn bị ngâm ủ rượu - Ảnh: Hoài Vũ" width="665" height="431" /><p class="wp-caption-text">Hồng quân chuẩn bị ngâm ủ rượu – Ảnh: Hoài Vũ</p></div> <div id="attachment_205574" style="width: 675px" class="wp-caption aligncenter"><img class="wp-image-205574 size-full" title=" Trái hồng quân chế biến thành \'Thiên Cấm Sơn kỳ tửu\' - Ảnh: Hoài Vũ" src="https://cdn3.ivivu.com/2017/05/len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-5.jpg" alt=" Trái hồng quân chế biến thành \'Thiên Cấm Sơn kỳ tửu\' - Ảnh: Hoài Vũ" width="665" height="384" /><p class="wp-caption-text">Trái hồng quân chế biến thành ”Thiên Cấm Sơn kỳ tửu” – Ảnh: Hoài Vũ</p></div> <p style="text-align: justify;">Theo y học dân gian, trái hồng quân ăn nhiều tốt cho gan mật, sạch khí huyết, còn thân và rễ hồng quân có thể trị được nhiều chứng bệnh.</p> <p style="text-align: justify;">Ở An Giang cũng có cơ sở chưng cất rượu dùng trái hồng quân chín để ngâm ủ rượu, lấy tên sản phẩm là “Thiên Cấm Sơn kỳ tửu”, khi uống vào cảm giác như dư vị của núi rừng Tây Nam ẩn chứa trong từng ly rượu.</p> <p style="text-align: justify;">Hồng quân còn có tên là bồ quân, mùng quân, tên khoa học là Flacourtia jangomas. Loại này có nhiều ở Đông Nam Á, riêng tại miền Tây Nam Bộ dòng họ nhà hồng quân đã chọn vùng Bảy Núi, An Giang làm quê hương.</p> <p style="text-align: justify;">Đó là loài cây bụi, cao trung bình 5 – 8m, lá nhỏ, bông tím, trái lúc còn sống có màu xanh, khi già chuyển dần sang màu nâu và lúc chín mọng có màu tím rịm trông rất bắt mắt, mùi thơm nhẹ, vị ngọt, hơi chua.</p> <p style="text-align: justify;"><div class="ltt-contentbox white"><span class="icon star"></span></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN AN GIANG GIÁ TỐT</strong></h4> <p><strong>1. <a href="https://www.ivivu.com/khach-san-chau-doc/khach-san-victoria-chau-doc?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Victoria Châu Đốc </a></strong></p> <p><strong>2. <a href="https://www.ivivu.com/khach-san-chau-doc/khach-san-victoria-núi-sam-lodge?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank" rel="nofollow">Khách sạn Victoria Núi Sam Lodge </a></strong></p> <p><strong>3. <a href="https://www.ivivu.com/khach-san-chau-doc/khach-san-phu-thong-chau-doc?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Phú Thông Châu Đốc</a></strong></p> <p></div>', '“Hàng cây xanh thắm đợi chờ/ Bồ quân chín đỏ giấc mơ cuộc đời”… Ở vùng Bảy Núi, An Giang, đến mùa hồng quân chín tím cả một khu vườn cũng là thời điểm trẻ con xôn xao cả một góc trời.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-11', '2017-04-08', 'len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-1.jpg', 0),
(11, '16', '2', ' Cuộc sống Nhật Bản xưa qua những bức ảnh màu', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p><strong><em>Nhiếp ảnh gia người Italy Felice Beato chụp những bức ảnh màu về cuộc sống ở <a href="https://www.ivivu.com/blog/category/the-gioi/du-lich-chau-a/nhat-ban-du-lich-chau-a/" target="_blank">Nhật Bản</a> giai đoạn 1864-1867, cho thấy thế giới bí ẩn của các samurai, geisha, và các tầng lớp xã hội.</em></strong><span id="more-205076"></span></p> <div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/tag/du-lich-nhat-ban/" target="_blank">Du lịch Nhật Bản</a></strong></div> <h2> Cuộc sống Nhật Bản xưa qua những bức ảnh màu</h2> <div id="attachment_205038" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205038 size-full" title="Những người thợ thủ công làm đèn lồng." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-1.jpg" alt="Những người thợ thủ công làm đèn lồng." width="1024" height="640" /><p class="wp-caption-text">Những người thợ thủ công làm đèn lồng.</p></div> <div id="attachment_205039" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205039 size-full" title="Hai người thợ cắt tóc cho khách. Cạo trọc trên đỉnh đầu rồi buộc lại là kiểu tóc đặc trưng của đàn ông Nhật Bản thời kỳ này. Ảnh chụp vào năm 1865." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-2.jpg" alt="Hai người thợ cắt tóc cho khách. Cạo trọc trên đỉnh đầu rồi buộc lại là kiểu tóc đặc trưng của đàn ông Nhật Bản thời kỳ này. Ảnh chụp vào năm 1865." width="1024" height="1136" /><p class="wp-caption-text">Hai người thợ cắt tóc cho khách. Cạo trọc trên đỉnh đầu rồi buộc lại là kiểu tóc đặc trưng của đàn ông Nhật Bản thời kỳ này. Ảnh chụp vào năm 1865.</p></div> <div id="attachment_205040" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205040 size-full" title="Một chiến binh Nhật Bản với cây cung dài và một bó tên." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-3.jpg" alt="Một chiến binh Nhật Bản với cây cung dài và một bó tên." width="1024" height="1301" /><p class="wp-caption-text">Một chiến binh Nhật Bản với cây cung dài và một bó tên.</p></div> <div id="attachment_205041" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205041 size-full" title="Nhiếp ảnh gia Felice Beato sinh năm 1832 ở Venice (Italy). Beato đã đi qua rất nhiều nơi như Hy Lạp, Jerusalem trước khi đưa tin về Chiến tranh Crimea. Năm 1858, ông đến Ấn Độ, Ai Cập và trải qua cuộc Chiến tranh Nha phiến lần thứ hai ở Trung Quốc. Trong ảnh là hình một phụ nữ Nhật Bản trang điểm trước gương, chụp năm 1865.Nhiếp ảnh gia Felice Beato sinh năm 1832 ở Venice (Italy). Beato đã đi qua rất nhiều nơi như Hy Lạp, Jerusalem trước khi đưa tin về Chiến tranh Crimea. Năm 1858, ông đến Ấn Độ, Ai Cập và trải qua cuộc Chiến tranh Nha phiến lần thứ hai ở Trung Quốc. Trong ảnh là hình một phụ nữ Nhật Bản trang điểm trước gương, chụp năm 1865." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-4.jpg" alt="Nhiếp ảnh gia Felice Beato sinh năm 1832 ở Venice (Italy). Beato đã đi qua rất nhiều nơi như Hy Lạp, Jerusalem trước khi đưa tin về Chiến tranh Crimea. Năm 1858, ông đến Ấn Độ, Ai Cập và trải qua cuộc Chiến tranh Nha phiến lần thứ hai ở Trung Quốc. Trong ảnh là hình một phụ nữ Nhật Bản trang điểm trước gương, chụp năm 1865." width="1024" height="1113" /><p class="wp-caption-text">Nhiếp ảnh gia Felice Beato sinh năm 1832 ở Venice (Italy). Beato đã đi qua rất nhiều nơi như Hy Lạp, Jerusalem trước khi đưa tin về Chiến tranh Crimea. Năm 1858, ông đến Ấn Độ, Ai Cập và trải qua cuộc Chiến tranh Nha phiến lần thứ hai ở Trung Quốc. Trong ảnh là hình một phụ nữ Nhật Bản trang điểm trước gương, chụp năm 1865.</p></div> <div id="attachment_205042" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205042 size-full" title="Beato đến Yokohama vào năm 1863. Phần lớn các bức ảnh của ông là chụp phong cảnh. Trong hình là một cửa hàng buôn bán đồ cổ." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-5.jpg" alt="Beato đến Yokohama vào năm 1863. Phần lớn các bức ảnh của ông là chụp phong cảnh. Trong hình là một cửa hàng buôn bán đồ cổ." width="1024" height="903" /><p class="wp-caption-text">Beato đến Yokohama vào năm 1863. Phần lớn các bức ảnh của ông là chụp phong cảnh. Trong hình là một cửa hàng buôn bán đồ cổ.</p></div> <div id="attachment_205043" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205043 size-full" title="Trước Beato, người nước ngoài bị hạn chế đến thăm Nhật Bản. Ông đã đến được những khu vực mà rất ít người phương Tây có thể đặt chân đến. Hình một cô gái Nhật Bản trong bộ trang phục mùa đông." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-6.jpg" alt="Trước Beato, người nước ngoài bị hạn chế đến thăm Nhật Bản. Ông đã đến được những khu vực mà rất ít người phương Tây có thể đặt chân đến. Hình một cô gái Nhật Bản trong bộ trang phục mùa đông." width="1024" height="1313" /><p class="wp-caption-text">Trước Beato, người nước ngoài bị hạn chế đến thăm Nhật Bản. Ông đã đến được những khu vực mà rất ít người phương Tây có thể đặt chân đến. Hình một cô gái Nhật Bản trong bộ trang phục mùa đông.</p></div> <div id="attachment_205044" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205044 size-full" title="Beato cũng từng kinh doanh thảm, túi xách của phụ nữ cùng rất nhiều thứ khác tại Nhật Bản cho đến năm 1884." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-7.jpg" alt="Beato cũng từng kinh doanh thảm, túi xách của phụ nữ cùng rất nhiều thứ khác tại Nhật Bản cho đến năm 1884." width="1024" height="823" /><p class="wp-caption-text">Beato cũng từng kinh doanh thảm, túi xách của phụ nữ cùng rất nhiều thứ khác tại Nhật Bản cho đến năm 1884.</p></div> <div id="attachment_205045" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205045 size-full" title="Sau đó, Beato sống ở Miến Điện (nay là Myanmar) và được cho là đã chết ở đó vào năm 1905 hoặc 1906. Nhưng một tờ giấy chứng tử được phát hiện năm 2009 cho thấy Beato qua đời tại Florence (Italy) vào năm 1909." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-8.jpg" alt="Sau đó, Beato sống ở Miến Điện (nay là Myanmar) và được cho là đã chết ở đó vào năm 1905 hoặc 1906. Nhưng một tờ giấy chứng tử được phát hiện năm 2009 cho thấy Beato qua đời tại Florence (Italy) vào năm 1909." width="1024" height="1307" /><p class="wp-caption-text">Sau đó, Beato sống ở Miến Điện (nay là Myanmar) và được cho là đã chết ở đó vào năm 1905 hoặc 1906. Nhưng một tờ giấy chứng tử được phát hiện năm 2009 cho thấy Beato qua đời tại Florence (Italy) vào năm 1909.</p></div> <div id="attachment_205046" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205046 size-full" title="Một người thợ tẩm quất làm việc tại cảng Tokyo." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-9.jpg" alt="Một người thợ tẩm quất làm việc tại cảng Tokyo." width="1024" height="730" /><p class="wp-caption-text">Một người thợ tẩm quất làm việc tại cảng Tokyo.</p></div> <div id="attachment_205047" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205047 size-full" title="Một nhóm phu khiêng kago, một loại kiệu nhỏ của Nhật Bản. Ảnh chụp năm 1865." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-10.jpg" alt="Một nhóm phu khiêng kago, một loại kiệu nhỏ của Nhật Bản. Ảnh chụp năm 1865." width="1024" height="747" /><p class="wp-caption-text">Một nhóm phu khiêng kago, một loại kiệu nhỏ của Nhật Bản. Ảnh chụp năm 1865.</p></div> <div id="attachment_205048" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205048 size-full" title="Thầy thuốc xem bệnh cho khách nữ. Thầy thuốc ở Nhật Bản thời kỳ này có quyền mang hai thanh kiếm và không cần bằng cấp để hành nghề." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-11.jpg" alt="Thầy thuốc xem bệnh cho khách nữ. Thầy thuốc ở Nhật Bản thời kỳ này có quyền mang hai thanh kiếm và không cần bằng cấp để hành nghề." width="1024" height="754" /><p class="wp-caption-text">Thầy thuốc xem bệnh cho khách nữ. Thầy thuốc ở Nhật Bản thời kỳ này có quyền mang hai thanh kiếm và không cần bằng cấp để hành nghề.</p></div> <div id="attachment_205049" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205049 size-full" title="Hình ảnh uy nghiêm của một vị quan (yakunin)." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-12.jpg" alt="Hình ảnh uy nghiêm của một vị quan (yakunin)." width="1024" height="1232" /><p class="wp-caption-text">Hình ảnh uy nghiêm của một vị quan (yakunin).</p></div> <div id="attachment_205050" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205050 size-full" title="Một gian hàng giải khát với một bếp than nhỏ để pha trà và một ít đồ ăn." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-13.jpg" alt="Một gian hàng giải khát với một bếp than nhỏ để pha trà và một ít đồ ăn." width="1024" height="795" /><p class="wp-caption-text">Một gian hàng giải khát với một bếp than nhỏ để pha trà và một ít đồ ăn.</p></div> <div id="attachment_205051" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205051 size-full" title="Một phụ nữ ngồi trong kago và được samurai mang hai thanh kiếm tháp tùng. Ảnh chụp năm 1865." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-14.jpg" alt="Một phụ nữ ngồi trong kago và được samurai mang hai thanh kiếm tháp tùng. Ảnh chụp năm 1865." width="1024" height="746" /><p class="wp-caption-text">Một phụ nữ ngồi trong kago và được samurai mang hai thanh kiếm tháp tùng. Ảnh chụp năm 1865.</p></div> <div id="attachment_205052" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205052 size-full" title="Một nữ tu sĩ kiếm sống bằng cách khất thực." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-15.jpg" alt="Một nữ tu sĩ kiếm sống bằng cách khất thực." width="1024" height="1389" /><p class="wp-caption-text">Một nữ tu sĩ kiếm sống bằng cách khất thực.</p></div> <div id="attachment_205053" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205053 size-full" title=" Nhóm tướng lĩnh phục vụ cho một vị hoàng tử ở phía bắc Nhật Bản." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-16.jpg" alt=" Nhóm tướng lĩnh phục vụ cho một vị hoàng tử ở phía bắc Nhật Bản." width="1024" height="809" /><p class="wp-caption-text">Nhóm tướng lĩnh phục vụ cho một vị hoàng tử ở phía bắc Nhật Bản.</p></div> <div id="attachment_205054" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205054 size-full" title="Vị quan trên lưng ngựa, ảnh chụp năm 1865." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-17.jpg" alt="Vị quan trên lưng ngựa, ảnh chụp năm 1865." width="1024" height="1286" /><p class="wp-caption-text">Vị quan trên lưng ngựa, ảnh chụp năm 1865.</p></div> <div id="attachment_205055" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205055 size-full" title="Chính sách cô lập của Nhật Bản, gọi là Kaikin, cấm tiếp xúc với các nước bên ngoài, được thực thi từ năm 1641-1853 bởi chế độ Mạc phủ Tokugawa, nhưng vẫn có mối quan hệ thương mại và ngoại giao, tuy rất hạn chế với Trung Quốc, Triều Tiên, quần đảo Ryukyu (nay thuộc tỉnh Okinawa và Kagoshima) và Hà Lan." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-18.jpg" alt="Chính sách cô lập của Nhật Bản, gọi là Kaikin, cấm tiếp xúc với các nước bên ngoài, được thực thi từ năm 1641-1853 bởi chế độ Mạc phủ Tokugawa, nhưng vẫn có mối quan hệ thương mại và ngoại giao, tuy rất hạn chế với Trung Quốc, Triều Tiên, quần đảo Ryukyu (nay thuộc tỉnh Okinawa và Kagoshima) và Hà Lan." width="1024" height="1328" /><p class="wp-caption-text">Chính sách cô lập của Nhật Bản, gọi là Kaikin, cấm tiếp xúc với các nước bên ngoài, được thực thi từ năm 1641-1853 bởi chế độ Mạc phủ Tokugawa, nhưng vẫn có mối quan hệ thương mại và ngoại giao, tuy rất hạn chế với Trung Quốc, Triều Tiên, quần đảo Ryukyu (nay thuộc tỉnh Okinawa và Kagoshima) và Hà Lan.</p></div> <div id="attachment_205056" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205056 size-full" title="Một ngư dân với gánh cá hồi khô và cá ngừ." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-19.jpg" alt="Một ngư dân với gánh cá hồi khô và cá ngừ." width="1024" height="1142" /><p class="wp-caption-text">Một ngư dân với gánh cá hồi khô và cá ngừ.</p></div> <div id="attachment_205057" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205057 size-full" title=" Một nhóm phụ nữ chơi các nhạc cụ truyền thống của Nhật Bản: Samisen, Tsudzumi và Taiko. Trong giai đoạn cô lập, văn hoá Nhật Bản phát triển mà không chịu sự tác động từ thế giới bên ngoài." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-20.jpg" alt=" Một nhóm phụ nữ chơi các nhạc cụ truyền thống của Nhật Bản: Samisen, Tsudzumi và Taiko. Trong giai đoạn cô lập, văn hoá Nhật Bản phát triển mà không chịu sự tác động từ thế giới bên ngoài." width="1024" height="824" /><p class="wp-caption-text">Một nhóm phụ nữ chơi các nhạc cụ truyền thống của Nhật Bản: Samisen, Tsudzumi và Taiko. Trong giai đoạn cô lập, văn hoá Nhật Bản phát triển mà không chịu sự tác động từ thế giới bên ngoài.</p></div> <div id="attachment_205058" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205058 size-full" title="Người dân uống bia làm từ gạo nếp của một người bán rong." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-21.jpg" alt="Người dân uống bia làm từ gạo nếp của một người bán rong." width="1024" height="1370" /><p class="wp-caption-text">Người dân uống bia làm từ gạo nếp của một người bán rong.</p></div> <div id="attachment_205059" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205059 size-full" title="Người hành hương lên núi Phú Sĩ trên đảo Honshu của Nhật Bản." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-22.jpg" alt="Người hành hương lên núi Phú Sĩ trên đảo Honshu của Nhật Bản." width="1024" height="1283" /><p class="wp-caption-text">Người hành hương lên núi Phú Sĩ trên đảo Honshu của Nhật Bản.</p></div> <div id="attachment_205060" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205060 size-full" title="Các cô gái Nhật thuộc tầng lớp trung lưu." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-23.jpg" alt="Các cô gái Nhật thuộc tầng lớp trung lưu." width="1024" height="1254" /><p class="wp-caption-text">Các cô gái Nhật thuộc tầng lớp trung lưu.</p></div> <div id="attachment_205061" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-205061 size-full" title="Một phụ nữ Nhật đang mặc kimono, quấn quanh eo bằng thắt lưng obi dài." src="https://cdn3.ivivu.com/2017/05/cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-24.jpg" alt="Một phụ nữ Nhật đang mặc kimono, quấn quanh eo bằng thắt lưng obi dài." width="1024" height="1384" /><p class="wp-caption-text">Một phụ nữ Nhật đang mặc kimono, quấn quanh eo bằng thắt lưng obi dài.</p></div> <p><strong><div class="ltt-contentbox white"><span class="icon star"></span></strong></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN TOKYO GIÁ TỐT</strong></h4> <p><strong>1. <a href="https://www.ivivu.com/khach-san-tokyo/smile-asakusa?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Smile Hotel Asakusa </a></strong></p> <p><strong>2. <a href="https://www.ivivu.com/khach-san-tokyo/weekly-mansion-tokyo-at-asakusa?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Hotel MyStays Asakusa </a></strong></p> <p><strong>3.<a href="https://www.ivivu.com/khach-san-tokyo/new-star-ikebukuro?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"> Hotel New Star Ikebukuro </a></strong></p> <p><strong></div>', 'Nhiếp ảnh gia người Italy Felice Beato chụp những bức ảnh màu về cuộc sống ở Nhật Bản giai đoạn 1864-1867, cho thấy thế giới bí ẩn của các samurai, geisha, và các tầng lớp xã hội.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-11', '2017-04-08', 'cuoc-song-nhat-ban-xua-qua-nhung-buc-anh-mau-ivivu-1.jpg', 0),
(12, '12', '2', 'Du lịch Thụy Sĩ tham quan Solvay Hut, ngôi nhà cô đơn nhất thế giới', 'Mỗi năm, hàng nghìn du khách đổ về ngọn núi ở Thụy Sĩ và leo lên độ cao hơn 4.000 m để được chạm vào Solvay Hut – kiệt tác kiến trúc mà khi đứng dưới, bạn chỉ nhìn thấy như một chấm nhỏ mờ ảo.  Du lịch Thụy Sĩ tham quan Solvay Hut, ngôi nhà cô đơn nhất thế giới Solvay hut là ngôi nhà nhỏ bằng gỗ, nằm trên độ cao hơn 4.000 m của dãy Matterhorn, gần Zermatt, bang Valais. Nó luôn được nhiều du khách du lịch Thụy Sĩ nhớ đến với biệt hiệu “chòi gỗ cô đơn và cao nhất thế giới”. Chủ sở hữu của Solvay là Alpine Alpine Club Thụy Sĩ – câu lạc bộ leo núi lớn nhất thế giới. Ngôi nhà được xây dựng vào năm 1915, và hơn 100 năm qua nó luôn là chỗ nghỉ chân và cung cấp đồ ăn cho những người leo núi. Ngôi nhà được đặt theo tên của Ernest Solvay, một nhà leo núi, thám hiểm và doanh nhân người Bỉ. Solvay cũng chính là người đã quyên góp tiền để xây công trình này. Solvay xây ngôi nhà nhằm thể hiện sự tri ân của mình đối với ngọn núi, vì trong năm tháng tuổi trẻ ông đã có những khoảnh khắc đáng nhớ khi leo núi và ngắm nhìn quang cảnh ở đây. Solvay cũng nhận ra sự nguy hiểm đến tính mạng với những nhà leo núi khi gặp thời tiết xấu. Do đó, khi về hưu ông đã quyên tiền để tài trợ xây dựng một căn nhà trên núi, nhằm hỗ trợ những du khách leo núi có chỗ nghỉ ngơi an toàn. Trong căn nhà có chỗ cho 10 người, luôn có đầy đủ đồ ăn, nước uống. Ngôi nhà được xây dựng chỉ trong 5 ngày. Năm 1966, nó được sửa chữa lại và năm 1976, người ta đã lắp thêm điện thoại để du khách mắc kẹt có thể liên lạc gọi cứu hộ. Ngày nay, không chỉ những người gặp điều kiện thời tiết xấu mới tá túc trong ngôi nhà này, mà Solvay đã trở thành một trong những điểm đến nổi tiếng nhất Thụy Sĩ. Nhiều người đến đây leo núi chỉ với mục đích tham quan căn chòi gỗ và nhìn toàn cảnh ngọn núi Monte Rosa phía trước mặt.  BẠN CẦN MỘT NƠI LƯU TRÚ VỚI GIÁ HỢP TÚI TIỀN TRONG HÀNH TRÌNH DU LỊCH, ĐỪNG NGẦN NGẠI GỌI NGAY TỚI SỐ 1900 1870 ĐỂ ĐƯỢC TƯ VẤN NHÉ Theo Anh Minh/Vnexpress  </p> Xem thêm các bài viết: Bộ ảnh du lịch sáng tạo của cặp đôi yêu xa Nơi chỉ cần hít thở cũng ‘giàu’ nhờ 72.000 tấn bụi kim cương Cô gái chạy xe phân khối lớn chu du thế giới   Click đặt ngay Khách sạn khắp Việt Nam và toàn thế giới giá tốt nhất chỉ có tại iVIVU.com *** Tham khảo: Cẩm nang du lịch iVIVU.com 			iVIVU.com    			May 5, 2017     			  				Đánh giá bài viết này  				 (1 votes, average: 5.00 out of 5)\r\n  			Loading...', 'Mỗi năm, hàng nghìn du khách đổ về ngọn núi ở Thụy Sĩ và leo lên độ cao hơn 4.000 m để được chạm vào Solvay Hut – kiệt tác kiến trúc mà khi đứng dưới, bạn chỉ nhìn thấy như một chấm nhỏ mờ ảo.', 0x30, '2017-04-08', '2017-04-08', '3', '2017-05-11', '2017-04-08', 'ngoi-nha-co-don-nhat-the-gioi-ivivu-1.jpg', 0);
INSERT INTO `baiviet` (`IDBV`, `IDKhuVuc`, `IDLoaiTin`, `TieuDe`, `NoiDung`, `TomTat`, `NoiBat`, `NgayViet`, `NgayCapNhat`, `IDNhanVien`, `updated_at`, `created_at`, `anhdaidien`, `luotxem`) VALUES
(13, '9', '1', 'Du lịch Maldives ở resort sang chảnh bao luôn ăn uống và vé máy bay chỉ 29.999.000 đồng', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/cham-ngo-thien-duong-maldives-o-resort-sang-chanh-bao-luon-an-uong-va-ve-may-bay-chi-29-999-000-dong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Không phải chỉ khi có rủng rỉnh túi tiền với ngân quỹ gần cả trăm triệu, bạn mới có thể tính đến một kỳ nghỉ tại quần đảo thiên đường Maldives. Bởi chỉ với 29.999.000 đồng/khách, bạn sẽ có ngay chuyến du lịch hè 2017 đến Maldives 4N3Đ tại <a href="https://www.ivivu.com/khach-san-maldives-maldives/khu-nghi-duong-club-med-kani?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">resort Club Med Kan</a>i siêu sang, bao luôn ăn uống, vé máy bay cùng nhiều dịch vụ thú vị miễn phí kèm theo.</strong></em></p> <p style="text-align: justify;"><span id="more-202411"></span></p> <p style="text-align: justify;"><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/category/the-gioi/du-lich-chau-a/du-lich-maldives/" target="_blank">Du lịch Maldives</a></div> </strong></p> <h2 style="text-align: left;">Du lịch Maldives ở resort sang chảnh bao luôn ăn uống và vé máy bay chỉ 29.999.000 đồng</h2> <p style="text-align: justify;">Từ trước đến nay, trong suy nghĩ của nhiều người, <strong><em>du lịch Maldives</em></strong> hẳn vẫn là cái gì đó… xa xỉ kinh khủng lắm. Nói đến Maldives là nói đến resort sang chảnh, nói đến những bãi biển riêng, những hòn đảo tư nhân hoành tráng. Một kỳ nghỉ ở Maldives, nhiều người chưa cần tính cũng đã ước lượng phải chi xấp xỉ bằng một chuyến đi châu Âu dài ngày. Cứ như thế, trong đầu người ta mặc định rằng, muốn đi <em>du lịch Maldives</em>? Phải chuẩn bị rất rất rất nhiều tiền.</p> <p style="text-align: justify;">Tuy nhiên, chỉ với 29.999.000 đồng/khách bạn có thể đặt ngay gói nghỉ dưỡng khuyến mãi đến Maldives của iVIVU.com cho kỳ nghỉ hè 2017.</p> <div id="attachment_202429" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-202429 size-full" title="Ảnh:@alphaboy07" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-8.png" alt="Ảnh:@alphaboy07" width="596" height="441" /><p class="wp-caption-text">Ảnh:@alphaboy07</p></div> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-202427" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-6-800x450.png" alt="khu-nghi-duong-club-med-kani-6-800x450" width="800" height="450" /></p> <p style="text-align: justify;">Được đánh giá là một những khu nghỉ mát sang trọng nhất ở Maldives, Club Med Kani là một resort xinh đẹp nằm trên hòn đảo Kani, với dòng nước trong vắt và những dải cát trắng mịn.</p> <p style="text-align: justify;">Do vị trí địa lý thuận lợi, nên từ Club Med Kani du khách có thể dễ dàng di chuyển đến các địa điểm du lịch thu hút hàng đầu của Maldives như: Vùng lặn Banana Reef, bến phà Hulhumale, bến phà Viligili…</p> <div id="attachment_202435" style="width: 603px" class="wp-caption aligncenter"><img class="wp-image-202435 size-full" title="Ảnh:@jasnakacarevic" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-16.png" alt="Ảnh:@jasnakacarevic" width="593" height="539" /><p class="wp-caption-text">Ảnh:@jasnakacarevic</p></div> <div id="attachment_202452" style="width: 607px" class="wp-caption aligncenter"><img class="wp-image-202452 size-full" title="Ảnh:@thuraynkyawmoe" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-30.png" alt="Ảnh:@thuraynkyawmoe" width="597" height="417" /><p class="wp-caption-text">Ảnh:@thuraynkyawmoe</p></div> <div id="attachment_202459" style="width: 545px" class="wp-caption aligncenter"><img class="wp-image-202459 size-full" title="Ảnh:@kuo_yi_ching" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-33.png" alt="Ảnh:@kuo_yi_ching" width="535" height="586" /><p class="wp-caption-text">Ảnh:@kuo_yi_ching</p></div> <p style="text-align: justify;">Bước vào Club Med Kani du khách sẽ choáng ngợp với vẻ đẹp tinh tế và sang trọng của khu nghỉ dưỡng bởi thiết kế tinh tế, hài hòa với thiên nhiên và cảnh đẹp vốn có. Phòng nghỉ của Club Med Kani là một dãy bungalow riêng biệt, tiện nghi hoàn hảo trong mọi chi tiết từ các vật dụng trong bồn tắm, cho đến giường ngủ và đồ đạc trong phòng.</p> <div id="attachment_202430" style="width: 603px" class="wp-caption aligncenter"><img class="wp-image-202430 size-full" title="Ảnh:@ivycassandra8477" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-9.png" alt="Ảnh:@ivycassandra8477" width="593" height="446" /><p class="wp-caption-text">Ảnh:@ivycassandra8477</p></div> <div id="attachment_202433" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-202433 size-full" title="Ảnh:@iamjezz_kritta" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-13.png" alt="Ảnh:@iamjezz_kritta" width="594" height="580" /><p class="wp-caption-text">Ảnh:@iamjezz_kritta</p></div> <div id="attachment_202440" style="width: 480px" class="wp-caption aligncenter"><img class="wp-image-202440 size-full" title="Ảnh:@ayladimitri" src="https://cdn3.ivivu.com/2017/04/maldives8.png" alt="Ảnh:@ayladimitri" width="470" height="586" /><p class="wp-caption-text">Ảnh:@ayladimitri</p></div> <p style="text-align: justify;">Các phương tiện kỹ thuật số hiện đại đều hạn chế trong mỗi phòng nghỉ, để đem tới sự thư giãn hoàn toàn cho khách. Chỉ có tiếng sóng vỗ rì rào cùng tiếng gió cọ qua mái lá trên đầu. Mỗi phòng có một ban công nhìn thẳng ra biển xanh, một bộ bàn ghế xinh xắn với hoa tươi chào mời và một chiếc chuông gió đung đưa không ngừng.</p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-202455" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-1.png" alt="khu-nghi-duong-club-med-kani-ivivu-1" width="800" height="450" /></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-202456" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-7.png" alt="khu-nghi-duong-club-med-kani-ivivu-7" width="800" height="450" /></p> <div id="attachment_202436" style="width: 603px" class="wp-caption aligncenter"><img class="wp-image-202436 size-full" title="Ảnh:@naruemon.st" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-23.png" alt="Ảnh:@naruemon.st" width="593" height="485" /><p class="wp-caption-text">Ảnh:@naruemon.st</p></div> <p style="text-align: justify;">Resort cũng cung cấp rất nhiều phương tiện giải trí độc đáo như bể bơi ngoài trời, các môn thể thao dưới nước (có động cơ), spa, phòng thể dục. Cơ sở vật chất tốt và vị trí hoàn hảo làm cho Club Med Kani trở thành nơi tuyệt vời để bạn tận hưởng kì nghỉ khi đến <strong>du lịch Maldives.</strong></p> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-202434" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-15.png" alt="khu-nghi-duong-club-med-kani-ivivu-15" width="800" height="450" /></p> <div id="attachment_202432" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-202432 size-full" title="Ảnh:@narish_zaza" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-12.png" alt="Ảnh:@narish_zaza" width="596" height="396" /><p class="wp-caption-text">Ảnh:@narish_zaza</p></div> <div id="attachment_202441" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-202441 size-full" title="Ảnh:@froschgce" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-24.png" alt="Ảnh:@froschgce" width="594" height="397" /><p class="wp-caption-text">Ảnh:@froschgce</p></div> <p style="text-align: justify;">Resort này rất thú vị bởi thay vì chỉ có nghỉ dưỡng ăn uống hay tắm mát tại các bãi biển đẹp nhất thì bạn và người thân còn được sum vầy với nhau, tham gia các hoạt động vui chơi ngoài trời như: lặn ngắm san hô, chèo thuyền Kayak, tập Yoga, lướt sóng bằng cano kéo… đặc biệt khu nghỉ dưỡng cũng có show trình diễn nghệ thuật miễn phí vào buổi tối – nơi giúp bạn hiểu hơn về văn hóa và con người quốc đảo Maldives.</p> <p style="text-align: justify;"><img class="aligncenter wp-image-202451 size-large" src="https://cdn3.ivivu.com/2017/04/maldives9-1024x679.jpg" alt="maldives9" width="1024" height="679" /></p> <div id="attachment_202444" style="width: 608px" class="wp-caption aligncenter"><img class="wp-image-202444 size-full" title="Ảnh:@kaew_putt" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-27.png" alt="Ảnh:@kaew_putt" width="598" height="395" /><p class="wp-caption-text">Ảnh:@kaew_putt</p></div> <div id="attachment_202446" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-202446 size-full" title="Ảnh:@kaew_putt" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-28.png" alt="Ảnh:@kaew_putt" width="596" height="398" /><p class="wp-caption-text">Ảnh:@kaew_putt</p></div> <p style="text-align: justify;"><img class="aligncenter size-full wp-image-202437" src="https://cdn3.ivivu.com/2017/04/maldives7.jpg" alt="maldives7" width="606" height="404" /></p> <p style="text-align: justify;">Một điều đặc biệt nữa khiến cho resort thu hút được nhiều khách chính là sự phát triển cũng như đổi mới liên tục của các trò chơi trong từng ngày sẽ được thiết kế riêng biệt để không trò nào sẽ bị lặp lại vào ngày hôm sau. Tới đây, bạn sẽ không thể cưỡng lại khỏi cám dỗ từ việc cởi giày và bước chân trần trên bờ cát mịn, hoà mình vào làn nước và tham gia các hoạt động lặn biển, ở một nơi được đánh giá là một trong những điểm lặn biển đẹp nhất ở Maldives.</p> <div id="attachment_202442" style="width: 605px" class="wp-caption aligncenter"><img class="size-full wp-image-202442" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-25.png" alt="Ảnh:@sharon_n.y" width="595" height="454" /><p class="wp-caption-text">Ảnh:@sharon_n.y</p></div> <div id="attachment_202443" style="width: 602px" class="wp-caption aligncenter"><img class="wp-image-202443 size-full" title="Ảnh:@averco.j" src="https://cdn3.ivivu.com/2017/04/khu-nghi-duong-club-med-kani-ivivu-26.png" alt="Ảnh:@averco.j" width="592" height="586" /><p class="wp-caption-text">Ảnh:@averco.j</p></div> <div class="ltt-contentbox white"><span class="icon add"></span></p> <p><strong>Thông tin thêm:</strong></p> <p>Với gói khuyến mãi 29.999.000 đồng /khách, bạn sẽ được tận hưởng các ưu đãi gồm:</p> <p>– 02 đêm nghỉ dưỡng tại phòng Garden Club Room của Club Med Kani Resort 4 sao</p> <p>– 01 đêm nghỉ tại khách sạn thủ đô Male</p> <p>– Vé máy bay khứ hồi đến Maldives</p> <p>– Ăn 3 bữa mỗi ngày và thức uống miễn phí cả ngày tại khu nghỉ dưỡng Club Med Kani</p> <p>– Miễn phí các hoạt động thể thao tại Club Med Kani</p> <p>– Miễn phí đưa đón từ sân bay đến đảo Kani</p> <p><em><strong>Lưu ý:</strong></em></p> <p>– Đặt tối thiểu 2 khách/phòng</p> <p>– Chính sách không hoàn, hủy, thay đổi</p> <p>– Thời gian lưu trú: từ nay đến hết ngày 29/09/2017</p> <p><strong>Hướng dẫn cách đặt khuyến mãi này:</strong></p> <p>– Gọi <strong>1900 1870</strong> để được tư vấn</p> <p>– Đặt online <a href="https://www.ivivu.com/khach-san-maldives-maldives/khu-nghi-duong-club-med-kani?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><strong>TẠI ĐÂY</strong></a></p> <p></div> <p style="text-align: right;"><strong>Theo iVIVU.com</strong></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p><strong>Xem thêm các bài viết khác:</strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/04/tu-van-kinh-nghiem-du-lich-%EF%B8%8Fmaldives-7-ngay-6-dem-cuc-chi-tiet/" target="_blank">Tư vấn kinh nghiệm du lịch ️Maldives 7 ngày 6 đêm cực chi tiết</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/02/du-lich-maldives-o-khach-san-4-ghe-tham-thien-duong-noi-ha-gioi-voi-gia-chua-toi-1-000-usd/" target="_blank">Du lịch Maldives, ở khách sạn 4* ghé thăm thiên đường nơi hạ giới với giá chưa tới 1.000 USD</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/04/bi-kip-de-co-chuyen-du-lich-sieu-re-o-maldives-cua-khach-viet/" target="_blank">Bí kíp để có chuyến du lịch siêu rẻ ở Maldives của khách Việt</a></strong></p> <p><strong></div>', 'Không phải chỉ khi có rủng rỉnh túi tiền với ngân quỹ gần cả trăm triệu, bạn mới có thể tính đến một kỳ nghỉ tại quần đảo thiên đường Maldives. Bởi chỉ với 29.999.000 đồng/khách, bạn sẽ có ngay chuyến du lịch hè 2017 đến Maldives 4N3Đ tại resort Club Med Kani siêu sang, bao luôn ăn uống, vé máy bay cùng nhiều dịch vụ thú vị miễn phí kèm theo.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'victoria-chau-doc-ivivu-10.jpg', NULL),
(14, '8', '1', 'Hè này vi vu xứ Đài với tour Đài Loan 5N5Đ chỉ 9.990.000 đồng', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/he-nay-vi-vu-xu-dai-voi-tour-dai-loan-5n5d-chi-9-990-000-dong/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Tour 5 ngày chỉ với giá 9.999.000 đồng, đi Đài Loan trong hè này khám phá các điểm đến cực kỳ hấp dẫn như: <a href="https://www.ivivu.com/du-lich/tour-dai-loan-5n5d-dai-bac-dai-trung-gia-nghia-a-ly-son/682?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Đài Bắc – Đài Trung– Gia Nghĩa – A Lý Sơn</a>. Gọi ngay đến số hotline  (08) 3933 8002 để được tư vấn kỹ hơn nhé!</strong></em></p> <p style="text-align: justify;"><span id="more-202219"></span></p> <p style="text-align: justify;"><strong><div class="ltt-contentbox white"><span class="icon add"></span>Xem thêm: <a href="https://www.ivivu.com/blog/tag/du-lich-dai-loan/" target="_blank">Du lịch Đài Loan</a></div> </strong></p> <h2 style="text-align: justify;">Hè này vi vu xứ Đài với tour Đài Loan 5N5Đ chỉ 9.990.000 đồng</h2> <p style="text-align: justify;">Du lịch Đài Loan luôn là điểm đến trong mơ của nhiều du khách. Không chỉ có những thành phố hiện đại, nhiều điểm mua sắm sầm uất Đài Loan còn níu chân du khách bởi nền văn hóa độc đáo và phong cảnh tuyệt đẹp.</p> <p style="text-align: justify;">Với gói tour trọn gói hấp dẫn này, du khách sẽ được ghé thăm các các điểm đến nổi tiếng nhất của Đài Loan như:</p> <p><strong>Ximending</strong></p> <p style="text-align: justify;">Ximending, nằm ở phía Tây thành phố Đài Bắc, là một trong những địa điểm du lịch phổ biến nhất. Nó nổi tiếng vì sự đa dạng các hoạt động giải trí, vui chơi dành cho giới trẻ.</p> <div id="attachment_202239" style="width: 597px" class="wp-caption aligncenter"><img class="wp-image-202239 size-full" title="Ảnh:@22ye_s" src="https://cdn3.ivivu.com/2017/04/ximending-ivivu-1.png" alt="Ảnh:@22ye_s" width="587" height="582" /><p class="wp-caption-text">Ảnh:@22ye_s</p></div> <div id="attachment_202240" style="width: 600px" class="wp-caption aligncenter"><img class="wp-image-202240 size-full" title="Ảnh:@cathsobrevega" src="https://cdn3.ivivu.com/2017/04/Ximending-ivivu-2.png" alt="Ảnh:@cathsobrevega" width="590" height="563" /><p class="wp-caption-text">Ảnh:@cathsobrevega</p></div> <div id="attachment_202275" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-202275 size-full" title="Ảnh: @janee._ell" src="https://cdn3.ivivu.com/2017/04/ximending-ivivu-11.png" alt="Ảnh: @janee._ell" width="594" height="516" /><p class="wp-caption-text">Ảnh: @janee._ell</p></div> <p style="text-align: justify;">Lang thang dọc đường đi bộ Ximending, bạn sẽ tìm thấy bất cứ điều gì mà bạn thích từ các cửa hàng quần áo thời trang và phụ kiện của Trung Quốc, Nhật Bản và các nước phương Tây, đến những món ăn nhẹ như món bánh tráng gạo, thịt lợn om, bún bò, bánh chiên, trà bong bóng…</p> <p style="text-align: justify;"><strong>Khu công viên địa chất Dạ Liễu (Yehliu)</strong></p> <p style="text-align: justify;">Công viên địa chất Yehliu nổi tiếng với những tảng đá có hình dạng kỳ lạ, những di tích hóa thạch có giá trị lịch sử và những cái tên hết sức ngộ nghĩnh. Tên các mỏm đá nơi đây được đặt dựa vào trí tưởng tượng đối với hình thù của nó, ví dụ như: Hài Tiên, Đá Nấm, Đá Nến,…</p> <div id="attachment_202241" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-202241 size-full" title="Ảnh: Thousand Wonders" src="https://cdn3.ivivu.com/2017/04/cong-vien-da-lieu-dai-loan-ivivu-2-1024x683.jpg" alt="Ảnh: Thousand Wonders" width="1024" height="683" /><p class="wp-caption-text">Ảnh: Thousand Wonders</p></div> <div id="attachment_202242" style="width: 483px" class="wp-caption aligncenter"><img class="wp-image-202242 size-full" title="Ảnh: @eunaee313" src="https://cdn3.ivivu.com/2017/04/da-lieu-ivivu-1.png" alt="Ảnh: @eunaee313" width="473" height="587" /><p class="wp-caption-text">Ảnh: @eunaee313</p></div> <p><strong>Hồ Nhật Nguyệt</strong></p> <p style="text-align: justify;">Thuộc địa phận xã Yuchi, huyện Nantou của miền Trung Đài Loan, hồ Nhật Nguyệt (Sun Moon Lake) là hồ nước tự nhiên lớn nhất Đài Loan với diện tích 7.73km², nằm ở độ cao 748 m so với mực nước biển, nơi sâu nhất là 27 m. Sở dĩ hồ có tên gọi như vậy vì phía Đông của hồ giống như mặt trời trong khi phía Tây lại giống với mặt trăng.</p> <div id="attachment_202243" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-202243 size-full" title="Ảnh:@vanessa_cc_lemon" src="https://cdn3.ivivu.com/2017/04/ho-nhat-nguyet-ivivu-3.jpg" alt="Ảnh:@vanessa_cc_lemon" width="1080" height="1080" /><p class="wp-caption-text">Ảnh:@vanessa_cc_lemon</p></div> <div id="attachment_202245" style="width: 603px" class="wp-caption aligncenter"><img class="wp-image-202245 size-full" title="Ảnh:@mameenn" src="https://cdn3.ivivu.com/2017/04/ho-nhat-nguyet-ivivu-1.png" alt="Ảnh:@mameenn" width="593" height="413" /><p class="wp-caption-text">Ảnh:@mameenn</p></div> <div id="attachment_202246" style="width: 600px" class="wp-caption aligncenter"><img class="wp-image-202246 size-full" title="Ảnh:@bellita33" src="https://cdn3.ivivu.com/2017/04/ho-nhat-nguyet-ivivu-2.png" alt="Ảnh:@bellita33" width="590" height="448" /><p class="wp-caption-text">Ảnh:@bellita33</p></div> <p style="text-align: justify;"><strong>Đền Võ Văn Miếu</strong></p> <p style="text-align: justify;">Là nơi thờ Khổng Tử (Văn Miếu) và Quan Công (Võ Miếu) nằm ở bờ hồ phía Bắc của Nhật Nguyệt Hồ, thuộc Đài Trung, là một trong những chùa nổi tiếng Đài Loan. Võ Văn Miếu gây ấn tượng với du khách bởi được xây dựng thành hệ thống kiến trúc bên triền đồi, trải dài từ thấp đến cao.</p> <div id="attachment_202247" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-202247 size-full" title="Ảnh: @wiebe_k" src="https://cdn3.ivivu.com/2017/04/ho-nhat-nguyet-ivivu-1.jpg" alt="Ảnh: @wiebe_k" width="1080" height="1080" /><p class="wp-caption-text">Ảnh: @wiebe_k</p></div> <div id="attachment_202248" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-202248 size-full" title="Ảnh: @lpxgm" src="https://cdn3.ivivu.com/2017/04/mieu-van-mieu-vo-ivivu-1.png" alt="Ảnh: @lpxgm" width="594" height="445" /><p class="wp-caption-text">Ảnh: @lpxgm</p></div> <p><strong>Núi Dương Minh Sơn</strong></p> <p>Núi Dương Minh Sơn (Yang Ming Shan) là một trong 8 vườn quốc gia lớn ở đây, nằm gần trung tâm thành phố Đài Bắc. Đây là ngọn núi có diện tích lớn nhất và đẹp nhất Đài Loan với cảnh sắc thiên nhiên tuyệt đẹp như suối nước nóng, thung lũng, núi lửa…</p> <div id="attachment_202250" style="width: 604px" class="wp-caption aligncenter"><img class="wp-image-202250 size-full" title="Ảnh: @ivywoww" src="https://cdn3.ivivu.com/2017/04/duong-minh-son-ivivu-2.png" alt="Ảnh: @ivywoww" width="594" height="448" /><p class="wp-caption-text">Ảnh: @ivywoww</p></div> <div id="attachment_202251" style="width: 607px" class="wp-caption aligncenter"><img class="wp-image-202251 size-full" title="Ảnh: @zona830" src="https://cdn3.ivivu.com/2017/04/duong-minh-son-ivivu-3.png" alt="Ảnh: @zona830" width="597" height="558" /><p class="wp-caption-text">Ảnh: @zona830</p></div> <div id="attachment_202252" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-202252 size-full" title="Ảnh: @jie_mel" src="https://cdn3.ivivu.com/2017/04/nui-duong-minh-ivivu-5.jpg" alt="Ảnh: @jie_mel" width="1080" height="1080" /><p class="wp-caption-text">Ảnh: @jie_mel</p></div> <p style="text-align: justify;"><strong>Phố cổ Thập Phần (Shifen old town)</strong></p> <p style="text-align: justify;">Những ai yêu thích sự lãng mạn và từng xem bộ phim Apple of My Eyes sẽ mê mẩn nơi này khi được tham gia thả đèn trời với các cặp đôi người bản địa. Ngoài những chiếc đèn lồng bán sẵn, du khách cũng có thể mua giấy, tự thiết kế đèn lồng, ghi điều ước lên đó, và thả lên trời.</p> <div id="attachment_202254" style="width: 483px" class="wp-caption aligncenter"><img class="wp-image-202254 size-full" title="Ảnh:@chen_shih_han_xx" src="https://cdn3.ivivu.com/2017/04/pho-co-thap-phan-ivivu-2.png" alt="Ảnh:@chen_shih_han_xx" width="473" height="569" /><p class="wp-caption-text">Ảnh:@chen_shih_han_xx</p></div> <div id="attachment_202255" style="width: 601px" class="wp-caption aligncenter"><img class="wp-image-202255 size-full" title="Ngoài những chiếc đèn lồng bán sẵn, du khách cũng có thể mua giấy, tự thiết kế đèn lồng, ghi điều ước lên đó, và thả lên trời. Ảnh:@ax.nnr" src="https://cdn3.ivivu.com/2017/04/pho-co-thap-phan-ivivu-6.png" alt="Ngoài những chiếc đèn lồng bán sẵn, du khách cũng có thể mua giấy, tự thiết kế đèn lồng, ghi điều ước lên đó, và thả lên trời. Ảnh:@ax.nnr" width="591" height="580" /><p class="wp-caption-text">Ngoài những chiếc đèn lồng bán sẵn, du khách cũng có thể mua giấy, tự thiết kế đèn lồng, ghi điều ước lên đó, và thả lên trời. Ảnh:@ax.nnr</p></div> <div id="attachment_202257" style="width: 602px" class="wp-caption aligncenter"><img class="wp-image-202257 size-full" title="Ảnh:@jaen_jen" src="https://cdn3.ivivu.com/2017/04/pho-co-thap-phan-ivivu-4.png" alt="Ảnh:@jaen_jen" width="592" height="596" /><p class="wp-caption-text">Ảnh:@jaen_jen</p></div> <p><strong>Ngôi làng cổ Jiu Fen</strong></p> <p style="text-align: justify;">Ngôi làng nhỏ trên vách núi nhìn ra bờ biển Đông Đài Loan là địa điểm không thể bỏ qua khi du lịch Đài Loan. Chỉ nằm cách Đài Bắc 1 giờ đi xe, Jiu Fen từng là nơi người ta đổ xô đến đào vàng. Ngày nay, khu mỏ đã đóng cửa, Jiufen được tu sửa và đón tiếp du khách với những trà quán thanh bình, những cửa hàng, quán bar, phòng triển lãm rải rác khắp nơi.</p> <div id="attachment_202261" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-202261 size-full" title="Ảnh: Minh Trí" src="https://cdn3.ivivu.com/2017/04/lang-co-jiufen-ivivu-2.jpg" alt="Ảnh: Minh Trí" width="960" height="960" /><p class="wp-caption-text">Ảnh: Minh Trí</p></div> <div id="attachment_202263" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-202263 size-full" title="Ảnh:@chu_won_917" src="https://cdn3.ivivu.com/2017/04/lang-co-jiufen-ivivu-3.jpg" alt="Ảnh:@chu_won_917" width="1080" height="1080" /><p class="wp-caption-text">Ảnh:@chu_won_917</p></div> <div id="attachment_202262" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-202262 size-full" title="Ảnh: Minh Trí" src="https://cdn3.ivivu.com/2017/04/du-lich-dai-loan-ivivu.com-81.jpg" alt="Ảnh: Minh Trí" width="960" height="640" /><p class="wp-caption-text">Ảnh: Minh Trí</p></div> <p style="text-align: justify;">Đặt chân đến Jiufen, bạn sẽ được thả hồn theo làn sương mù giăng mắc khắp nơi tạo nên một khung cảnh thiên nhiên huyền ảo. Đặc biệt, bạn còn sẽ được tận hưởng bầu không khí trong lành, mát lạnh từ biển khơi xanh thẳm và phóng tầm mắt ra xa ngắm mây trời, non nước hữu tình.</p> <p><strong>Tòa nhà Taipei 101</strong></p> <p style="text-align: justify;">Tòa nhà Taipei 101 ở quận Xinyi, thành phố Đài Bắc cao khoảng 509m, có tổng cộng 101 tầng là biểu tượng và niềm tự hào của người dân Đài Loan. Ở thời điểm năm 2004, nó được mệnh danh là tòa nhà cao nhất thế giới và danh hiệu đó được giữ vững cho đến tận năm 2010 khi toà tháp Buri Khalifa xuất hiện.</p> <div id="attachment_202266" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-202266 size-full" title="Ảnh:@sodasodium" src="https://cdn3.ivivu.com/2017/04/taipei-101-ivivu.jpg" alt="Ảnh:@sodasodium" width="1080" height="1349" /><p class="wp-caption-text">Ảnh:@sodasodium</p></div> <div id="attachment_202267" style="width: 475px" class="wp-caption aligncenter"><img class="wp-image-202267 size-full" title="Ảnh:@cultureguy77" src="https://cdn3.ivivu.com/2017/04/Taipei-101-ivivu-3.png" alt="Ảnh:@cultureguy77" width="465" height="568" /><p class="wp-caption-text">Ảnh:@cultureguy77</p></div> <div id="attachment_202268" style="width: 484px" class="wp-caption aligncenter"><img class="wp-image-202268 size-full" title="Ảnh:@doublel.010" src="https://cdn3.ivivu.com/2017/04/Taipei-101-ivivu-4.png" alt="Ảnh:@doublel.010" width="474" height="563" /><p class="wp-caption-text">Ảnh:@doublel.010</p></div> <p style="text-align: justify;">Tòa tháp này có 2 thang máy vận hành với tốc độ 16.83m/giây, du khách đi tầng 5 của tòa tháp lên đến tầng 89 chỉ với 37 giây. Là hình ảnh biểu tượng của Đài Loan nên dễ hiểu vì sao du khách đến Đài Bắc đều không quên lưu lại những hình ảnh với tòa nhà này.</p> <p style="text-align: justify;"><strong>Alishan (A Lý Sơn)</strong></p> <p style="text-align: justify;">Alishan nằm ở giữa phía Đông của Đài Loan, trải dài trên bốn thị trấn: Fanlu, Zhuqi, Mi Sơn và Alishan; có tổng diện tích 41.520 ha trải dài trên 18 ngọn đồi. Ở đây có tháp Hill cao khoảng 2.663 m. Các cảnh quan bạn không nên bỏ lỡ khi đến đây, đó là cảnh mặt trời mọc lộng lẫy, những đám mây trắng bồng bềnh, khu rừng bách tuyệt đẹp, những bụi cherry thơm lừng và đường ray trong rừng có hình chữ Z độc đáo.</p> <div id="attachment_202269" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-202269 size-full" title="Ảnh:@taetee" src="https://cdn3.ivivu.com/2017/04/du-lich-dai-loan-ivivu-11.png" alt="Ảnh:@taetee" width="596" height="399" /><p class="wp-caption-text">Ảnh:@taetee</p></div> <div id="attachment_202270" style="width: 608px" class="wp-caption aligncenter"><img class="wp-image-202270 size-full" title="Ảnh:@roundjamesround" src="https://cdn3.ivivu.com/2017/04/du-lich-dai-loan-ivivu-21.png" alt="Ảnh:@roundjamesround" width="598" height="396" /><p class="wp-caption-text">Ảnh:@roundjamesround</p></div> <div id="attachment_202271" style="width: 605px" class="wp-caption aligncenter"><img class="wp-image-202271 size-full" title="Ảnh:@onchani" src="https://cdn3.ivivu.com/2017/04/du-lich-dai-loan-ivivu-31.png" alt="Ảnh:@onchani" width="595" height="584" /><p class="wp-caption-text">Ảnh:@onchani</p></div> <p><strong>Chợ đêm Phụng Giáp</strong></p> <p style="text-align: justify;">Là chợ đêm nổi tiếng nhất Đài Trung, chợ đêm Phụng Giáp bày bán đủ mọi mặt hàng từ quần áo đến đồ gia dụng. Nhưng có lẽ mặt hàng thu hút khách nhất là đồ ăn. Các gian hàng bán đồ ăn trải đầy khắp mọi nẻo đường, và rất dễ nhận ra là người Hoa rất thích những món chiên xào như cá viên chiên, tàu hũ chiên, cánh gà chiên, mì xào…</p> <div id="attachment_202264" style="width: 602px" class="wp-caption aligncenter"><img class="wp-image-202264 size-full" title="Ảnh: @c89_" src="https://cdn3.ivivu.com/2017/04/cho-dem-phung-giap-ivivu-1.png" alt="Ảnh: @c89_" width="592" height="568" /><p class="wp-caption-text">Ảnh: @c89_</p></div> <div id="attachment_202265" style="width: 605px" class="wp-caption aligncenter"><img class="wp-image-202265 size-full" title="Ảnh: @vnhy" src="https://cdn3.ivivu.com/2017/04/cho-dem-phung-giap-ivivu-2.png" alt="Ảnh: @vnhy" width="595" height="565" /><p class="wp-caption-text">Ảnh: @vnhy</p></div> <div class="ltt-contentbox white"><span class="icon add"></span></p> <p><strong>Hướng dẫn cách đặt gói khuyến mãi này:</strong></p> <p>– Gọi  <strong>(08) 3933 8002</strong> để được tư vấn</p> <p>– Đặt online và xem lịch trình tour chi tiết <a href="https://www.ivivu.com/du-lich/tour-dai-loan-5n5d-dai-bac-dai-trung-gia-nghia-a-ly-son/682?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"><strong>TẠI ĐÂY</strong></a></p> <p></div> <p style="text-align: right;"><strong> Theo iVIVU.com</strong></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p><strong> Xem thêm các bài viết:</strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/04/dai-loan-mien-phi-thu-tuc-cap-visa-nhap-canh-trong-7-ngay-cho-khach-di-theo-nhom/" target="_blank">Đài Loan miễn phí thủ tục cấp visa nhập cảnh trong 7 ngày cho khách đi theo nhóm</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/03/4-ngay-3-dem-vi-vu-dai-loan-het-cong-suat-chi-mat-7-trieu-dong/" target="_blank">4 ngày 3 đêm vi vu Đài Loan “hết công suất” chỉ mất 7 triệu đồng</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/03/di-dai-loan-ngay-va-luon-thoi-dep-den-the-nay-co-ma/" target="_blank">Đi Đài Loan ngay và luôn thôi, đẹp đến thế này cơ mà!</a></strong></p> <p><strong></div>', 'Tour 5 ngày chỉ với giá 9.999.000 đồng, đi Đài Loan trong hè này khám phá các điểm đến cực kỳ hấp dẫn như: Đài Bắc – Đài Trung– Gia Nghĩa – A Lý Sơn. Gọi ngay đến số hotline  (08) 3933 8002 để được tư vấn kỹ hơn nhé!', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'len-ngu-ho-son-thuong-thuc-mui-hong-quan-ivivu-3.jpg', NULL);
INSERT INTO `baiviet` (`IDBV`, `IDKhuVuc`, `IDLoaiTin`, `TieuDe`, `NoiDung`, `TomTat`, `NoiBat`, `NgayViet`, `NgayCapNhat`, `IDNhanVien`, `updated_at`, `created_at`, `anhdaidien`, `luotxem`) VALUES
(15, '9', '2', 'Khi còn trẻ, hãy để để mình một lần ‘lạc trôi’ đến 9 điểm hấp dẫn ở Đông Nam Á này', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/khi-con-tre-hay-de-de-minh-mot-lan-lac-troi-den-9-diem-hap-dan-o-dong-nam-a-nay/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><em><strong>Vẻ đẹp thơ mộng cùng nền văn hóa đặc sắc của 9 điểm đến <a href="https://www.ivivu.com/blog/category/dong-nam-a-2/" target="_blank">Đông Nam Á</a> dưới đây khiến bất cứ tín đồ xê dịch nào cũng muốn được một lần khám phá.</strong></em></p> <p style="text-align: justify;"><span id="more-203332"></span></p> <h2 style="text-align: justify;">Khi còn trẻ, hãy để để mình một lần ‘lạc trôi’ đến 9 điểm hấp dẫn ở Đông Nam Á này</h2> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2016/05/kinh-nghiem-du-lich-phuket-4-ngay-3-dem-xa-lang-voi-7-trieu-dong/" target="_blank">Phuket</a>, Thái Lan</strong></p> <p style="text-align: justify;">Những năm gần đây, Thái Lan trở thành điểm đến yêu thích của giới trẻ Việt Nam. Việc đi Thái ngày càng trở nên đơn giản và dễ dàng với vé máy bay giá rẻ cùng các chi phí ăn ở hợp túi tiền. Nhưng mà, loanh quanh mua sắm ở Bangkok mãi cũng chán, giới trẻ dần để mắt hơn đến những địa điểm thú vị khác tại Thái, trong đó có Phuket. Nằm cách thủ đô Bangkok không xa, Phuket được mệnh danh là đảo thiên đường với những hòn đảo, bãi biển trong veo xinh đẹp và những bữa tiệc bất tận trên biển.</p> <div id="attachment_203415" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-203415 size-full" title="Ảnh: Hoà Bùi" src="https://cdn3.ivivu.com/2017/04/phuket-ivivu-1.jpg" alt="Ảnh: Hoà Bùi" width="650" height="488" /><p class="wp-caption-text">Ảnh: Hoà Bùi</p></div> <div id="attachment_203417" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-203417 size-full" title="Ảnh:@mail_mital" src="https://cdn3.ivivu.com/2017/04/phuket-ivivu-3.jpeg" alt="Ảnh:@mail_mital" width="650" height="645" /><p class="wp-caption-text">Ảnh:@mail_mital</p></div> <div id="attachment_203416" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-203416 size-full" title="Ảnh:@pear_jinipa" src="https://cdn3.ivivu.com/2017/04/phuket-ivivu-2.jpeg" alt="Ảnh:@pear_jinipa" width="650" height="801" /><p class="wp-caption-text">Ảnh:@pear_jinipa</p></div> <div id="attachment_203418" style="width: 660px" class="wp-caption aligncenter"><img class="wp-image-203418 size-full" title="Ảnh:@gg.around.the.world" src="https://cdn3.ivivu.com/2017/04/phuket-ivivu-4.jpeg" alt="Ảnh:@gg.around.the.world" width="650" height="480" /><p class="wp-caption-text">Ảnh:@gg.around.the.world</p></div> <h4 style="text-align: center;"><div class="ltt-contentbox white"><span class="icon star"></span>Click xem thêm danh sách <a href="https://www.ivivu.com/khach-san-phuket?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">khách sạn Phuket</a> với giá ưu đãi cực tốt từ iVIVU.com</div></h4> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2015/12/du-lich-indonesia-kham-pha-ve-dep-doc-dao-cua-ngoi-den-borobudur/" target="_blank">Đền Borobudur</a>, Indonesia</strong></p> <p style="text-align: justify;">Đền Borobudur là công trình kiến trúc độc đáo của Indonesia được xây dựng vào thế kỷ thứ VIII, dưới triều đại vua Syailendra. Quần thể đền Borobudur gồm nhiều tháp nhỏ bao bọc xung quanh một tháp chính cao 42m với 12 tầng tháp. Để lên đến đỉnh tháp cao nhất, bạn phải trèo lên các bậc thang và đi qua các hành lang của 12 tầng tháp với tổng chiều dài 5 km.</p> <div id="attachment_203420" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-203420 size-full" title="Ảnh: Alexander Ipfelkofer" src="https://cdn3.ivivu.com/2017/04/den-Borobudur-ivivu-4.jpg" alt="Ảnh: Alexander Ipfelkofer" width="1024" height="683" /><p class="wp-caption-text">Ảnh: Alexander Ipfelkofer</p></div> <div id="attachment_203419" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-203419 size-full" title="Ảnh: Rico Glaus" src="https://cdn3.ivivu.com/2017/04/den-Borobudur-ivivu-9-1024x683.jpg" alt="Ảnh: Rico Glaus" width="1024" height="683" /><p class="wp-caption-text">Ảnh: Rico Glaus</p></div> <p style="text-align: justify;">Đền Borobudur có vị trí thuận lợi cách Yogyakarta khoảng 40km về phía Bắc. Để đến tham quan được ngôi đền, du khách có thể đi xe taxi từ trung tâm Yogyakarta hoặc thuê xe máy.</p> <div id="attachment_203421" style="width: 1034px" class="wp-caption aligncenter"><img class="size-full wp-image-203421" src="https://cdn3.ivivu.com/2017/04/den-Borobudur-ivivu-7.jpg" alt="Ảnh: Alexander Ipfelkofer" width="1024" height="686" /><p class="wp-caption-text">Ảnh: Alexander Ipfelkofer</p></div> <div id="attachment_203422" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-203422 size-full" title="Ảnh: Trey Ratcliff" src="https://cdn3.ivivu.com/2017/04/den-Borobudur-ivivu-1.jpg" alt="Ảnh: Trey Ratcliff" width="1024" height="582" /><p class="wp-caption-text">Ảnh: Trey Ratcliff</p></div> <p style="text-align: justify;">Đền Borobudur được coi là điểm tham quan nổi tiếng, thu hút khách du lịch đến với Indonesia. Đây không chỉ là một kỳ quan hiếm có trở thành biểu tượng của Indonesia mà còn là một trong những công trình nghệ thuật với kiến trúc tinh xảo có giá trị lớn nhất thế giới.</p> <p><strong><a href="https://www.ivivu.com/blog/2016/05/du-lich-el-nido-an-choi-toet-ga-trong-6-ngay-7-dem-chi-12-trieu-dong/" target="_blank">El Nido</a>, Phillipines</strong></p> <p style="text-align: justify;">Nếu nói Phillipines là một kho báu dành cho các tín đồ của biển, với những hòn đảo đẹp tuyệt vời như những viên ngọc đính trên chuỗi đá, thì El Nido sẽ là viên ngọc sáng nhất ở đó.</p> <div id="attachment_203423" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-203423 size-full" title="Ảnh:@hetoooo" src="https://cdn3.ivivu.com/2017/04/du-lich-El-Nido-ivivu-1.jpg" alt="Ảnh:@hetoooo" width="1080" height="1080" /><p class="wp-caption-text">Ảnh:@hetoooo</p></div> <div id="attachment_203424" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-203424 size-full" title="Ảnh:@forever_red_persia" src="https://cdn3.ivivu.com/2017/04/du-lich-El-Nido-ivivu-7.jpg" alt="Ảnh:@forever_red_persia" width="1080" height="1350" /><p class="wp-caption-text">Ảnh:@forever_red_persia</p></div> <p style="text-align: justify;">Nằm ở cuối đảo Palawan, cách Manila khoảng 420km, El Nido vắng vẻ và duyên dáng như một hòn ngọc thô vừa mới được phát hiện. Ở El Nido, bạn có thể thoải mái tắm nắng, lặn biển và cùng xuôi thuyền ghé thăm các đảo nhỏ hơn hay các bãi tắm bí mật ít người biết đến. Khi chán, bạn có thể nằm ườn trên bãi cát trắng phau, nghe nhạc và thả lỏng bản thân thôi cũng đủ thấy hạnh phúc.</p> <div id="attachment_203425" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-203425 size-full" title="Ảnh:@reghmanlangit" src="https://cdn3.ivivu.com/2017/04/du-lich-El-Nido-ivivu-3.jpg" alt="Ảnh:@reghmanlangit" width="1080" height="809" /><p class="wp-caption-text">Ảnh:@reghmanlangit</p></div> <div id="attachment_203426" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-203426 size-full" title="Ảnh:@moninamercado" src="https://cdn3.ivivu.com/2017/04/du-lich-El-Nido-ivivu-11.jpg" alt="Ảnh:@moninamercado" width="1080" height="810" /><p class="wp-caption-text">Ảnh:@moninamercado</p></div> <p style="text-align: justify;">Người ta gọi El Nido là vùng đất của rồng bởi cảnh quan hùng vĩ được tạo nên từ những hòn đá vôi lớn nhỏ đủ kích cỡ và hình dáng khác nhau. Những khối đá này nằm rải rác trên biển, cùng liên kết lại để hình thành một quần thể du lịch sinh thái vô cùng thu hút. Bên cạnh đó El Nido còn trở nên hấp dẫn hơn bởi những bãi biển, bờ cát trắng, cánh rừng ngập mặn và cả hang động kỳ bí.</p> <p><strong><a href="https://www.ivivu.com/blog/2015/09/phuot-thu-viet-chia-se-nhung-den-khong-the-bo-qua-khi-du-lich-bagan/" target="_blank">Bagan</a>, Myanmar</strong></p> <p style="text-align: justify;">Nằm bên bờ sông Irrawaddy miền trung Myanmar, Bagan là cố đô hưng thịnh nhất của vương quốc Pagan trong thế kỷ 11-13. Đây là địa danh lưu giữ nhiều nhất các giá trị văn hóa Phật giáo như đền, bảo tháp và tu viện.</p> <div id="attachment_203445" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-203445 size-full" title="Ảnh: San San" src="https://cdn3.ivivu.com/2017/04/bagan-ivivu-1.jpg" alt="Ảnh: San San" width="960" height="519" /><p class="wp-caption-text">Ảnh: San San</p></div> <div id="attachment_203448" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-203448 size-full" title="Ảnh: San San" src="https://cdn3.ivivu.com/2017/04/bagan-ivivu-3.jpg" alt="bagan-ivivu-3" width="960" height="640" /><p class="wp-caption-text">Ảnh: San San</p></div> <p style="text-align: justify;">Cố đô Myanmar bao gồm ba thị trấn: Nyuang U, Old Bagan và New Bagan cùng một khu vực trải rộng với nhiều đền thờ. Đến Bagan, bạn có thể đi bộ, xe đạp hay xe ngựa, thong dong trên các con đường mòn đất đỏ ngắm nhìn những quần thể đền, chùa chiền và tu viện giữa khoảng trời bao la. Đến nay, Bagan vẫn lưu giữ được khoảng 2.000 ngôi chùa và đền. Các ngôi đền ở đây khá gần nhau, bạn có thể đi khám phá hết khu vực này bằng phương tiện đơn giản.</p> <div id="attachment_203446" style="width: 970px" class="wp-caption aligncenter"><img class="size-full wp-image-203446" src="https://cdn3.ivivu.com/2017/04/bagan-ivivu-2.jpg" alt="Ảnh: San San" width="960" height="640" /><p class="wp-caption-text">Ảnh: San San</p></div> <div id="attachment_203450" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-203450 size-full" title="Ảnh: San San" src="https://cdn3.ivivu.com/2017/04/bagan-ivivu-5.jpg" alt="Ảnh: San San" width="960" height="640" /><p class="wp-caption-text">Ảnh: San San</p></div> <p style="text-align: justify;">Bạn có thể lựa chọn đi bộ để dừng tham quan bất cứ đâu bạn muốn, hay đi xe đạp để rong ruổi thêm những vùng đất xa hơn, các khu đền rộng lớn mà đỡ tốn sức, hoặc kết hợp đi xe ngựa để có thêm trải nghiệm thú vị hơn.</p> <p><strong><a href="https://www.ivivu.com/blog/2016/11/goi-y-lich-trinh-chi-tiet-3-ngay-du-lich-penang-an-ngon-va-canh-dep/" target="_blank">Penang</a>, Malaysia</strong></p> <p style="text-align: justify;">Nếu là một người thích yêu thích sự lắng đọng, thích lang thang qua những con phố cổ kính thì thành phố Penang của Malaysia là một điểm đến dành cho bạn. Nằm ở eo biển Malacca, Penang không chỉ có những cảnh quan biển, đảo đẹp “mê hồn”, mà còn sở hữu một vẻ đẹp văn hoá vô cùng thú vị.</p> <div id="attachment_203428" style="width: 606px" class="wp-caption aligncenter"><img class="wp-image-203428 size-full" title="Ảnh:@junbunnybee" src="https://cdn3.ivivu.com/2017/04/penang-ivivu-1.png" alt="Ảnh:@junbunnybee" width="596" height="583" /><p class="wp-caption-text">Ảnh:@junbunnybee</p></div> <div id="attachment_203427" style="width: 730px" class="wp-caption aligncenter"><img class="wp-image-203427 size-full" title="Ảnh: Trần Xuân" src="https://cdn3.ivivu.com/2017/04/du-lich-penang-ivivu-17.jpg" alt="Ảnh: Trần Xuân" width="720" height="960" /><p class="wp-caption-text">Ảnh: Trần Xuân</p></div> <div id="attachment_203430" style="width: 607px" class="wp-caption aligncenter"><img class="wp-image-203430 size-full" title="Ảnh:@abdul__asis" src="https://cdn3.ivivu.com/2017/04/penang-ivivu-3.png" alt="Ảnh:@abdul__asis" width="597" height="443" /><p class="wp-caption-text">Ảnh:@abdul__asis</p></div> <div id="attachment_203429" style="width: 1090px" class="wp-caption aligncenter"><img class="wp-image-203429 size-full" title="Ảnh: thetravelearn.com" src="https://cdn3.ivivu.com/2017/04/du-lich-penang-ivivu-1.jpg" alt="Ảnh: thetravelearn.com" width="1080" height="719" /><p class="wp-caption-text">Ảnh: thetravelearn.com</p></div> <p style="text-align: justify;">Trong không khí nhẹ nhàng, chậm rãi của thành phố này, bạn sẽ có thể thoả chí lang thang của mình, từ các con phố cổ ở Georgetown với những bức tranh nghệ thuật đường phố sáng tạo, những cửa hàng truyền thống, đến những ngôi chùa đặc sắc hay những hàng quán ẩm thực đường phố độc đáo. Penang có tất cả mọi thứ mà bạn cần cho một chuyến đi khám phá, nhẹ nhàng và thư giãn.</p> <h4 style="text-align: center;"><div class="ltt-contentbox white"><span class="icon star"></span>Tham khảo danh sách <a href="https://www.ivivu.com/khach-san-penang-island?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">khách sạn Penang</a> giá tốt tại iVIVU.com</div></h4> <p><strong><a href="https://www.ivivu.com/blog/2015/06/nhung-diem-dung-chan-thu-vi-khi-du-lich-luang-prabang/" target="_blank">Luang Prabang</a>, Lào</strong></p> <p style="text-align: justify;">Là một trong những điểm du lịch hấp dẫn nhiều du khách nước ngoài nhất ở Lào, cố đô Luang Prabang với vẻ đẹp bình yên mang dấu ấn trung tâm Phật giáo được xem là điểm du lịch nổi tiếng bởi sự thanh bình và vẻ đẹp vừa tự nhiên vừa nguy nga lộng lẫy…</p> <div id="attachment_203437" style="width: 630px" class="wp-caption aligncenter"><img class="wp-image-203437 size-full" title="Ảnh:Magazine.tripzilla.com" src="https://cdn3.ivivu.com/2017/04/kham-pha-luang-phrabang-ivivu-4.jpg" alt="Ảnh:Magazine.tripzilla.com" width="620" height="413" /><p class="wp-caption-text">Ảnh:Magazine.tripzilla.com</p></div> <div id="attachment_203432" style="width: 1930px" class="wp-caption aligncenter"><img class="wp-image-203432 size-full" title="Ảnh: guardian" src="https://cdn3.ivivu.com/2017/04/kham-pha-luang-phrabang-ivivu-1.jpg" alt="Ảnh: guardian" width="1920" height="1152" /><p class="wp-caption-text">Ảnh: guardian</p></div> <div id="attachment_203436" style="width: 610px" class="wp-caption aligncenter"><img class="wp-image-203436 size-full" title="Ảnh: Thiện Nguyễn" src="https://cdn3.ivivu.com/2017/04/kham-pha-luang-phrabang-ivivu-3.jpg" alt="Ảnh: Thiện Nguyễn" width="600" height="400" /><p class="wp-caption-text">Ảnh: Thiện Nguyễn</p></div> <div id="attachment_203433" style="width: 1034px" class="wp-caption aligncenter"><img class="wp-image-203433" title="Ảnh: Vincent Jary/Getty Images" src="https://cdn3.ivivu.com/2017/04/kham-pha-luang-phrabang-ivivu-2-1024x614.jpg" alt="Ảnh: Vincent Jary/Getty Images " width="1024" height="614" /><p class="wp-caption-text">Ảnh: Vincent Jary/Getty Images</p></div> <p style="text-align: justify;">Nét rêu phong cổ kính của những dãy phố nhuốm màu thời gian và lung linh đèn lồng về đêm khiến Luang Prabang giống với Hội An, Việt Nam, đồng thời gợi nhớ đến Sapa với những con đường đèo uốn lượn, những quán nhỏ nghi ngút khói từ các món nướng… Ngoài ra, nét quyến rũ của Luang Prabang còn nằm ở tinh thần Phật giáo với lối sống thong thả, thật thà, cùng sự tinh tươm của phố xá và cả sắc áo cam rực rỡ của những nhà tu hành bên những kiến trúc thếp vàng lộng lẫy.</p> <p><strong><a href="https://www.ivivu.com/blog/2016/09/di-bali-ngay-thoi-khong-chi-co-bien-bali-con-co-nhieu-dieu-thu-vi-hon-the/" target="_blank">Bali</a>, Indonesia</strong></p> <p style="text-align: justify;">Với cảnh quan thiên nhiên tuyệt đẹp, khí hậu biển và nền văn hóa đặc trưng, từ lâu Bali được du khách ưu ái đặt tên là “thiên đường nhiệt đới” và là địa điểm phải đến ít nhất một lần của mọi tín đồ du lịch. Sức hấp dẫn của đảo Bali đến từ những bãi biển xinh đẹp, những thửa ruộng bậc thang xanh ngút ngàn, hay những cánh rừng nhiệt đới xanh mát quanh năm tạo nên cảnh sắc có một không hai tại đảo Bali.</p> <div id="attachment_203440" style="width: 921px" class="wp-caption aligncenter"><img class="wp-image-203440 size-full" title="Ảnh: kizie.com" src="https://cdn3.ivivu.com/2017/04/bali-ivivu.com-1.jpg" alt="Ảnh: kizie.com" width="911" height="648" /><p class="wp-caption-text">Ảnh: kizie.com</p></div> <div id="attachment_203438" style="width: 605px" class="wp-caption aligncenter"><img class="wp-image-203438 size-full" title="Ảnh:@liburanbali" src="https://cdn3.ivivu.com/2017/04/bali-ivivu-2.png" alt="Ảnh:@liburanbali" width="595" height="565" /><p class="wp-caption-text">Ảnh:@liburanbali</p></div> <div id="attachment_203439" style="width: 600px" class="wp-caption aligncenter"><img class="wp-image-203439 size-full" title="Ảnh:@mybalitrips" src="https://cdn3.ivivu.com/2017/04/bali-ivivu-4.png" alt="Ảnh:@mybalitrips" width="590" height="578" /><p class="wp-caption-text">Ảnh:@mybalitrips</p></div> <div id="attachment_203441" style="width: 1250px" class="wp-caption aligncenter"><img class="wp-image-203441 size-full" title="Ảnh: unmotivating.com" src="https://cdn3.ivivu.com/2017/04/bali-ivivu.com-2.jpg" alt="Ảnh: unmotivating.com" width="1240" height="827" /><p class="wp-caption-text">Ảnh: unmotivating.com</p></div> <p style="text-align: justify;">Hơn nữa, với danh hiệu xứ sở của những ngôi đền, Bali có đến 20.000 ngôi đền lớn nhỏ, Khi đến đây, bạn nhớ đừng bỏ qua đền Uluwatu và đền Tanah Lot – đây là những ngôi đền được đánh giá là đẹp nhất trong các ngôi đền ở Bali, cùng tọa lạc trên những vách đá dựng đứng nhìn xuống biển, cả hai ngôi đền đều được khách du lịch ưa thích nhờ vị trí địa lý độc đáo đảo mình.</p> <h4 style="text-align: center;"><div class="ltt-contentbox white"><span class="icon star"></span>XEM THÊM DANH SÁCH <a href="https://www.ivivu.com/khach-san-bali-indonesia?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">KHÁCH SẠN BALI </a> VỚI GIÁ ƯU ĐÃI CỰC TỐT TỪ IVIVU.COM</div></h4> <p><strong><a href="https://www.ivivu.com/blog/2015/12/25-trai-nghiem-khong-the-bo-qua-khi-du-lich-siem-reap/" target="_blank">Siem Reap</a>, Campuchia</strong></p> <p style="text-align: justify;">Nằm ở phía Tây Bắc Campuchia, Siem Reap là thành phố thu hút nhiều du khách du lịch Campuchia nhất nhờ những ngôi đền Angkor hùng vĩ. Từng là kinh đô của đế quốc Khmer xưa, cố đô Siem Reap ngày nay tuy phát triển sầm uất, nhưng vẫn còn mang nhiều nét huyền bí, cổ xưa của một trong những nền văn minh huy hoàng nhất một thời.</p> <div id="attachment_203442" style="width: 670px" class="wp-caption aligncenter"><img class="wp-image-203442 size-full" title="Ảnh: Trần Ngọc Toàn." src="https://cdn3.ivivu.com/2017/04/siem-riep-ivivu-3.jpg" alt="Ảnh: Trần Ngọc Toàn." width="660" height="449" /><p class="wp-caption-text">Ảnh: Trần Ngọc Toàn.</p></div> <div id="attachment_203443" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-203443 size-full" title="Ảnh: San San" src="https://cdn3.ivivu.com/2017/04/siem-riep-ivivu-5.jpg" alt="Ảnh: San San" width="960" height="719" /><p class="wp-caption-text">Ảnh: San San</p></div> <div id="attachment_203444" style="width: 970px" class="wp-caption aligncenter"><img class="wp-image-203444 size-full" title="Ảnh: San San" src="https://cdn3.ivivu.com/2017/04/siem-riep-ivivu-1.jpg" alt="Ảnh: San San" width="960" height="719" /><p class="wp-caption-text">Ảnh: San San</p></div> <p style="text-align: justify;">Cách trung tâm Siep Reap 7 km là quần thể di tích nổi tiếng thế giới Angkor Wat. Đây là một trong các di tích lịch sử quan trọng bậc nhất tại Campuchia và được xem là tuyệt đỉnh kiến trúc Khmer. Angkor Wat được UNESCO công nhận là Di sản Văn hóa Thế giới vào năm 1991 và có thể sánh cùng với Vạn Lý Trường Thành, kim tự tháp Ai Cập…</p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2016/01/cao-nguyen-cameron-chon-binh-yen-giua-long-malaysia/" target="_blank">Cao nguyên Cameron</a>, Malaysia</strong></p> <p style="text-align: justify;">Nằm về hướng Đông Bắc và cách thủ đô Kuala Lumpur khoảng chừng 300km, cao nguyên Cameron được ví như một nàng sơn nữ còn đang ngủ vùi giữa những khu rừng xanh um và những đồi chè bạt ngàn của Malaysia. Cao nguyên Cameron là một huyện thuộc tỉnh Pahang, nằm ở độ cao 1.500m và cách thủ đô Kuala Lumpur hơn 4 tiếng xe buýt. Nơi đây được ví như Đà Lạt của Việt Nam với khí hậu mát mẻ quanh năm, thời tiết ôn hòa, thích hợp cho nhiều loại hoa cỏ ôn đới.</p> <div id="attachment_203453" style="width: 810px" class="wp-caption aligncenter"><img class="wp-image-203453 size-full" title="Ảnh: thelongestwayhome.com" src="https://cdn3.ivivu.com/2017/04/cao-nguyen-Cameron-chon-binh-yen-giua-long-malaysia-ivivu-8.jpg" alt="Ảnh: thelongestwayhome.com" width="800" height="533" /><p class="wp-caption-text">Ảnh: thelongestwayhome.com</p></div> <div id="attachment_203454" style="width: 980px" class="wp-caption aligncenter"><img class="wp-image-203454 size-full" title="Ảnh: kiwicollection.com" src="https://cdn3.ivivu.com/2017/04/cao-nguyen-Cameron-chon-binh-yen-giua-long-malaysia-ivivu-2.jpg" alt="Ảnh: kiwicollection.com" width="970" height="546" /><p class="wp-caption-text">Ảnh: kiwicollection.com</p></div> <p style="text-align: justify;">Cao nguyên Cameron là một trong những vùng đồi núi rộng lớn nhất Malaysia, và là một địa điểm du lịch có từ rất lâu đời. Cameron nổi tiếng với những con đường mòn sẽ dẫn bạn đi qua những khoảnh rừng rộng lớn, đến các thác nước và các điểm đến yên tĩnh khác.</p> <div id="attachment_203455" style="width: 610px" class="wp-caption aligncenter"><img class="wp-image-203455 size-full" title="Ảnh: getaways-guide.groupon.my" src="https://cdn3.ivivu.com/2017/04/cao-nguyen-Cameron-chon-binh-yen-giua-long-malaysia-ivivu-6.jpg" alt="Ảnh: getaways-guide.groupon.my" width="600" height="403" /><p class="wp-caption-text">Ảnh: getaways-guide.groupon.my</p></div> <div id="attachment_203457" style="width: 1010px" class="wp-caption aligncenter"><img class="wp-image-203457 size-full" title="Ảnh: kuantan-hotels.com" src="https://cdn3.ivivu.com/2017/04/cao-nguyen-Cameron-chon-binh-yen-giua-long-malaysia-ivivu-3.jpg" alt="Ảnh: kuantan-hotels.com" width="1000" height="656" /><p class="wp-caption-text">Ảnh: kuantan-hotels.com</p></div> <p style="text-align: justify;">Đến với những đồi chè ở cao nguyên Cameron, du khách sẽ có cảm giác lạc vào một vùng đồng quê ở nước Anh. Những ngôi nhà với kiến trúc kiểu Anh xinh đẹp là nơi nghỉ chân cho du khách khi đến đây tham quan và nghỉ dưỡng, ngoài ra nơi đây còn khiến du khách phải bồi hồi vì những vườn dâu được chăm sóc kỹ lưỡng, với những chùm dâu trĩu quả.</p> <h4 style="text-align: center;"><div class="ltt-contentbox white"><span class="icon star"></span>BẠN CẦN MỘT NƠI LƯU TRÚ VỚI GIÁ HỢP TÚI TIỀN TRONG HÀNH TRÌNH DU LỊCH, ĐỪNG NGẦN NGẠI GỌI NGAY TỚI SỐ 1900 1870 ĐỂ ĐƯỢC TƯ VẤN NHÉ</div></h4> <p style="text-align: right;"><strong> Theo San San</strong></p> <p><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p><strong>Xem thêm các bài viết:</strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/04/dat-ngay-tour-thai-lan-4n3d-bangkok-pattaya-dip-le-304-chi-5-590-000-dong/" target="_blank">Đặt ngay tour Thái Lan 4N3Đ Bangkok – Pattaya dịp lễ 30/4 chỉ 5.590.000 đồng</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/02/lich-trinh-6-ngay-an-choi-quen-duong-ve-trong-chuyen-du-lich-krabi-phi-phi-koh-tao/" target="_blank">Lịch trình 6 ngày ăn chơi ‘quên đường về’ trong chuyến du lịch Krabi – Phi Phi – Koh Tao</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/03/han-quoc-noi-long-chinh-sach-xin-visa-cho-du-khach-dong-nam-a/" target="_blank">Hàn Quốc nới lỏng chính sách xin visa cho du khách Đông Nam Á</a></strong></p> <p><strong></div>', 'Vẻ đẹp thơ mộng cùng nền văn hóa đặc sắc của 9 điểm đến Đông Nam Á dưới đây khiến bất cứ tín đồ xê dịch nào cũng muốn được một lần khám phá.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'du-ngoan-ho-dai-nhat-new-zealand-tren-con-tau-tram-tuoi-ivivu-2.jpg', NULL),
(16, '9', '2', 'Top 6 địa điểm chụp ảnh sống ảo ở Vũng Tàu bao đẹp', '<div class="entry-content">	      				<div class="top-sns-wrap">  					<iframe src="//www.facebook.com/plugins/like.php?href=https://www.ivivu.com/blog/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep/&width=296&layout=button_count&action=like&show_faces=true&share=true&height=21&appId=238105066541501" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:296px; height:21px;" allowtransparency="true"></iframe>  				</div>        			<p style="text-align: justify;"><strong><em>Tuy không quá nhộn nhịp và nhiều chỗ vui chơi nhưng nếu muốn có một bộ ảnh độc đáo thì <a href="https://www.ivivu.com/blog/category/viet-nam/vung-tau-viet-nam/" target="_blank">Vũng Tàu</a> là sự lựa chọn hoàn hảo cho bạn.</em></strong><span id="more-203343"></span></p> <div class="ltt-contentbox white"><span class="icon add"></span><strong>Xem thêm: <a href="https://www.ivivu.com/blog/category/viet-nam/vung-tau-viet-nam/" target="_blank">Du lịch Vũng Tàu</a></strong></div> <h2>Top 6 địa điểm chụp ảnh sống ảo ở Vũng Tàu bao đẹp</h2> <p style="text-align: justify;">Dưới đây là chia sẻ về những địa điểm chụp ảnh sống ảo ở Vũng Tàu từ bạn Dương Thùy. Theo Dương Thùy, vì đây là lần đầu đến <strong>du lịch Vũng Tàu</strong> nên trước khi đi mình đã tìm hiểu khá kĩ về vài địa điểm nổi bật để chụp hình. Tuy nhiên do các địa điểm như Hồ Cốc, Hồ Đá Xanh, bến du thuyền Marina… đã có rất nhiều bạn chụp rồi nên mình muốn tìm chút gì đó mới lạ hơn. Bên dưới đều là những chỗ mình vô tình đi qua và ưng mắt nên dừng lại chụp, mời các bạn cùng tham khảo nhé.</p> <p><strong>Đường ven biển ra Long Hải </strong></p> <p style="text-align: justify;">Vì thấy nhiều người review về Long Hải với bờ cát trắng chụp hình đẹp nên mình có ý định đến thử. Trên đường từ thành phố đi bạn hãy để ý khúc cua ven biển. Đoạn này rất ngắn và chỉ duy nhất chỗ đó nên không lẫn đi đâu được. Nếu vào tầm 9,10h trưa bạn đến đó ánh sáng rất tốt, cung đường cua rộng nhìn ra biển xanh chụp nhìn chất đừng hỏi, bạn có thể đi bộ xuống bãi đá check-in thêm vài kiểu nữa nha.</p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203364" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-1.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-1" width="960" height="726" /> <img class="alignnone size-full wp-image-203365" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-2.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-2" width="960" height="720" /> <img class="alignnone size-full wp-image-203366" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-3.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-3" width="768" height="960" /> <img class="alignnone size-full wp-image-203367" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-4.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-4" width="960" height="960" /></p> <p><strong>Đoạn đường hoa giấy</strong></p> <p style="text-align: justify;">Mình không nhớ chính xác địa chỉ nhưng đoạn đường này nằm gần <a href="https://www.ivivu.com/khach-san-vung-tau/binh-an-village-vung-tau?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">“Làng Bình An”</a>. Mình đi vào giữa tháng 4, hoa hồng rực cả một góc đường đẹp mê luôn. Quãng đường này khoảng 100m thôi nhưng đủ để chụp choẹt đủ kiểu và tha hồ có ảnh đẹp rồi ấy.</p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203372" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-5.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-5" width="960" height="720" /> <img class="alignnone size-full wp-image-203373" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-6.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-6" width="960" height="844" /></p> <p><strong><a href="https://www.ivivu.com/khach-san-vung-tau/khach-san-imperial-vung-tau?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Imperial Vũng Tàu</a></strong></p> <p>Toạ lạc tại 159 đường Thuỳ Vân, khách sạn có vẻ ngoài cực kì sang trọng và đặc biệt là thiết kế tượng quá ư là sang chảnh. Quanh khu khách sạn còn có cả cầu thang đi lên cao chụp ra thành phố đẹp thôi rồi.</p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203375" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-8.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-8" width="761" height="960" /></p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203377" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-10.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-10" width="768" height="960" /><img class="alignnone size-full wp-image-203376" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-9.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-9" width="768" height="960" /></p> <h4 style="text-align: center;"><div class="ltt-contentbox white"><span class="icon star"></span>Click đặt ngay<a href="https://www.ivivu.com/khach-san-vung-tau/khach-san-imperial-vung-tau?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank"> Imperial Vũng Tàu</a> với mức giá ưu đãi cực tốt từ iVIVU.com</div></h4> <p><strong>Đường cây khô Hàn Quốc</strong></p> <p style="text-align: justify;">Đoạn đường này gần đường hoa giấy. Kiểu hết hoa giấy là tiếp theo đến đoạn này luôn. Cây khô, cạnh biển. Bạn nên chụp buổi chiều tầm 2,3h để ánh sáng thuận nhất. Đoạn đường này rất ít xe cộ nên chụp ảnh vô cùng lý tưởng.</p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203384" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-16.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-16" width="768" height="960" /></p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203379" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-12.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-12" width="960" height="719" /></p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203380" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-13.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-13" width="720" height="960" /></p> <p><strong>Cây bông đoạn đường lên ngọn Hải Đăng</strong></p> <p style="text-align: justify;">Cây bông này là khi đang đi trên đường tới ngọn Hải Đăng mình vô tình nhìn thấy, cây xinh to và nằm ngay khúc cua đẹp nữa. Chỉ có một cây trên cả đoạn đường đó thôi nên các bạn dừng lại chụp ngay nhé không phải tìm thêm đâu. Đoạn đường lên ngọn Hải Đăng cũng nhiều góc đẹp lắm tha hồ cho các bạn chụp.</p> <p style="text-align: center;"><img class="alignnone size-full wp-image-203385" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-17.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-17" width="768" height="960" /> <img class="alignnone size-full wp-image-203386" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-18.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-18" width="960" height="719" /></p> <p><strong>Rừng thông và xương rồng hồ Tràm</strong></p> <p style="text-align: justify;">Rừng thông này nằm trên đường từ thành phố đi Hồ Tràm, nằm bên tay phải lúc các bạn đi, cách Hồ Tràm khoảng 10km đằng sau rừng thông có cả bãi biển. Đặc biệt, gần bãi cát ở Hồ Tràm cũng có mấy khóm xương rồng nhìn hay hay nên mình tạt vào chụp ảnh luôn.</p> <p style="text-align: justify;"><img class="alignnone size-full wp-image-203381" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-14.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-14" width="960" height="729" /> <img class="alignnone size-full wp-image-203382" src="https://cdn3.ivivu.com/2017/04/top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-15.jpg" alt="top-6-dia-diem-chup-anh-song-ao-o-vung-tau-bao-dep-ivivu-15" width="960" height="723" /></p> <p style="text-align: justify;">Đó là các điểm mình đã dành hơn nửa ngày để đi, chia sẻ với mọi người mong các bạn đến <em><strong>du lịch Vũng Tàu</strong></em> và sẽ có những bức hình sống ảo lung linh nhất mang về. Tuy nhiên, lưu ý thêm là đường khá xa nên các bạn có thể chọn ô tô nếu muốn tiết kiệm thời gian chụp ảnh.</p> <div class="ltt-contentbox white"><span class="icon star"></span></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ KHÁCH SẠN VŨNG TÀU GIÁ TỐT</strong></h4> <p><strong><a href="https://www.ivivu.com/khach-san-vung-tau/khach-san-imperial-vung-tau?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Khách sạn Imperial Vũng Tàu</a></strong></p> <p><strong><a href="https://www.ivivu.com/blog/2017/04/2n1d-o-pullman-vung-tau-5-sao-buffet-bbq-hai-san-buffet-sang-chi-1-888-000-dong/" target="_blank">Pullman Vũng Tàu</a></strong></p> <p><strong><a href="https://www.ivivu.com/khach-san-vung-tau/the-grand-ho-tram-strip?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">The Grand Hồ Tràm Strip Vũng Tàu </a></strong></p> <p></div> <div class="ltt-contentbox white"><span class="icon star"></span></p> <h4><strong>IVIVU.COM GỢI Ý MỘT SỐ TOUR VŨNG TÀU HẤP DẪN</strong></h4> <p><strong><a href="https://www.ivivu.com/du-lich/tour-vung-tau-1n/289?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tour Vũng Tàu 1N </a></strong></p> <p><strong><a href="https://www.ivivu.com/du-lich/tour-vung-tau-2n1d-vung-tau-long-hai/421?utm_source=blog_ivivu&utm_medium=post_link&utm_campaign=internal" target="_blank">Tour Vũng Tàu 2N1D: Vũng Tàu – Long Hải</a></strong></p> <p></div> <p style="text-align: right;"><strong>Theo Dương Thùy Thái</strong></p> <p style="text-align: justify;"><strong><div class="ltt-contentbox white"><span class="icon add"></span></strong></p> <p style="text-align: justify;"><strong> Xem thêm các bài viết:</strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2014/10/du-lich-vung-tau-cam-nang-tu-a-den-z/" target="_blank">Du lịch Vũng Tàu: Cẩm nang từ A đến Z</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2017/03/danh-sach-cac-quan-an-ngon-o-vung-tau-nen-ghi-nho-cho-chuyen-du-lich-toi/" target="_blank">Danh sách các quán ăn ngon ở Vũng Tàu nên ghi nhớ cho chuyến du lịch tới</a></strong></p> <p style="text-align: justify;"><strong><a href="https://www.ivivu.com/blog/2017/02/danh-sach-cac-quan-ca-phe-dep-o-vung-tau-save-ve-de-di-dan-cho-ca-nam/" target="_blank">Danh sách các quán cà phê đẹp ở Vũng Tàu ‘save’ về để đi dần cho cả năm</a></strong></p> <p><strong></div>', 'Tuy không quá nhộn nhịp và nhiều chỗ vui chơi nhưng nếu muốn có một bộ ảnh độc đáo thì Vũng Tàu là sự lựa chọn hoàn hảo cho bạn.', 0x31, '2017-04-08', '2017-04-08', '3', '2017-05-06', '2017-04-08', 'nhung-dia-danh-in-dau-chan-anh-hung-xa-dieu-quach-tinh-ivivu-5.jpg', NULL);

