-- Đang đổ dữ liệu cho bảng `chinhsach`
--

INSERT INTO `chinhsach` (`IDChinhSach`, `IDKhachSan`, `NhanPhong`, `TraPhong`, `DiChuyen`, `HoatDong`, `HuongDan`, `PhuThu`) VALUES
(1, 1, '14:03:00.000000', '12:00:00.000000', '\r\n- Từ sân bay: di chuyển bằng taxi đến bến tàu Ninh Vân Bay khoảng 30 phút, sau đó lên tàu đến Khu nghỉ dưỡng khoảng 20 phút.\r\n\r\n- Từ trung tâm thành phố Nha Trang: di chuyển bằng taxi đến bến tàu Ninh Vân Bay khoảng 1 giờ đồng hồ, sau đó lên tàu đến Khu nghỉ dưỡng khoảng 20 phút.\r\n\r\n', '\nKhu nghỉ dưỡng có nhiều hoạt động giải trí & thể thao: tô tượng, vẽ tranh cát, thả diều, chèo thuyền Kayak, câu cá, leo núi,...\n\n', 'Quý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\nKhông cho phép trẻ em dưới 12 tuổi ở phòng Hill Rock Villas vì lý do an toàn.', '- Miễn phí tối đa trẻ dưới 3 tuổi ở chung phòng với bố mẹ\r\n\r\n- Trẻ từ 3-12 phụ thu ăn sáng 660.000 VND/trẻ/đêm hoặc phụ thu giường phụ 1.320.000 VNĐ/giường/đêm\r\n\r\n- Trẻ từ 13 tuổi trở lên được xem như người lớn, phụ thu giường phụ 1.320.000 VNĐ/giường/đêm\r\n\r\n*Lưu ý:\r\n\r\n- Nôi trẻ em sẽ được sắp xếp khi có yêu cầu (tùy vào tình trạng sẵn có)\r\n\r\n- Không cho phép trẻ em dưới 12 tuổi ở phòng Hill Rock Pool Villas vì lý do an toàn\r\n\r\n- Giường phụ không kê được ở phòng Hill Rock Pool Villas'),
(2, 2, '15:00:00.000000', '12:00:00.000000', '\r\nCách sân bay Quốc Tế Đà Nẵng 21 phút bằng xe hơi\r\n\r\n', '', '\r\n- Tối đa 1 trẻ em dưới 6 tuổi được ngủ chung phòng với bố mẹ không tính phí.\r\n\r\n- Trẻ từ 6 - 11 tuổi phụ thu ăn sáng 180.000 VND/trẻ/đêm.\r\n\r\n- Trẻ từ 12 tuổi trở lên tính như người lớn, phụ thu giường phụ 960.000 VND/giường/đêm.\r\n\r\n- Tối đa kê thêm 1 giường phụ/Villa.\r\n\r\n', '\r\n- Compulsory Dinner New Year’s Eve (31st Dec 2017): 1.350.000 VND/người lớn, 675.000/trẻ em (từ 6 - 11 tuổi).\r\n\r\n'),
(3, 11, '14:00:00.000000', '12:00:00.000000', '\r\nKhu nghỉ dưỡng cách sân bay 25 lái xe', '\r\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\n- Miễn phí tiền phòng cho hai trẻ em dưới 5 tuổi ở chung phòng với bố mẹ (không đặt giường phụ).\r\n\r\n- Phụ thu cho trẻ từ 5–11 tuổi 290.000 VNĐ/trẻ/đêm (có ăn sáng, không đặt giường phụ).\r\n\r\n- Trẻ em 12 tuổi trở lên tính phụ thu như người lớn, phụ thu giường phụ 1.000.000 VNĐ/giường/đêm\r\n\r\n*Mỗi phòng chỉ kê được tối đa 1 giường phụ (trừ phòng Family không kê được giường phụ)\r\n\r\n', '\nTối ngày 24/12/2017 (Christmas Eve) hoặc Trưa 25/12/2017(Christmas Date)\n\nTối 31/12/2017 (New Year Eve) hoặc Trưa 01/01/2018 (New Year Date)\n\nGiá 1.800.000 VNĐ/người lớn, 900.000 VNĐ/trẻ em cho trẻ từ 6- 11 tuổi. Trẻ dưới 6 tuổi miễn phí '),
(4, 12, '14:00:00.000000', '12:00:00.000000', '\r\nTừ sân bay: di chuyển bằng taxi đến bến tàu Ninh Vân Bay khoảng 30 phút, sau đó lên tàu đến Khu nghỉ dưỡng khoảng 20 phút.\r\n\r\nTừ trung tâm thành phố Nha Trang: di chuyển bằng taxi đến bến tàu Ninh Vân Bay khoảng 1 giờ đồng hồ, sau đó lên tàu đến Khu nghỉ dưỡng khoảng 20 phút.\r\n\r\n', '\r\nQuý khách vui lòng xuất trình CMND (Hộ chiếu) và phiếu xác nhận đặt phòng khi làm thủ tục nhận phòng. (Mang theo Chứng nhận Đăng Ký Kết Hôn giữa người nam ngoại quốc và người nữ Việt Nam khi sử dụng chung phòng).\r\n\r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\n- Miễn phí  ăn sáng trẻ em 0 -11 tuổi khi sử dụng chung giường với cha mẹ.\r\n\r\n- Trẻ em từ 12 tuổi trở lên phụ thu như người lớn: 743.000 VNĐ/khách/đêm.\r\n\r\nLưu ý: Vì lý do an toàn quý khách có trẻ em đi kèm dưới 12 tuổi sẽ không được ở Hill Top Pool Villa.\r\n\r\n', '\r\nPhụ thu 2.500.000 VNĐ mỗi đêm cho Water Pool Villa số 5 nếu yêu cầu giữ đúng phòng này ( ngoại trừ mùa cao điểm ưu tiên cho những đặt phòng ở ít nhất 3 đêm)\r\n\r\n'),
(5, 5, '14:00:00.000000', '12:00:00.000000', '\r\n* Hiện nay, tại Hà Nội và thành phố Hồ Chí Minh có nhiều chuyến bay thẳng đến sân bay Cam Ranh – Khánh Hòa của các hãng hàng không nội địa như: Vietjet Air, Jetstar Paciffic, Vietnam Airlines. \r\n\r\nTừ sân bay Cam Ranh đến Khách sạn Havana Nha Trang khoảng 36km, bạn có thể di chuyển bằng xe bus trung chuyển có giá khoảng 60.000 đồng hoặc bằng taxi có giá từ 300.000 – 450.000 VNĐ đồng/lượt.\r\n\r\n*Xe khách:\r\n\r\n+Tuyến Sài Gòn – Nha Trang: Bạn có thể lựa chọn một số nhà xe uy tín ở Nha Trang như Phương Nam, Hà Linh, Nam Phương, Phương Trang, Hạnh cafe, TM Brother, Ngọc Trang.\r\n\r\n+Tuyến Hà Nội – Nha Trang: Bạn có thể lựa chọn một số nhà xe uy tín như Hoàng Long, Sinh Cafe (open tour), Mai Linh.\r\n\r\n* Ga Nha Trang cách khách sạn khoảng 2km.\r\n\r\n', '\r\nKhách sạn nằm trên đường Trần phú - trục đường chính của Khách du lịch, cách bờ biển 200m, chợ Đầm khoảng 2.2 km, cách Tháp Bà Ponagar 4.8km và tắm bùn khoảng 20 phút lái xe. \r\n\r\n', '\r\nPhụ thu ăn sáng: 770.000 VND/phòng/đêm (dành cho 2 người lớn)\r\n\r\nPhụ thu trẻ em:\r\n\r\n- Miễn phí trẻ em dưới 6 tuổi khi sử dụng chung giường với cha mẹ.\r\n\r\n- Trẻ em từ 6- dưới 12 tuổi sử dụng chung giường với cha mẹ và phụ thu 110.000 VNĐ/trẻ em/đêm (không ăn sáng). Hoặc phụ thu 374.000 VNĐ/trẻ em/đêm (bao gồm ăn sáng)\r\n\r\n- Trẻ em từ 12 tuổi trở lên tính phụ thu như người lớn.\r\n\r\nExtra bed:\r\n\r\n- Phụ thu: 440.000 VNĐ/người/đêm (không bao gồm ăn sáng).\r\n\r\n- Phụ thu: 763.000 VNĐ/người/đêm (bao gồm ăn sáng).\r\n\r\n', '\r\nGiá phòng chưa bao gồm Bữa ăn tối bắt buộc 31/12/2017:\r\n\r\n+ Người lớn (từ 12 tuổi trở lên): 1.600.000VNĐ/người\r\n\r\n+ Trẻ em (4->11 tuổi): 50% giá người lớn\r\n\r\n'),
(6, 6, '14:00:00.000000', '12:00:00.000000', '\r\n- Máy bay: Khách hàng có thể đến Nha Trang bằng máy bay từ các thành phố lớn tại Việt Nam & nước ngoài. Khách sạn cách sân bay quốc tế Cam Ranh 10km (12 đi xe), khách hàng có thể đến khách sạn bằng taxi hoặc xe bus của Khách sạn (có tính phí)\r\n\r\n- Xe lửa: Khách hàng có thể đến Nha Trang bằng xe lửa từ Hồ Chí Minh. Khách sạn cách ga Nha Trang 45km (1h đi xe), khách hàng có thể đến khách sạn bằng taxi hoặc xe bus của thành phố đến sân bay & đi thêm taxi đến khách sạn\r\n\r\n- Xe khách: Có nhiều hãng xe nổi tiếng xuất phát từ Hồ Chí Minh & dừng tại bến của hãng (trong trung tâm Nha Trang). Khách sạn cách trung tâm Nha Trang 45km (1h đi xe), khách hàng có thể đến khách sạn bằng taxi hoặc xe bus của thành phố đến sân bay & đi thêm taxi đến khách sạn\r\n\r\n', '\r\n- Cách trung tâm thành phố Nha Trang 45km (1h đi xe): Chợ Đầm, Quảng trường,...\r\n\r\n- Cách Vinpearl Land 43km (50 đi xe)\r\n\r\n', '\r\nQuý khách vui lòng xuất trình CMND (Hộ chiếu) và phiếu xác nhận đặt phòng khi làm thủ tục nhận phòng.\r\n\r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\nSố lượng khách tối đa lưu trú tại Villa:\r\n\r\n- Villa 2 phòng ngủ: 4 người lớn & 2 trẻ em\r\n\r\n- Villa 3 phòng ngủ: 6 người lớn & 3 trẻ em\r\n\r\n- Villa 4 phòng ngủ: 8 người lớn & 4 trẻ em\r\n\r\nChính sách phụ thu:\r\n\r\n- Trẻ em dưới 4 tuổi: Miễn phí\r\n\r\n- Trẻ em từ 4-12 tuổi: Ngủ chung giường có sẵn\r\n\r\n   + Gói Ăn sáng: 220.000 VND/trẻ/đêm\r\n\r\n   + Gói Ăn 3 bữa: 715.000 VND/trẻ/đêm\r\n\r\n   + Gói Inclusive: 2.200.000 VND/trẻ/đêm\r\n\r\n- Trẻ từ 12 tuổi trở lên tính như người lớn\r\n\r\n'),
(7, 7, '14:00:00.000000', '12:00:00.000000', '\r\n- Cách Sân bay Quốc tế Đà Nẵng 5.2km (10 đi xe).\r\n\r\n- Cách bãi biển 600m (7 đi bộ).\r\n\r\n- Cách cầu Rồng 2km (4 đi xe).\r\n\r\n', '\r\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\n- Miễn phí 1 trẻ em dưới 6 tuổi ngủ chung phòng với bố mẹ.\r\n\r\n- Trẻ từ 6 - 11 tuổi phụ thu ăn sáng 99.000 VND/trẻ/đêm.\r\n\r\n- Trẻ từ 12 tuổi trở lên tính như người lớn, phụ thu giường phụ 605.000 VND/giường/đêm.\r\n\r\n- Giường phụ chỉ kê được ở các phòng Elite Deluxe, Grand Deluxe, Studio, Lavish Studio, Thera Suite, Santorin Suite và Duplex Residence\r\n\r\n', '\r\n- Đưa đón sân bay bằng xe limousine theo lịch trình.\r\n\r\n(Lịch đón từ sân bay: 9h45, 16h, 18h30\r\n\r\nLịch đưa từ resort: 9h15, 15h30, 18h)\r\n\r\n- Miễn phí sử dụng mini bar.\r\n\r\n- Miễn phí dùng trà chiều tại Elia Lounge.\r\n\r\n** Đặt loại phòng Studio Room trở lên, khách hàng sẽ được hưởng thêm ưu đãi RISE Privilege Club như sau:\r\n\r\n- Miễn phí giặt ủi cho 2 món/phòng ngủ/đêm.\r\n\r\n- Khi sử dụng dịch vụ tại nhà hàng La Maison:\r\n\r\n + Giảm 10% trên thực đơn A La Carte.\r\n\r\n + Bữa trưa & bữa tối theo thực đơn đặc biệt RISE Delighted.\r\n\r\n + Cocktail trước bữa tối.\r\n\r\n + Miễn phí 3 món nước uống (nước ngọt, nước trái cây, trà) & cafe mỗi bữa ăn.\r\n\r\n'),
(8, 8, '14:00:00.000000', '12:00:00.000000', '\r\n- Cách trung tâm thành phố 2km.\r\n\r\n- Cách Sân bay Quốc Tế Đà Nẵng 5 phút đi xe.\r\n\r\n', '\r\n- Khách Honeymoon: miễn phí nâng hạng phòng lên loại cao cấp hơn (phụ thuộc vào tình trạng phòng của khách sạn & không kết hợp các khuyến mãi khác).\r\n\r\n- Miễn phí 02 chai nước suối trong phòng mỗi ngày.\r\n\r\n- Miễn phí sử dụng dịch vụ tại phòng GYM, xông hơi và hồ bơi.\r\n\r\n- Miễn phí sử dụng xe buýt đi Trung tâm Thành phố, Cổ viện Chàm, Chợ Hàn, bãi biển và Công viên Châu Á (6 chuyến/ngày) \r\n\r\n*Tiện ích riêng cho phòng Deluxe & Suite:\r\n\r\n- Ưu đãi thủ tục Nhận phòng và Trả phòng tại sảnh Privilege tại tầng 21.\r\n\r\n- Thưởng thức các loại cocktail và thức ăn nhẹ từ 5:00pm đến 7:00pm tại tầng 21.\r\n\r\n- Thoải mái thưởng thức trong ngày các loại trái cây, bánh ngọt, trà, cà phê và nước ngọt các loại tại tầng 21.\r\n\r\n', '\r\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách).\r\n\r\n', '\r\n- Miễn phí cho tối đa 2 trẻ em dưới 12 tuổi ở cùng phòng với ba mẹ.\r\n\r\n- Người lớn thứ 3 phụ thu giường phụ 847.000 VND.\r\n\r\n'),
(9, 9, '14:00:00.000000', '11:00:00.000000', '\r\n- Cách bãi tắm Phạm Văn Đồng 100m.\r\n\r\n- Cách cầu Rồng 2.5km.\r\n\r\n- Cách sân bay Quốc tế Đà Nẵng 5.8km.\r\n\r\n', '\r\n- 1 đêm nghỉ ở phòng Light\r\n\r\n- 1 bữa tối set menu Việt Nam tại nhà hàng Fishca\r\n\r\n- 2 đơn vị nước uống tại Top Bar\r\n\r\n- 1 liệu trình 60 dành cho mỗi khách tại Spice Spa\r\n\r\n- 1 lần sử dụng shuttle bus đi Bà Nà/Hội An\r\n\r\n- Đưa đón sân bay miễn phí\r\n\r\n*Giai đoạn áp dụng: đến hết 30/09 (không áp dụng giai đoạn lễ 03/05 - 05/05, 01/09 - 03/09)\r\n\r\n*Điều kiện: đặt tối thiểu 2 đêm\r\n\r\n', '\r\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\n- Miễn phí tối đa 2 trẻ em dưới 6 tuổi khi sử dụng chung giường với cha mẹ.\r\n\r\n- Trẻ em từ 6-11 tuổi sử dụng chung giường với cha mẹ phụ thu 150.000 VNĐ/trẻ em/đêm (không bao gồm ăn sáng) hoặc phụ thu 300.000 VNĐ/trẻ em/đêm bao gồm giường phụ (không bao gồm ăn sáng).\r\n\r\n- Trẻ em từ 12 tuổi trở lên bắt buộc kê thêm giường phụ và phụ thu 500.000 VNĐ/trẻ em/đêm (không bao gồm ăn sáng).\r\n\r\n- Phụ thu ăn sáng (áp dụng đến 30/10/2017):\r\n\r\n+ Trẻ từ 6 -11 tuổi phụ thu: 100.000 VNĐ/trẻ em/ngày\r\n\r\n+ Trẻ từ 12 trở lên phụ thu: 200.000 VNĐ/người/ngày\r\n\r\nExtra Bed: 500.000 VNĐ/người/đêm ( không bao gồm ăn sáng)\r\n\r\n* Extrabed áp dụng cho các loại phòng Light, Light Plus, Delight\r\n\r\n'),
(10, 10, '14:00:00.000000', '11:00:00.000000', '\r\n- Cách cầu Rồng 4.9km.\r\n\r\n- Cách vòng quay Mặt Trời 3.6km.\r\n\r\n- Cách sân bay Quốc tế Đà Nẵng 7.4km.\r\n\r\n', '\r\n*Điều kiện:\r\n\r\n- Khuyến mãi dành cho người Việt Nam, Việt kiều và người nước ngoài sống và làm việc tại Việt Nam\r\n\r\n- Áp dụng cho đặt phòng tối thiểu 2 đêm trở lên\r\n\r\n*Chương trình bao gồm:\r\n\r\n- Ăn sáng tự chọn hàng ngày\r\n\r\n- Miễn phí hoa quả tươi, bình đun nước, trà và café tự pha trong phòng\r\n\r\n- Miễn phí đưa đón khách 2 chiều tại sân bay Đà Nẵng\r\n\r\n- Xe đưa đón đi tham quan phố cổ Hội An hàng ngày (theo lịch trình)\r\n\r\n- Miễn phí các tiện ích: wifi, bể bơi, phòng tập thể hình, phòng tắm hơi và sauna, phòng chơi game, CLB trẻ em và tham gia miễn phí các lớp Taichi vào các buổi sáng\r\n\r\n- Giảm 10% cho bữa tối tự chọn, các trò chơi dưới nước và SPA, 10% các dịch vụ tham quan tour\r\n\r\nĐặc biệt: Lưu trú từ 3 đêm trở lên tặng phiếu ẩm thực trị giá VND 300,000/người lớn/ngày\r\n\r\n*Chính sách phụ thu (***):\r\n\r\n- Mỗi phòng ngủ chỉ bao gồm tối đa 02 người lớn và 02 trẻ em (dưới 12 tuổi) hoặc 3 người lớn\r\n\r\n- Miễn phí đối với trẻ dưới 5 tuổi, sử dụng chung giường với bố mẹ\r\n\r\n- Trẻ từ 5-11 tuổi phụ thu ăn sáng 682.000 VND/trẻ/đêm hoặc 1.139.000 VND/giường phụ/đêm\r\n\r\n- Trẻ từ 11 tuổi trở lên được xem như là người lớn, phụ thu giường phụ 2.030.000 VND/giường phụ/đêm (phòng Ocean Studio Suite không kê được giường phụ)\r\n\r\n', '\r\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn. \r\n(Khách sạn có thể sẽ yêu cầu Quý khách số thẻ tín dụng hoặc tiền đặt cọc cho các dịch vụ phát sinh trong thời gian lưu trú của Quý khách)\r\n\r\n', '\r\n- Tối đa 3 người lớn hoặc 2 người lớn & 2 trẻ em dưới 12 tuổi được ở chung phòng.\r\n\r\n- Phòng Family bao gồm 2 phòng thông nhau, tối đa 2 người lớn & 3 trẻ em dưới 16 tuổi được ở chung phòng.\r\n\r\n- Trẻ em từ 4-12 tuổi sử dụng chung giường với cha mẹ và phụ thu ăn sáng 308.000 VNĐ/trẻ em/đêm.\r\n\r\n- Trẻ em từ 12 tuổi trở lên tính phụ thu như người lớn.\r\n\r\nExtra Bed: 1.281.500 VNĐ/giường/đêm.\r\n\r\n');

