INSERT INTO `khachsan` (`IDKhachSan`, `TenKhachSan`, `IDLoaiKhachSan`, `DiaChi`, `IDKhuVuc`, `SoPhong`, `DanhGia`, `ThongTin`, `LienHe`, `minprice`, `IDNguoiQuanLy`) VALUES
(1, 'L’Alyana Ninh Vân Bay Nha Trang   ( Biệt thự An Lâm Ninh Vân Bay Nha Trang cũ )', 1, 'Vịnh Ninh Vân, Việt Nam, Việt Nam', '2', 30, 0, '<p><em>*Khởi h&agrave;nh từ HCM:</em> 6,699,000 VND/kh&aacute;ch.</p><p><br></p><p><em>*Khởi h&agrave;nh từ HN:</em> 7,799,000 VND/kh&aacute;ch.</p><p><br></p><p><strong>Gi&aacute; bao gồm:</strong></p><p><br></p><p>- V&eacute; m&aacute;y bay khứ hồi</p><p><br></p><p>- 2 đ&ecirc;m nghỉ dưỡng ở Hill Rock Pool Villa</p><p><br></p><p>- T&agrave;u cao tốc đưa đ&oacute;n l&uacute;c nhận &amp; trả ph&ograve;ng</p><p><br></p><p>- Ăn s&aacute;ng buffet</p><p><br></p><p>- Hằng ng&agrave;y c&oacute; 1 chuyến t&agrave;u &amp; xe đưa kh&aacute;ch đến trung t&acirc;m th&agrave;nh phố &amp; ngược lại (theo lịch tr&igrave;nh)</p><p><br></p><p>- Dịch vụ quản gia ri&ecirc;ng 24/24</p><p><br></p><p>- Tr&aacute;i c&acirc;y tươi &amp; tr&agrave; chiều hằng ng&agrave;y tại Villa</p><p><br></p><p>- C&aacute;c hoạt động thể thao: ch&egrave;o thuyền Kayak, c&acirc;u c&aacute;, leo n&uacute;i,...</p><p><br></p><p>- Sử dụng c&aacute;c tiện &iacute;ch: wifi, hồ bơi, ph&ograve;ng tập gym,...</p><p><br></p><p><strong>Điều kiện &aacute;p dụng:</strong></p><p><br></p><p>- Đặt tối thiểu 2 kh&aacute;ch/ph&ograve;ng</p><p><br></p><p>-&nbsp;Giai đoạn lưu tr&uacute;: đến hết 23/12/17 (kh&ocirc;ng &aacute;p dụng giai đoạn Lễ 01/09 - 03/09)</p><p><br></p><p><br></p><p><br></p><h3 class="bundle__remain_date"><br></h3>', NULL, 1000000, 8),
(2, 'The Ocean Villas Đà Nẵng', 1, 'Đường Trường Sa, Phường Hòa Hải, Quận Ngũ Hành Sơn, TP.Đà Nẵng, Việt Nam', '1', 140, 0, '<p><strong>The Ocean Villas Danang</strong> nhìn ra bãi biển thuộc bờ biển miền Trung Việt Nam và cung cấp các biệt thự sang trọng đi kèm Wi-Fi miễn phí cùng hồ bơi riêng. Chỗ ở này có các ban công riêng nhìn ra sân gôn hoặc bờ biển. Du khách được lui tới bãi biển tư nhân và sử dụng các tiện nghi spa.</p>  <p>Các phòng thoáng mát và rộng rãi tại đây có sàn gỗ cùng cửa sổ kính suốt từ trần đến sàn. Các biệt thự có bếp ngoài trời và khu vực ăn uống. Phòng khách mở ra hiên tắm nắng, khu vườn và bể ngâm riêng.</p>  <p>The Ocean Villas nằm bên cạnh sân gôn Danang Golf Club do Greg Norman thiết kế, cách Ngũ Hành Sơn 5 phút lái xe và cách Phố cổ Hội An, Di sản Thế giới được UNESCO công nhận, 15 phút lái xe. Sân bay Quốc tế Đà Nẵng cách đó 20 phút lái xe.</p>', NULL, 1500000, 3),
(5, 'Khu nghỉ dưỡng Mia Nha Trang', 1, 'Đường Bãi Dông, Cam Hải Đông, Cam Lâm, TP.Nha Trang, Việt Nam', '2', 110, 0, '<p>*Garden View Condo: 2,860,000 VNĐ/phòng/đêm</p>  <p>*Garden Villa: 3.699.000 VNĐ/phòng/đêm</p>  <p>*Beach Front Villa: 4,999,000 VNĐ/phòng/đêm</p>  <p><strong>Giá bao gồm:</strong></p>  <p>- Ăn sáng Buffet</p>  <p>- Xe đưa đón vào trung tâm theo lịch trình</p>  <p>- Sử dụng các tiện ích: hồ bơi, phòng tập thể dục, wifi, ...</p>  <p><strong>Điều kiện áp dụng:</strong></p>  <p>- Đặt tối thiểu 2 đêm</p>  <p>- Xe đưa đón sân bay 139.000 VND/khách/chiều (thanh toán trực tiếp tại khách sạn)</p>  <p>- Thứ sáu, thứ bảy: Phụ thu 400.000VND/phòng/đêm.</p>  <p><em><strong>* Liên hệ: 19001870 để được giá ưu đãi.</strong></em></p>  <p>Giai đoạn lưu trú: Đến 30/06/2017</p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>48</b> ngày</h3>', NULL, 1005000, NULL),
(6, 'Khu nghỉ dưỡng Six Senses Ninh Vân Bay Nha Trang', 2, 'Vịnh Ninh Vân, Ninh Hòa, Khánh Hòa, Việt Nam', '2', 140, 0, '<p><strong>Combo bao gồm:</strong></p>  <p>- 1 đêm nghỉ dưỡng tại phòng Hill Top Pool Villa</p>  <p>- Ăn sáng Buffet</p>  <p>- Tặng 1 bữa ăn trưa/tối cho khách </p>  <p>- Đưa đón sân bay Cam Ranh đến khu nghỉ dưỡng</p>  <p>- Hoạt động thể thao: tập thái cực, chèo thuyền kayak, leo núi, khu vui chơi trẻ em, ...</p>  <p><strong>Điều kiện áp dụng:</strong></p>  <p>-<em> Đặt trước 7 ngày, tối thiểu 2 khách, chỉ áp dụng cho khách Việt Nam và Khách nước ngoài sinh sống - làm việc tại Việt Nam.</em></p>  <p><em>- Phụ thu người lớn thứ 3: ăn sáng 743.000 VND/người/đêm ( Sử dụng Sofa Bed hoặc kê 1 nệm phụ miễn phí ), đưa đón sân bay 2.229.000 VND/người/đêm, ăn tối 858.000 VND/người/đêm.</em></p>  <p><em>- Chương trình không áp dụng cho giai đoạn tháng 7 và tháng 8.</em></p>  <p><em><strong>- </strong>Áp dụng đến<strong> </strong>31/10/17</em></p>  <p> </p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>172</b> ngày</h3>', NULL, 1500000, NULL),
(7, 'Khách sạn  Havana Nha Trang', 1, '38 Trần Phú, phường Lộc Thọ, Nha Trang, Việt Nam', '2', 140, 0, '<p><strong>*Combo bao gồm:</strong></p>  <p>- 2 đêm nghỉ dưỡng tại phòng Deluxe Ocean View</p>  <p>- 1 tour du ngoạn đảo vịnh Nha Trang:</p>  <p>   + Xe và tàu đưa đón khách </p>  <p>   + Bơi, ngắm san hô và cá cảnh bằng kính lặn</p>  <p>   + Dùng cơm trưa trên tàu</p>  <p>   + ...</p>  <p>- Sử dụng các tiện ích: wifi, hồ bơi, phòng tập gym,...</p>  <p><em>*Giai đoạn lưu trú:</em> tới hết 31/08/2017. </p>  <p><em>*Lưu ý:</em> Tour du ngoạn đảo vịnh Nha Trang không bao gồm vé tham quan & các dịch vụ khác.</p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>19</b> ngày</h3>', NULL, 1500000, NULL),
(8, 'Vinpearl Nha Trang Long Beach Resort & Villas', 1, 'Lô D6B2 VÀ D7A1 thuộc khu 2 - khu du lịch bán đảo Cam Ranh, huyện Cam Lâm, Khánh Hòa, Việt Nam', '2', 140, 0, '- 1 đêm lưu trú tại Villa\r\n\r\n- Ăn 3 bữa\r\n\r\n- Xe đưa đón sân bay theo lịch trình\r\n\r\n- Trà chiều trong khung giờ 15:00 - 17:00 (bao gồm trà, cà phê, bánh ngọt và một số món ăn nhẹ)\r\n\r\n- Quầy bar phục vụ nước uống cả ngày từ 07:00 - 22:00 (bao gồm trà, cà phê, các loại nước giải khát và thức uống có cồn)\r\n\r\n- City Tour mỗi ngày (miễn phí xe đưa đón, chi phí tự túc)\r\n\r\n- Shuttle bus đưa đi Vinpearl Land & vào trung tâm theo lịch trình mỗi ngày\r\n\r\n- Miễn phí sử dụng fitness center, các dụng cụ phục vụ chơi tennis, bóng chuyền bãi biển, pétanque...trong suốt thời gian lưu trú\r\n\r\n- Yêu cầu: đặt tối thiểu 2 đêm\r\n\r\nGiai đoạn lưu trú: 29/04/2017 - 15/06/2017\r\n\r\nPromotion Còn 19 ngày', NULL, 1500000, NULL),
(9, 'Khu nghỉ dưỡng Risemount Đà Nẵng', 1, '120 Nguyễn Văn Thoại, P. Mỹ An, Q. Ngũ Hành Sơn, Tp. Đà Nẵng, Việt Nam', '1', 140, 0, '<p><em>* Khởi hành từ Hà Nội/HCM:</em> 4.499.000/khách</p>  <p><strong>Giảm đến 500.000đ/khách cho giai đoạn lưu trú trong tuần! </strong></p>  <p>(Số lượng có hạn, chương trình kết thúc khi hết chỗ)</p>  <p><strong>Giá bao gồm:</strong></p>  <p>- Vé máy bay khứ hồi đi Đà Nẵng (bao gồm 7kg hành lý xách tay)</p>  <p>- 02 đêm nghỉ dưỡng tại phòng Superior</p>  <p>- Ăn sáng Buffet</p>  <p>- 1 bữa ăn tối Vietnamese set menu</p>  <p>- Đưa đón sân bay bằng xe limousine theo lịch trình</p>  <p>(Lịch đón từ sân bay: 9h45, 16h, 18h30</p>  <p>Lịch đưa từ resort: 9h15, 15h30, 18h)</p>  <p>- Miễn phí sử dụng mini bar trong phòng</p>  <p>- Miễn phí sử dụng trà chiều tại Elia Lounge (14h - 16h)</p>  <p><em>*Giai đoạn lưu trú: </em>đến hết 30/09/2017 (không áp dụng giai đoạn 01/09 - 02/09 có phụ thu thêm)</p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>141</b> ngày</h3>', NULL, 1500000, NULL),
(10, 'Khách Sạn Grand Mercure Đà Nẵng', 1, 'Lô A1 Khu biệt thự Đảo Xanh, Quận Hải Châu, TP.Đà Nẵng, Việt Nam', '1', 140, 0, '<p><em>* Khởi hành từ Hà Nội/HCM:</em> 4.499.000/khách </p>  <p><strong>Giá bao gồm:</strong></p>  <p>- Vé máy bay khứ hồi đi Đà Nẵng (bao gồm 7kg hành lý xách tay)</p>  <p>- 02 đêm nghỉ dưỡng tại phòng Superior</p>  <p>- Ăn sáng Buffet</p>  <p>- Đưa đón sân bay trong khung giờ 7h - 19h</p>  <p>- Xe đưa đón đến Bảo tàng Chăm, Chợ Hàn, Bãi biển, công viên Á Châu theo lịch trình</p>  <p>- Giảm 15% phí sử dụng Spa, nhà hàng, giặt ủi tại khách sạn</p>  <p>- Miễn phí sử dụng các tiện ích: hồ bơi, wifi,...</p>  <p><em>Giai đoạn lưu trú:</em> đến hết 31/05/2017 (giai đoạn lễ 20/05, 27/05 có phụ thu thêm)</p>  <p>- Yêu cầu: đặt tối thiểu 2 người/phòng & đặt trước 7 ngày</p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>19</b> ngày</h3>', NULL, 1500000, NULL),
(11, 'Khách sạn A La Carte Đà Nẵng Beach', 1, '200 Võ Nguyên Giáp, Quận Sơn Trà, Tp. Đà Nẵng, Việt Nam', '1', 140, 0, '<p>Phòng Delight Plus (2-Bedroom) áp dụng cho đặt phòng tối thiểu 2 đêm.</p>  <p><strong>Giá bao gồm:</strong></p>  <p>- Ăn sáng buffet dành cho gia đình</p>  <p>- Tiễn sân bay miễn phí</p>  <p>- 1 bữa tối set menu Việt Nam tại nhà hàng Fishca</p>  <p>- 4 đơn vị nước uống tại Top Bar</p>  <p>- 1 lần sử dụng shuttle bus đi Bà Nà/Hội An</p>  <p>- Sử dụng các tiện ích: wifi, hồ bơi, phòng gym,...</p>  <p>- Sẽ có phụ thu thêm nếu có em bé.</p>  <p><strong>Điều kiện áp dụng:</strong></p>  <p>- Đến hết 30/09 (không áp dụng giai đoạn lễ 03/05 - 05/05, 01/09 - 03/09)</p>                                      <h3 class="bundle__remain_date"><i class="glyphicon glyphicon-time"></i> Promotion Còn <b>140</b> ngày</h3>', NULL, 1500000, NULL),
(12, 'Khu nghỉ dưỡng Furama Đà Nẵng', 1, '105 Đường Võ Nguyên Giáp, Phường Khuê Mỹ, Quận Ngũ Hành Sơn, Bãi biển Bắc Mỹ An, Đà Nẵng, Việt Nam', '1', 140, 0, 'Khu nghỉ dưỡng Furama Đà Nẵng là một khu nghỉ dưỡng 5 sao sang trọng, có uy tín và được xem là một trong những biểu tượng của ngành du lịch Việt Nam.\r\n\r\nKhu nghỉ dưỡng có 198 phòng được thiết kế theo phong cách truyền thống Việt Nam kết hợp với phong cách Pháp, 2 tòa nhà 4 tầng có hướng nhìn ra biển, nhìn ra hồ bơi trong xanh và những khu vườn nhiệt đới xanh tươi mát. Ngoài ra, khu nghỉ dưỡng Furama còn cung cấp các liệu pháp spa, phòng xông hơi ướt, phòng xông hơi khô, dịch vụ mát-xa cạnh hồ bơi, các dịch vụ thể thao dưới nước và các lớp tập yoga và Thái Cực Quyền trên bãi biển.\r\n\r\nNhà hàng café Signature Indochine với hàng hải sản tự chọn, nhà hàng Ý Don Cipriani, nhà hàng Terrace Dương phục vụ Đông món ăn Trung, Hải Vân Lounge với ban nhạc Philippines đêm, quán bar ngoài trời và nhà hàng bên hồ bơi làm cho một khu nghỉ mát, với không gian thật sự sang trọng ấm áp và ấm cúng.\r\n\r\nSố lượng phòng: 198', NULL, 1500000, NULL),
(13, 'Sunrise Nha Trang Beach Hotel &amp; Spa ', 1, 'Hà Nội', '2', 60, 0, '          <p style="text-align: justify;"><strong>Sunrise Nha Trang Beach Hotel &amp; Spa</strong> l&agrave; kh&aacute;ch sạn 5 sao duy nhất c&oacute; lối kiến tr&uacute;c cổ điển, sang trọng&nbsp;tại&nbsp;trung t&acirc;m th&agrave;nh phố Nha Trang.&nbsp;Tọa lạc b&ecirc;n một trong những vịnh biển đẹp nhất thế giới,&nbsp;Kh&aacute;ch sạn sở hữu 125&nbsp;ph&ograve;ng nghỉ&nbsp;hướng biển với&nbsp;ban c&ocirc;ng ri&ecirc;ng,&nbsp;hướng nh&igrave;n trực diện,&nbsp;bao qu&aacute;t to&agrave;n cảnh vịnh Nha Trang.&nbsp;</p>  <p style="text-align: justify;">Du kh&aacute;ch&nbsp;sẽ t&igrave;m được tất cả những g&igrave; m&igrave;nh cần cho một kỳ nghỉ ho&agrave;n hảo.&nbsp;Hồ bơi kiểu La M&atilde;&nbsp;tr&ecirc;n tầng 1 hay&nbsp;b&atilde;i biển ri&ecirc;ng trước kh&aacute;ch sạn l&agrave; nơi&nbsp;l&yacute; tưởng d&agrave;nh cho du kh&aacute;ch đắm m&igrave;nh trong nắng v&agrave;ng, biển xanh Nha Trang.&nbsp;Thư gi&atilde;n b&ecirc;n ly cocktail nhiệt đới,&nbsp;cảm nhận trọn vẹn n&eacute;t thơ mộng của vịnh&nbsp;Nha Trang&nbsp;khi&nbsp;đến với Sky Lounge tr&ecirc;n tầng 10. Kh&ocirc;ng gian thanh lịch&nbsp;ở&nbsp;Lobby Lounge hay trẻ trung, ấm &aacute;p&nbsp;ở&nbsp;Irish Pub th&iacute;ch hợp để gặp gỡ đối t&aacute;c, bạn b&egrave;.&nbsp;Phục hồi, t&aacute;i tạo năng lượng với những liệu ph&aacute;p độc đ&aacute;o tại&nbsp;Sunrise Spa. Ngo&agrave;i ra, <strong>Sunrise Nha Trang Beach Hotel &amp; Spa</strong> c&ograve;n giới thiệu&nbsp;thế giới ẩm thực quyến rũ, phong ph&uacute; ở nh&agrave; h&agrave;ng Hương Việt, Imperial v&agrave; Hanabusa.&nbsp;</p>  <p style="text-align: justify;">Dịch vụ chuy&ecirc;n nghiệp, th&acirc;n thiện tại Sunrise Nha Trang Beach Hotel &amp; Spa sẽ mang đến cho qu&yacute; kh&aacute;ch những trải nghiệm đ&aacute;ng nhớ nhất.&nbsp;</p>  <p style="text-align: justify;">&nbsp;</p>  <p style="text-align: justify;">Số ph&ograve;ng : 120 ph&ograve;ng</p>      ', '', 1000000, NULL);

