CREATE DEFINER=`root`@`localhost` EVENT `Decrease expire day` ON SCHEDULE EVERY 1 DAY STARTS '2018-05-01 00:00:00' ENDS '2019-04-27 00:00:00' ON COMPLETION NOT PRESERVE ENABLE COMMENT 'Giamr ngày còn hiệu lực của tour' DO UPDATE tour SET NgayHieuLuc=NgayHieuLuc-1 where 0=0$$

CREATE DEFINER=`root`@`localhost` EVENT `Disable expired tour` ON SCHEDULE EVERY 1 YEAR STARTS '2018-05-01 00:00:00' ON COMPLETION NOT PRESERVE ENABLE COMMENT 'Update lại status của tour đã hết hạn' DO UPDATE tour SET STATUS=0 where NgayHieuLuc <= 0$$

CREATE DEFINER=`root`@`localhost` EVENT `Reset tour book count` ON SCHEDULE EVERY 1 MONTH STARTS '2018-05-01 00:00:00' ON COMPLETION NOT PRESERVE ENABLE DO update tour set LuotBook=0 WHERE 0=0$$

